insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 311300, 16, 2, 3000, 0, 0, '覚醒・浮気星', 2.8, 311301, '【魔法】ぽっかり空いた心のスキマに取り憑く\\n星の精霊。過剰な自意識とピュアな下心で相手\\nを惑わせる。\\n\\n・ユニオンバーストは、敵全体に浮気星が保持し\\n\u3000ている【ふた心】の数に応じた魔法割合ダメー\\n\u3000ジを与える。\\n\u3000また、浮気星の残りＨＰが50％未満の場合、\\n\u3000さらに与えるダメージがアップする。\\n\u3000スキル発動後、浮気星が保持している\\n\u3000【ふた心】の数は０になる。\\n・一番後ろの敵を中心として、一定範囲に\\n\u3000魔法小ダメージを与え、さらに誘惑状態にして\\n\u3000物理攻撃力と魔法攻撃力を小アップさせる。\\n\u3000このスキルでダメージを与えた敵の数だけ、\\n\u3000浮気星に一定時間【ふた心】を追加する。\\n・前方範囲内の敵をノックバックさせ、\\n\u3000魔法小ダメージを与える。\\n\u3000さらに誘惑状態にして物理攻撃力と魔法攻撃力\\n\u3000を小アップさせる。\\n\u3000このスキルでダメージを与えた敵の数だけ、\\n\u3000浮気星に一定時間【ふた心】を追加する。', 0, 270);
