insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 312500, 6, 2, 1000, 0, 0, 'マダムエレクトラ', 1.6, 312500, '【魔法】塔の魔力を蓄え雷撃として放電する怪鳥。\\n雷雲と共に顕現し、天災の如き雷で敵を葬り去る。\\n\\n・敵全体に魔法大ダメージを与え、自分の魔法\\n\u3000攻撃力と、行動速度を小アップさせる。\\n\u3000物理攻撃をする敵にはさらに追加ダメージ。\\n・前方範囲内の敵すべてに魔法中ダメージを与え\\n\u3000物理防御力と魔法防御力を中ダウンさせる。\\n・物理攻撃力が最も高い敵１キャラに魔法大\\n\u3000ダメージを与え、長時間麻痺状態にする。\\n\u3000自分のＨＰが85％を切ると、魔法攻撃力が\\n\u3000最も高い敵にもこの攻撃を行う。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 312501, 6, 2, 1000, 0, 0, 'マダムエレクトラ', 1.6, 312501, '【魔法】魔力を蓄え雷撃として放電する怪鳥。\\n雷雲と共に顕現し、天災の如き雷で敵を葬り去る。\\n\\n・バトル開始時、自身の物理防御力と魔法防御力\\n\u3000を小アップさせる。この防御力アップ効果で上\\n\u3000昇した防御力は、バトル中に受ける防御力ダウ\\n\u3000ンスキルの影響を受けない。\\n・マダムエレクトラから受けるすべてのダメージ\\n\u3000で発生する、パーティキャラのＴＰ回復量を中\\n\u3000ダウンさせる。\\n・ユニオンバーストは、自身の魔法攻撃力と魔法\\n\u3000防御力をバトル終了まで小アップさせ、最も近\\n\u3000い敵１キャラに魔法特大ダメージを与える。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 312502, 6, 2, 1000, 0, 0, 'マダムエレクトラ', 1.6, 312502, '【魔法】魔力を蓄え雷撃として放電する怪鳥。\\n雷雲と共に顕現し、天災の如き雷で敵を葬り去る。\\n\\n・バトル開始時、自身の物理防御力と魔法防御力\\n\u3000を中アップさせる。この防御力アップ効果で上\\n\u3000昇した防御力は、バトル中に受ける防御力ダウ\\n\u3000ンスキルの影響を受けない。\\n・バトル開始時、自身にＴＰダウン100％カッ\\n\u3000ト効果を付与する。\\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\\n\u3000させ、ＴＰ上昇を小ダウンさせる。\\n・マダムエレクトラから受けるすべてのダメージ\\n\u3000で発生する、パーティキャラのＴＰ回復量を中\\n\u3000ダウンさせる。\\n・ユニオンバーストは、バトル終了まで自身の魔\\n\u3000法攻撃力を小アップ、魔法防御力を中アップさ\\n\u3000せる。その後、最も近い敵１キャラに魔法特大\\n\u3000ダメージを与え、麻痺状態を付与する。このス\\n\u3000キルの状態異常はダメージを無効化しても付与\\n\u3000される。\\n・最も近い敵１キャラに魔法特大ダメージを与え、\\n\u3000麻痺状態を付与する。このスキルの状態異常は\\n\u3000ダメージを無効化しても付与される。', 0, 270);
