insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 312600, 16, 1, 3000, 0, 0, 'ジェネラルホーン', 2.7, 312606, '【ＭＯＤＥ３】手負いの角獣が死力を振り絞る。\\n命を賭した突進は体躯を紫電に変え、光の如き\\n一閃は歴戦の冒険者一行を瓦解させる。\\n\\n・通常攻撃は、前方１キャラを中心とした範囲内\\n\u3000に物理小ダメージを与える。\\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\\n\u3000Ｐに応じた魔法割合ダメージを与える。\\n\u3000さらに、自身の物理・魔法防御力を中ダウンさ\\n\u3000せる。\\n・前方範囲内に物理中ダメージを与え、麻痺さ\\n\u3000せる。さらに、自身の物理・魔法防御力を大ダ\\n\u3000ウンさせる。\\n・残り時間が25秒を切ると、さらに攻撃が激し\\n\u3000く凶暴になる。', 0, 270);
