insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 312705, 16, 2, 3000, 0, 0, '黒風の精霊', 2.7, 312705, '【ＭＯＤＥ２】第二の試練。己の弱き心と対峙\\nせよ。いつも答えは己の内にある。今一度、仲間\\nの力を信じ立ち向かえ。\\n\\n・前から２キャラに物理小ダメージを与え、さら\\n\u3000にノックバックさせ、スタンを与える。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 312700, 16, 2, 3000, 0, 0, '黒風の精霊', 2.7, 312706, '【ＭＯＤＥ３】第三の試練。終の試練は、己の\\nすべてを懸け黒き風の精霊に本当の想いと絆を\\n顕現し、己の想いを確かめよ。\\n\\n・ユニオンバーストは、敵全体に黒風の精霊\\n\u3000の物理攻撃力とキャラの最大ＨＰに応じた\\n\u3000魔法割合ダメージを与える。さらに、\\n\u3000残りＨＰが最も低い１キャラにカウントダウン\\n\u3000を付与し、５秒後に最大ＨＰの80%の魔法\\n\u3000割合ダメージを与える。\\n・敵全体を猛毒状態にし、さらに物理・魔法攻撃\\n\u3000力を大ダウンさせる。\\n・残り時間が25秒を切ると、さらに攻撃が激し\\n\u3000く凶暴になる。', 0, 270);
