insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 312800, 16, 1, 3000, 0, 0, '魔物の姫', 1.8, 312801, '【物理】辺境の城に座する、孤高な魔物の姫君。\\n悲哀なる恋慕を胸に、自らに挑む者を打ち砕く。\\n\\n・ユニオンバーストは、前から２キャラに\\n\u3000最大ＨＰに応じた物理割合ダメージを与える。\\n\u3000力を溜めることに成功した場合、ダメージが\\n\u3000アップし、敵全体を畏怖状態にする。\\n\u3000畏怖状態のキャラは、一定時間攻撃およびＨＰ\\n\u3000回復のユニオンバーストとスキルの効果が\\n\u3000ダウン。\\n\u3000このスキルの状態異常はダメージを無効化して\\n\u3000も付与される。\\n・物理防御力と魔法防御力が大幅にダウンした後、\\n\u3000一定時間、力を溜める。\\n\u3000力が溜まると、ＴＰが特大回復する。\\n\u3000力を溜めている間に魔物の姫が7000以上の\\n\u3000ダメージを受けると、力を溜めることに失敗し、\\n\u3000ＴＰが大ダウン。 \\n・目の前の敵１キャラに物理ダメージを与える。\\n\u3000この攻撃は物理攻撃力アップ・ダウン効果の\\n\u3000影響を受けやすい。\\n・前から３キャラに最大ＨＰに応じた物理割合\\n\u3000ダメージを与える。\\n\u3000この攻撃は物理攻撃力アップ・ダウン効果の\\n\u3000影響を受けやすい。', 0, 270);
