insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 312800, 16, 1, 3000, 0, 0, '魔物の姫', 1.8, 312804, '【ＭＯＤＥ１】高潔なる姫君は従兵を以て対敵を\\n拒絶する。さながら内なる嘆きを虚飾するが如く。\\n\\n・ユニオンバーストは、前方２キャラに最大ＨＰ\\n\u3000に応じた物理割合ダメージを与える。\\n・目の前の敵１キャラに物理ダメージを与える。\\n\u3000この攻撃は物理攻撃力アップ・ダウン効果の\\n\u3000影響を受けやすい。\\n・前方３キャラに最大ＨＰに応じた物理割合\\n\u3000ダメージを与える。\\n\u3000この攻撃は物理攻撃力アップ・ダウン効果の\\n\u3000影響を受けやすい。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 312800, 16, 1, 3000, 0, 0, '魔物の姫', 1.8, 312805, '【ＭＯＤＥ２】姫君は更なる力を解き放つ。\\n歪曲した強き恋慕で、全てを拒み屠らんとする。\\n\\n・敵からの攻撃による麻痺、スタン、束縛、凍結、\\n\u3000時間停止、行動速度低下効果への耐性を持たな\\n\u3000い。\\n・ユニオンバーストは、前方２キャラに最大ＨＰ\\n\u3000に応じた物理割合ダメージを与える。\\n・目の前の敵１キャラに物理ダメージを与える。\\n\u3000この攻撃は物理攻撃力アップ・ダウン効果の\\n\u3000影響を受けやすい。\\n・前方３キャラに最大ＨＰに応じた物理割合\\n\u3000ダメージを与える。\\n\u3000この攻撃は物理攻撃力アップ・ダウン効果の\\n\u3000影響を受けやすい。\\n・短時間無敵状態になり、敵全体のステータス\\n\u3000アップ効果を解除した後、一定時間バトルフ\\n\u3000ィールドを展開する。\\n\\n【バトルフィールド効果】\\n・敵全体の物理攻撃力と魔法攻撃力を100％\\n\u3000ダウンした後、物理攻撃力と魔法攻撃力を\\n\u3000特大アップ。\\n・魔物の姫の物理攻撃力を特大アップ。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 312800, 16, 1, 3000, 0, 0, '魔物の姫', 1.8, 312806, '【ＭＯＤＥ３】大いなる悲哀に呼応して、姫君の\\n進攻は苛烈を極める。乙女の熱情を以て、その\\n悲しみに救いをもたらさん。\\n\\n・ユニオンバーストは、前方２キャラに最大ＨＰ\\n\u3000に応じた物理割合ダメージを与える。\\n\u3000力を溜めることに成功した場合、ダメージが\\n\u3000アップし、敵全体を畏怖状態にする。\\n\u3000畏怖状態のキャラは、一定時間攻撃およびＨＰ\\n\u3000回復のユニオンバーストとスキルの効果が\\n\u3000ダウン。\\n・物理防御力と魔法防御力が大幅にダウンした後、\\n\u3000一定時間、力を溜める。\\n\u3000力が溜まると、ＴＰが特大回復する。\\n\u3000力を溜めている間に魔物の姫が600000\\n\u3000以上のダメージを受けると、力を溜めることに\\n\u3000失敗し、ＴＰが大ダウン。 \\n・目の前の敵１キャラに物理ダメージを与える。\\n\u3000この攻撃は物理攻撃力アップ・ダウン効果の\\n\u3000影響を受けやすい。\\n・前方３キャラに最大ＨＰに応じた物理割合\\n\u3000ダメージを与える。\\n\u3000この攻撃は物理攻撃力アップ・ダウン効果の\\n\u3000影響を受けやすい。\\n・魔物の姫自身のＨＰが25％を切ると、さらに\\n\u3000攻撃が激しく凶暴になる。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 312900, 16, 1, 3000, 0, 0, '黒月の守護像', 2.5, 312900, '【物理】天上の聖域を護る、黒月に祝福されし像。\\n聖域に踏み入る、冒険者を常世の闇に葬り去る。\\n\\n・ユニオンバーストは、敵全体にキャラの最大\\n\u3000ＨＰに応じた物理割合ダメージを与える。\\n・敵全体に物理大ダメージを与え、ステータス\\n\u3000アップ効果を解除する。このスキルの効果は\\n\u3000ダメージを無効化しても付与される。\\n・自身の物理攻撃力と魔法攻撃力を大アップさ\\n\u3000せる。白陽の守護像が討伐されている場合、\\n\u3000このスキルを使用する。\\n・黒月の守護像のＨＰが50％を切ると、物理\\n\u3000攻撃力を大アップさせ、物理防御力を中アップ\\n\u3000する。\\n・黒月の守護像のターゲットがブレイクしてい\\n\u3000る場合、物理防御力と魔法防御力が小ダウンす\\n\u3000る。\\n・白陽の守護像が討伐されていた場合、バトルス\\n\u3000タートの際、もしくは黒月の守護像が通常攻撃\\n\u3000を行った際に、黒月の守護像の攻撃行動を変更\\n\u3000する。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 313000, 16, 2, 3000, 0, 0, '白陽の守護像', 2.5, 313000, '【魔法】天上の聖域を護る、白陽に祝福されし像。\\n全てを浄化する白雷で、冒険者に審判を下す。\\n\\n・ユニオンバーストは、敵全体にキャラの最大\\n\u3000ＨＰに応じた魔法割合ダメージを与える。\\n・敵全体に魔法大ダメージを与え、ステータス\\n\u3000アップ効果を解除する。さらに白陽の守護像の\\n\u3000魔法攻撃力を大アップする。このスキルの効果\\n\u3000はダメージを無効化しても付与される。黒月の\\n\u3000守護像が討伐されている場合、このスキルを使\\n\u3000用する。\\n・味方全体の物理防御力と魔法防御力を中アップ\\n\u3000させる。\\n・白陽の守護像のＨＰが50％を切ると、魔法攻\\n\u3000撃力を特大アップさせ、魔法防御力を小アップ\\n\u3000する。\\n・白陽の守護像のターゲットがブレイクしている\\n\u3000場合、物理防御力と魔法防御力が小ダウンする。\\n・黒月の守護像が討伐されていた場合、バトルス\\n\u3000タートの際、もしくは白陽の守護像が通常攻撃\\n\u3000を行った際に、白陽の守護像の攻撃行動を変更\\n\u3000する。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 313100, 16, 2, 3000, 0, 0, 'バースト・タイムラーベ', 1.4, 313100, '', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 313100, 16, 2, 3000, 0, 0, 'バースト・タイムラーベ', 1.4, 313101, '【魔法】ホマレ所蔵の魔道具が、臨界を超え暴走\\nした姿。本来の役目も忘れ、冒険者を混沌へと\\n導く水先案内人と化す。\\n\\n・ユニオンバーストは、敵全体に魔法大ダメージ\\n\u3000を与え、さらに行動速度を中ダウン、魔法防御\\n\u3000力を中ダウンさせる。このスキルの状態異常は\\n\u3000ダメージを無効化しても付与される。\\n・自分の魔法攻撃力を中アップ、行動速度を大\\n\u3000アップさせる。\\n・向かって左側のターゲットのブレイク時、\\n\u3000物理/魔法防御力が中ダウン。\\n・中央下部のターゲットのブレイク時、\\n\u3000物理/魔法防御力が大ダウン。\\n・中央上部のターゲットのブレイク時、\\n\u3000物理/魔法防御力が大ダウン。\\n・向かって右側のターゲットのブレイク時、\\n\u3000物理/魔法防御力が特大ダウン。', 0, 270);
