insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 314001, 16, 1, 3000, 0, 0, '得意な宿題', 1.0, 314001, 'まずは手始めに得意な宿題を終わらせよう。\\nここでつまづいてはいられない。夏休みの宿題は\\nほかにもまだまだあるぞ！！\\n\\n・バトル開始後、大量の宿題が降り注ぎ、\\n\u3000敵全体に物理中ダメージ。このスキル\\n\u3000によるダメージでは、キャラのＴＰが\\n\u3000回復しない。\\n・バトル中、一定時間が経過するごとに\\n\u3000モンスターが押し寄せてくる。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 314002, 16, 1, 3000, 0, 0, 'ちょっと苦手な宿題', 1.0, 314002, '誰にでもある苦手科目の宿題。ついつい後回しに\\nしがちで、溜まってしまうとなかなか大変。\\nここで立ち止まるわけにはいかないぞ！\\n\\n・バトル開始後、大量の宿題が降り注ぎ、\\n\u3000敵全体に物理中ダメージ。このスキル\\n\u3000によるダメージでは、キャラのＴＰが\\n\u3000回復しない。\\n・バトル中、一定時間が経過するごとに\\n\u3000モンスターが押し寄せてくる。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 314003, 16, 1, 3000, 0, 0, 'すごく苦手な宿題', 1.0, 314003, '眺めているだけで眩暈のするいや～な宿題。\\nペンを置いて外に飛び出したくなる衝動を耐え、\\nコツコツ進めていくことが攻略のカギ！\\n\\n・バトル開始後、大量の宿題が降り注ぎ、\\n\u3000敵全体に物理中ダメージ。このスキル\\n\u3000によるダメージでは、キャラのＴＰが\\n\u3000回復しない。\\n・バトル中、一定時間が経過するごとに\\n\u3000モンスターが押し寄せてくる。', 0, 270);
