insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 314006, 16, 1, 3000, 0, 0, '夏休み最後まで残りし宿題', 1.0, 314006, '夏休みの間ずっと、目を背けてきた最後の宿題。\\nここまで放置してきた後悔が一気に襲い掛かる。\\nすべてやり遂げて笑顔で登校できるか！？\\n\\n・バトル開始後、大量の宿題が降り注ぎ、\\n\u3000敵全体に物理中ダメージ。\\n・バトル中、一定時間が経過するごとに\\n\u3000モンスターが押し寄せてくる。', 0, 270);
