insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 314700, 16, 1, 3000, 0, 0, 'グレーターゴーレム', 1.6, 314701, '【物理】同族のゴーレムを次々に呼び寄せる\\n岩石の魔物。大地を揺さぶる巨腕が冒険者たちに\\n襲い掛かる。\\n\\n・主人公が目覚めていないため、バトル開始時\\n\u3000に全キャラクターのユニオンバーストが発動\\n\u3000不能状態になる。\\n・ぺコリーヌが目覚めていないため、本バトルで\\n\u3000はペコリーヌ（同名キャラ含む）が編成不可。\\n・ユニオンバーストは、前から4キャラにグレー\\n\u3000ターゴーレムの物理攻撃力とキャラの最大ＨＰ\\n\u3000に応じた物理割合ダメージを与える。\\n・敵全体をノックバックさせ、ゴーレムを召喚す\\n\u3000る。さらに自分の物理攻撃力を中アップさせ\\n\u3000る。\\n・前から１番目のキャラに物理大ダメージ。', 0, 270);
