insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 320504, 16, 1, 3000, 0, 0, '顕れる苦衷 アイラ＆シナツ', 1.6, 320504, '【ＭＯＤＥ２】青き瘴雷の陰より、藍の瞬剣が迸\\nる。震える大気、舞い荒ぶ嵐刃。無我に振り続け\\nる刀には、行き場のない憤りと鬱積が滲む――\\n\\n・アイラは赤い霧の精霊による支配に抗っており、\\n\u3000その場から動くことができない。\\n・バトル開始時、自身の物理防御力と魔法防御力\\n\u3000を小アップさせる。この防御力アップ効果で上\\n\u3000昇した防御力は、バトル中に受ける防御力ダウ\\n\u3000ンスキルの影響を受けない。\\n・バトル開始時、自身にＴＰダウン100％カッ\\n\u3000ト効果を付与する。\\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\\n\u3000させる。\\n・顕れる苦衷 アイラ＆シナツから受けるすべて\\n\u3000のダメージで発生する、パーティキャラのＴＰ\\n\u3000回復量を中ダウンさせる。\\n・最も遠い３キャラに物理中ダメージを与え、物\\n\u3000理防御力を中ダウンさせる。さらにスタン状態\\n\u3000を付与する。このスキルの状態異常はダメージ\\n\u3000を無効化しても付与される。\\n・最も遠い２キャラに物理大ダメージを与える。\\n・向かって左側のターゲットのブレイク時、\\n\u3000物理/魔法防御力が特大ダウン。\\n・向かって右側のターゲットのブレイク時、\\n\u3000物理/魔法防御力が特大ダウン。', 0, 270);
