insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 320600, 16, 1, 3000, 0, 0, 'エリマキレックス', 1.6, 320600, '【物理】ジオ・ゲヘナに棲む恐竜魔\\n物。頭のエリマキは威嚇だけでなく\\n刃のように変形し獲物を両断する。\\n\\n・バトル開始時、自身にＴＰダウン\\n\u300050％カット効果を付与する。\\n・エリマキレックスから受けるすべ\\n\u3000てのダメージで発生する、パー\\n\u3000ティキャラのＴＰ回復量を中ダ\\n\u3000ウンさせる。\\n・ユニオンバーストは、敵全体に\\n\u3000エリマキレックスの物理攻撃力\\n\u3000に応じた物理割合ダメージを与\\n\u3000える。\\n・前から２キャラに物理中ダメージ\\n\u3000を与え、ノックバックさせると同\\n\u3000時にスタンさせる。このスキルの\\n\u3000状態異常はダメージを無効化して\\n\u3000も付与される。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 320700, 16, 1, 3000, 0, 0, '奈落の聖女トミヱ', 1.6, 320700, '【物理】聖なる学舎の秘されし暗部を預かる獄卒。\\n教義絶対を掲げる『鋼の聖女』の正義を守るべく\\nまつろわぬ咎人に容赦なき服従と更生を強いる。\\n\\n・ユニオンバーストは、敵全体にキャラの最大\\n\u3000ＨＰに応じた物理割合ダメージを与える。自身\\n\u3000の物理攻撃力に応じて与えるダメージの割合が\\n\u3000増加する。\\n・最も近い敵３キャラにキャラの残りＨＰに応じ\\n\u3000た物理割合ダメージを与える。自身の物理攻撃\\n\u3000力に応じて与えるダメージの割合が増加する。\\n・自身の物理攻撃力を特大アップし、ＴＰを特大\\n\u3000回復する。自身のＨＰが低いほど、物理攻撃力\\n\u3000の上昇量がアップする。\\n・最も近い敵１キャラに物理特大ダメージを与え\\n\u3000る。', 0, 270);
