CREATE TABLE 'dsb_story_data' ('sub_story_id' INTEGER NOT NULL, 'original_event_id' INTEGER NOT NULL, 'title' TEXT NOT NULL, 'condition_time' TEXT NOT NULL, 'condition_quest_id' INTEGER NOT NULL, 'reward_type' INTEGER NOT NULL, 'reward_id' INTEGER NOT NULL, 'reward_count' INTEGER NOT NULL, 'owner_unit_id' INTEGER NOT NULL, 'guest_unit_id' INTEGER NOT NULL, 'day_num' INTEGER NOT NULL, 'material_text' TEXT NOT NULL, 'effect_text' TEXT NOT NULL, 'article_text' TEXT NOT NULL, PRIMARY KEY('sub_story_id'));
