insert into dsb_story_data values (1010801, 10108, '忍耐比賽要適可而止\u3000火焰果實溫泉', '2024/05/17 16:00:00', 10135107, 8, 91002, 40, 106701, 106601, 1, '艾爾皮斯島原產\u3000火焰果實、大辣椒', '改善怕冷體質，促進代謝，保溫效果\\n※請避免長時間入浴', '熱情的溫泉，主要材料是由艾爾皮斯島的\\n似龍生物所提供的大量火焰果實。\\n一旦浸泡其中，汗水便會如瀑布般湧出。\\n那份熱度會緊緊抓住想不斷挑戰\\n自己極限的猛男猛女們的好勝心。');
insert into dsb_story_data values (1010802, 10108, '心花朵朵開紓壓放鬆\u3000香芬溫泉', '2024/05/18', 0, 8, 91002, 40, 101501, 101601, 2, '精靈之森產\u3000芬芳花朵的精油', '美肌效果，放鬆效果，保香效果\\n※可能使心情變得酸酸甜甜的', '使用了香氣濃郁的花朵與該花朵的精油，\\n使此溫泉散發出鮮豔的色彩。\\n擁有使人忘卻時間的放鬆效果，\\n非常符合繁忙都市人的需求。\\n亦有高度的美容效果，因此逐漸成為\\n年輕女性們注目的熱門場所。');
insert into dsb_story_data values (1010803, 10108, '喚醒童心的微碳酸\u3000哈蜜瓜汽水溫泉', '2024/05/18', 0, 8, 91002, 40, 105101, 103601, 3, '女接待員特製的碳酸泉、哈密瓜精油', '促進血液循環，去角質效果\\n回歸童心效果', '此溫泉充滿了溫和微碳酸與甜美哈密瓜香氣，\\n能夠挑起玩心。是深月希望孩子們開心，\\n並依此想法創造出來的產物，即使是平時\\n看似成熟的優等生，泡進此浴池，\\n也會顯露出與年齡相符的模樣開心玩耍。');
insert into dsb_story_data values (1010804, 10108, '心跳電擊源泉掛流☆\u3000酥麻溫泉', '2024/05/19', 0, 8, 91002, 40, 106701, 104501, 4, '小老闆娘的魔法（電擊）', '消除腰痛與肩頸痠痛，促進血液循環\\n麻酥酥絕頂體驗（？）', '本旅館引以為豪的溫泉，添加了小老闆娘的\\n電擊而變得更加刺激。對緩解全身僵硬\\n相當有效而大受好評。今天也有許多客人\\n為了舒緩身體僵硬而前來，從文書工作者\\n到老人都有。些微痛感也是使人上癮的\\n原因之一。');
insert into dsb_story_data values (1010805, 10108, '給你迎接明天的活力\u3000消除疲勞溫泉', '2024/05/19', 0, 8, 91002, 40, 101501, 104801, 5, '精靈之森產\u3000治癒之葉的根部', '超‧消除肉體疲勞，超‧消除神經疲勞\\n超‧消除精神疲勞', '因可消除各種疲勞而聞名的溫泉。\\n藥湯是以精靈之森自豪的藥草類調合而成，\\n因而使其效果倍增，是人們精疲力盡時的\\n休憩場所。世上的能幹者們，會在浸泡於\\n此湯後工作，然後泡湯後再繼續工作，\\n如此重複全力以赴。');
insert into dsb_story_data values (1010806, 10108, '粗暴排毒！\u3000火辣辣溫泉', '2024/05/20', 0, 8, 91002, 40, 105101, 118001, 6, '女接待員特製的中藥、罕見的辣椒', '脂肪燃燒效果，高排毒效果\\n※請務必避免長時間入浴', '奢侈地使用了據說因為太過辛辣而無法於\\n一般市場流通的辣椒，並具有高排毒效果的\\n溫泉。由於產生的蒸氣也很刺激，有提供\\n護目鏡租借服務。（如有需要歡迎隨時向\\n櫃檯詢問。）');
insert into dsb_story_data values (1010807, 10108, '高黏度的嶄新溫泉\u3000軟泥怪溫泉', '2024/05/20', 0, 8, 91002, 40, 106701, 106001, 7, '小老闆娘特製的藥水、精力充沛的軟泥怪', '恢復魔力，美肌效果，保濕效果\\n※有可能會被溫泉輕咬', '將恢復魔力的藥水與軟泥怪混合製成的\\n稀奇溫泉。據說軟泥怪們是由小老闆娘招募\\n來打工的。該溫泉水不可思議的觸感與確實\\n的魔力恢復效果，在喜歡新事物的魔法師與\\n魔族們之間蔚為話題。');
insert into dsb_story_data values (1010808, 10108, '清爽地撫慰身心♪\u3000涼爽溫泉', '2024/05/21', 0, 8, 91002, 40, 101501, 102101, 8, '精靈之森產\u3000涼爽草的種子油', '清涼效果，喉嚨與鼻腔的保濕效果\\n※哼唱聲可能會傳到旅館裡', '以具清涼效果的藥草類調合而成的藥湯。\\n吸入其蒸氣有助於喉嚨與鼻腔保濕，\\n使聲音變得更加清晰，因而備受好評，\\n再加上泡完澡後的清爽感，因此有許多\\n回頭客。據說歌手與偶像們也會偷偷\\n前來造訪。');
insert into dsb_story_data values (1010809, 10108, '啊，玫瑰色的人生\u3000美肌溫泉', '2024/05/21', 0, 8, 91002, 40, 105101, 100801, 9, '女接待員特製的超美肌藥、大朵玫瑰', '美肌效果，保濕效果，保溫效果\\n玫瑰花香帶來的放鬆效果', '總而言之就是追求美肌效果的珍貴溫泉。\\n據說只是稍微泡一下就能感受到肌膚變化，\\n也有試圖開發以此溫泉水製成的化妝水。\\n順帶一提，其中的玫瑰僅是用來增添香氣，\\n並不具有美肌效果。');
