insert into login_bonus_message_data values (149, 80003, 1, 3, 0, 0, 105801, '貪吃佩可', '那個香腸\\n真的很好吃耶～♪\\n我也吃了很多呢！', 7, 0, '');
insert into login_bonus_message_data values (150, 80003, 0, 3, 0, 0, 105901, '可可蘿', '還有就是，\\n牧場蓋了一座很棒的溫泉……', 9, 0, '');
insert into login_bonus_message_data values (151, 80003, 1, 3, 0, 0, 105801, '貪吃佩可', '在牧場嗎？\u3000泡著溫泉\\n一邊吃著起司也很棒呢～♪\\n光想口水就都快流出來了！\\n太讚了吧☆', 8, 0, '');
insert into login_bonus_message_data values (152, 80003, 0, 3, 0, 0, 105901, '可可蘿', '貪吃佩可小姐\\n您話才剛說完口水就……\\n在下馬上幫您擦乾淨。擦擦。', 10, 0, '');
insert into login_bonus_message_data values (153, 80003, 1, 3, 0, 0, 105801, '貪吃佩可', '欸嘿嘿，謝謝妳！\\n小可蘿！', 9, 0, '');
insert into login_bonus_message_data values (154, 80003, 0, 3, 0, 0, 105901, '可可蘿', '話說回來，主人您\\n身為【美食殿堂】的一員\\n也邂逅了許許多多的美食呢。\\n在下實在是深感佩服。', 11, 0, '');
insert into login_bonus_message_data values (155, 80003, 1, 3, 0, 0, 105801, '貪吃佩可', '今後也要繼續不斷冒險，\\n盡情地嚐遍\\n所有的美食！', 10, 0, '');
insert into login_bonus_message_data values (156, 80003, 0, 4, 0, 0, 105801, '貪吃佩可', '2.5周年倒數♪\\n小凱留小凱留！\\n好期待呢☆', 11, 0, '');
insert into login_bonus_message_data values (157, 80003, 1, 4, 0, 0, 106001, '凱留', '啊真是的，妳很吵耶～\\n還早得很吧？\\n妳也稍微冷靜一點吧。', 12, 0, '');
insert into login_bonus_message_data values (158, 80003, 0, 4, 0, 0, 105801, '貪吃佩可', '小凱留妳自己最近還不是\\n有點靜不下心～\\n剛剛好像還心情很好地\\n哼著歌不是嗎？', 12, 0, '');
insert into login_bonus_message_data values (159, 80003, 1, 4, 0, 0, 106001, '凱留', '什麼……！\u3000妳、妳都聽到了嗎！？\\n不，我才沒有哼歌呢！？\\n妳再隨便亂講的話\\n看我宰了妳喔！？', 13, 0, '');
insert into login_bonus_message_data values (160, 80003, 0, 4, 0, 0, 105801, '貪吃佩可', '欸嘿嘿～♪\\n我越來越喜歡\\n最近的小凱留了♪\\n妳變得很積極唷！', 13, 0, '');
insert into login_bonus_message_data values (161, 80003, 1, 4, 0, 0, 106001, '凱留', '……積極？\\n什麼東西很積極？', 14, 0, '');
insert into login_bonus_message_data values (162, 80003, 0, 4, 0, 0, 105801, '貪吃佩可', '又在裝傻呢～♪\\n難道妳忘記妳之前\\n想要成為偶像的事情了？', 14, 0, '');
insert into login_bonus_message_data values (163, 80003, 0, 4, 0, 0, 105801, '貪吃佩可', '在繆思小鎮的時候\\n妳還說過凱留凱留～☆呢！', 15, 0, '');
insert into login_bonus_message_data values (164, 80003, 1, 4, 0, 0, 106001, '凱留', '啊……！？\u3000才才才沒有！\\n那是因為我當時被操縱了，\\n我才沒有\\n想要當什麼偶像好嗎！！', 15, 0, '');
insert into login_bonus_message_data values (165, 80003, 0, 4, 0, 0, 105801, '貪吃佩可', '妳說的是真的嗎？\\n我總覺得\\n小凱留妳非常\\n有興趣耶～', 16, 0, '');
insert into login_bonus_message_data values (166, 80003, 1, 4, 0, 0, 106001, '凱留', '嘛，是也不錯啦……咦、\\n就﹑就跟妳說了\\n我沒有要當偶像啦！！\\n不要害我又想起那件事情啦！？', 16, 0, '');
insert into login_bonus_message_data values (167, 80003, 1, 4, 0, 0, 106001, '凱留', '啊啊……不行了……\\n我只能把妳抹殺掉了……\\n我要在2.5周年來臨之前\\n連同記憶一起全部消除——！', 17, 0, '');
insert into login_bonus_message_data values (168, 80003, 0, 5, 0, 0, 106001, '凱留', '距2.5周年還剩10天……\\n嗯～？\\n比想像中還快呢？', 18, 0, '');
insert into login_bonus_message_data values (169, 80003, 1, 5, 0, 0, 105901, '可可蘿', '是啊……\\n為了迎接那一天的到來，\\n還有很多事還沒完成。\\n必須繃緊神經才行了……！', 12, 0, '');
insert into login_bonus_message_data values (170, 80003, 0, 5, 0, 0, 106001, '凱留', '幹勁十足呢可蘿仔……\\n太逞強的話可是會喘不過氣喔？\\n放輕鬆去做吧？', 19, 0, '');
insert into login_bonus_message_data values (171, 80003, 1, 5, 0, 0, 105901, '可可蘿', '凱留小姐……\\n謝謝妳，\\n凱留小姐您真的是一位\\n很溫柔的人呢。', 13, 0, '');
insert into login_bonus_message_data values (172, 80003, 0, 5, 0, 0, 106001, '凱留', '妳、妳在說什麼\\n莫名其妙的話啊……\\n我才不溫柔呢。', 20, 0, '');
insert into login_bonus_message_data values (173, 80003, 1, 5, 0, 0, 105901, '可可蘿', '才沒有這回事。\\n……那個，其實在下\\n有件事情\\n想要拜託凱留小姐。', 14, 0, '');
insert into login_bonus_message_data values (174, 80003, 0, 5, 0, 0, 106001, '凱留', '拜託我？\u3000真稀奇呢，\\n可蘿仔竟然會講出這種話。\\n……嘛，如果是我\\n能做得到的事的話倒是可以。', 21, 0, '');
insert into login_bonus_message_data values (175, 80003, 1, 5, 0, 0, 105901, '可可蘿', '非常謝謝您。\\n其實在下想為紀念日這一天\\n準備一個叫做\\n「才藝表演」的節目。', 15, 0, '');
insert into login_bonus_message_data values (176, 80003, 0, 5, 0, 0, 106001, '凱留', '才藝表演？', 22, 0, '');
insert into login_bonus_message_data values (177, 80003, 1, 5, 0, 0, 105901, '可可蘿', '是的。在下參考了您的凱留凱留～☆\\n想出了可蘿可蘿～☆\\n這樣的開場白，但是卻\\n一直無法擺出完美的姿勢……', 16, 0, '');
insert into login_bonus_message_data values (178, 80003, 0, 5, 0, 0, 106001, '凱留', '～～！！\u3000虧我感覺被稱讚了還有點\\n開心，根本空歡喜一場了嘛！！\\n少開玩笑了少開玩笑了！\\n少～開～玩～笑～了～！！', 23, 0, '');
insert into login_bonus_message_data values (179, 80003, 0, 6, 0, 0, 105801, '貪吃佩可', '還有9天！\u3000就快到了呢♪\\n欸嘿嘿，好期待好期待\\n都能配好幾碗飯了呢☆', 17, 0, '');
insert into login_bonus_message_data values (180, 80003, 2, 6, 0, 0, 106001, '凱留', '都能配好幾碗飯了呢☆\\n才不是這樣吧！\\n再怎麼說也吃太多了！\\n到時候可會無法迎接紀念日唷！？', 24, 0, '');
insert into login_bonus_message_data values (181, 80003, 1, 6, 0, 0, 105901, '可可蘿', '沒問題的凱留小姐，\\n畢竟我們一路走來\\n就是一直在\\n享用美食啊。', 17, 0, '');
insert into login_bonus_message_data values (182, 80003, 0, 6, 0, 0, 105801, '貪吃佩可', '真不愧是小可蘿，\\n說得真好！\\n沒錯，我們【美食殿堂】的\\n活動目的就是——', 18, 0, '');
insert into login_bonus_message_data values (183, 80003, 2, 6, 0, 0, 106001, '凱留', '——大家一起開心地吃飯，\\n對吧？\\n一路到現在都吃了那麼多了\\n我很清楚啦。', 25, 0, '');
insert into login_bonus_message_data values (184, 80003, 1, 6, 0, 0, 105901, '可可蘿', '是的……確實是如此♪\\n話說回來在下等人\\n真的是一路吃了很多東西\\n然後來到了這裡呢……', 18, 0, '');
insert into login_bonus_message_data values (185, 80003, 2, 6, 0, 0, 106001, '凱留', '是啊……的確如此呢……\\n像是渾身是毒的假龍、\\n恐龍……好像被牠逃了，還有\\n鱷魚龍……鱷魚的魔物之類的。', 26, 0, '');
insert into login_bonus_message_data values (186, 80003, 2, 6, 0, 0, 106001, '凱留', '還有，我們幾個\\n最後還對上了\\n真正的龍呢。', 27, 0, '');
insert into login_bonus_message_data values (187, 80003, 2, 6, 0, 0, 106001, '凱留', '還真是強到令人不敢相信呢。\\n那隻龍……果然還是要\\n好好擬定作戰計畫\\n才能夠對付呢。', 28, 0, '');
insert into login_bonus_message_data values (188, 80003, 0, 6, 0, 0, 105801, '貪吃佩可', '欸嘿嘿……☆\\n小凱留妳還記得\\n這麼多事情\\n讓我好高興喔♪', 19, 0, '');
insert into login_bonus_message_data values (189, 80003, 1, 6, 0, 0, 105901, '可可蘿', '在下也記憶鮮明喔。\\n主人一定也……♪', 19, 0, '');
insert into login_bonus_message_data values (190, 80003, 2, 6, 0, 0, 106001, '凱留', '那是當然的啊！\\n我怎麼可能忘得了！\\n被逼著吃下那些亂七八糟的東西！\\n……我怎麼可能、忘得了！', 29, 0, '');
insert into login_bonus_message_data values (191, 80003, 0, 7, 0, 0, 106001, '凱留', '喂、等、等一下！？\\n花凜說，希望我們能夠\\n參加2.5周年的\\n現場直播節目！', 30, 0, '');
insert into login_bonus_message_data values (192, 80003, 0, 7, 0, 0, 106001, '凱留', '這麼重要的事情為什麼\\n現在才說！？\\n我根本沒聽說有這種事啊～！？', 31, 0, '');
insert into login_bonus_message_data values (193, 80003, 0, 7, 0, 0, 106001, '凱留', '到時候會被很多人看到吧？\\n為什麼我得做這種事……', 32, 0, '');
insert into login_bonus_message_data values (194, 80003, 0, 7, 0, 0, 106001, '凱留', '啊——\\n啊——\\n……我的聲音這樣沒問題吧？', 33, 0, '');
insert into login_bonus_message_data values (195, 80003, 0, 7, 0, 0, 106001, '凱留', '這樣可能會聽不太清楚……\\n還是要再開朗一點比較好呢，\\n嗯……像這種感覺？', 34, 0, '');
insert into login_bonus_message_data values (196, 80003, 0, 7, 0, 0, 106001, '凱留', '阿囉哈～☆', 35, 0, '');
insert into login_bonus_message_data values (197, 80003, 0, 7, 0, 0, 106001, '凱留', '……\\n不、這個我不行。\\n還是跟平常一樣最好，嗯。', 36, 0, '');
insert into login_bonus_message_data values (198, 80003, 0, 7, 0, 0, 106001, '凱留', '真是的，周年紀念的現場直播\\n責任超級重大耶……\\n萬一有個什麼閃失要怎麼辦……', 37, 0, '');
insert into login_bonus_message_data values (199, 80003, 0, 7, 0, 0, 106001, '凱留', '怎、怎怎、怎麼辦！？\\n開始緊張起來了……！！', 38, 0, '');
insert into login_bonus_message_data values (200, 80003, 0, 8, 0, 0, 105901, '可可蘿', '主人，在下要和\\n貪吃佩可小姐和凱留小姐一同\\n擔任演出2.5周年慶祝現場直播\\n的這個重責大任。', 20, 0, '');
