insert into login_bonus_message_data values (202, 80003, 0, 8, 0, 0, 105901, '可可蘿', '在下會\\n全心全意盡力\\n進行現場直播的。', 22, 0, '');
insert into login_bonus_message_data values (203, 80003, 0, 8, 0, 0, 105901, '可可蘿', '但是，在下說不定\\n到時候會犯下吃螺絲或是\\n搞錯節目的順序\\n之類的錯誤……', 23, 0, '');
insert into login_bonus_message_data values (204, 80003, 0, 8, 0, 0, 105901, '可可蘿', '在下會好好練習\\n不讓這樣的事情發生，\\n首先要先從這個開始……', 24, 0, '');
insert into login_bonus_message_data values (205, 80003, 0, 8, 0, 0, 105901, '可可蘿', '阿囉哈～☆', 25, 0, '');
insert into login_bonus_message_data values (206, 80003, 0, 8, 0, 0, 105901, '可可蘿', '在下方才看到\\n凱留小姐正在練習，\\n在下也試著練習看看……\\n您覺得如何呢？', 26, 0, '');
insert into login_bonus_message_data values (207, 80003, 0, 8, 0, 0, 105901, '可可蘿', '感覺還可以嗎？\\n……呵呵，謝謝您。\\n有了主人您的暖心話語\\n讓在下如獲百人之力。', 27, 0, '');
insert into login_bonus_message_data values (208, 80003, 0, 8, 0, 0, 105901, '可可蘿', '為了能順利完成這個重責大任\\n讓主人能夠迎接一個很棒的\\n2.5周年紀念……\\n在下會竭盡所能的。', 28, 0, '');
