insert into login_bonus_message_data values (213, 80003, 0, 9, 0, 0, 105801, '貪吃佩可', '為了要在現場直播時\\n為大家送上最棒的阿囉哈～☆\\n我要加油才行！\\n那麼，現在就再來一次……', 24, 0, '');
