insert into login_bonus_message_data values (216, 80003, 0, 9, 0, 0, 105801, '貪吃佩可', '雖然剛剛才剛吃過飯，\\n不過餓著肚子\\n可是沒辦法好好地說出\\n阿囉哈～☆\u3000呢！', 27, 0, '');
insert into login_bonus_message_data values (217, 80003, 0, 9, 0, 0, 105801, '貪吃佩可', '既然決定了，那就馬上來邀\\n小可蘿和小凱留\\n一起去吃飯吧！\\n打鐵要趁熱！', 28, 0, '');
