insert into login_bonus_message_data values (220, 80003, 0, 10, 0, 0, 105801, '貪吃佩可', '雖然只剩5天就要2.5周年了\\n不過在那之前就快要直播了呢！\\n小可蘿\\n妳準備得如何了？', 31, 0, '');
