insert into login_bonus_message_data values (222, 80003, 0, 10, 0, 0, 105801, '貪吃佩可', '喔～感覺妳非常有自信呢……！\\n看來會是一場很棒的現場直播呢☆\\n……咦？\u3000我怎麼感覺這邊\\n好像晃得很厲害？', 32, 0, '');
