insert into login_bonus_message_data values (224, 80003, 1, 10, 0, 0, 105901, '可可蘿', '凱留小姐？\\n您是不是感冒了？\\n身體顫抖得好厲害……', 30, 0, '');
insert into login_bonus_message_data values (225, 80003, 2, 10, 0, 0, 106001, '凱留', '我我我我才沒有感冒！\\n這這這這只是\\n因因因為我太期待現場直播\\n興興興興奮得停不下來而已！', 40, 0, '');
insert into login_bonus_message_data values (226, 80003, 0, 10, 0, 0, 105801, '貪吃佩可', '妳沒事吧？小凱留！？\\n對了，妳把這個喝了！', 33, 0, '');
insert into login_bonus_message_data values (227, 80003, 2, 10, 0, 0, 106001, '凱留', '妳妳妳還蠻體貼的嘛！謝謝，\\n咕嚕、咳……呸呸！？\\n妳給我喝了什麼東西！？\\n好辣！\u3000好苦！？\u3000好甜！？', 41, 0, '');
insert into login_bonus_message_data values (228, 80003, 0, 10, 0, 0, 105801, '貪吃佩可', '這個裡面放了魔物的萃取物\\n還有藥草跟很多其他的東西\\n混合而成的特製能量飲料！', 34, 0, '');
insert into login_bonus_message_data values (229, 80003, 0, 10, 0, 0, 105801, '貪吃佩可', '只要喝了這個身體就會暖暖的，\\n變得精神飽滿唷♪', 35, 0, '');
insert into login_bonus_message_data values (230, 80003, 2, 10, 0, 0, 106001, '凱留', '妳這個白癡佩可！\\n我不是已經說我沒有感冒了！？\\n妳不要趁亂\\n餵我喝那種恐怖的飲料～！', 42, 0, '');
