insert into login_bonus_message_data values (324, 60002, 2, 0, 4, 1, 0, '', '今天的運勢是小吉！\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000自由自在地度過每一天就是通往幸福的捷徑！\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000望著吃魚吃得津津有味的貓咪，放鬆地度過這一天如何？', 0, 0, '');
