insert into login_bonus_message_data values (329, 60002, 3, 0, 0, 0, 190804, '花凜', '抽籤的結果如何呢？\\n嶄新的一天，希望對你來說\\n能變成很好的一天，\\n明天也要再來玩唷♪', 2, 0, '');
