insert into login_bonus_message_data values (333, 80005, 3, 1, 0, 0, 105801, '貪吃佩可', '「恭喜恭喜！\\n哇～啪啪啪啪～！」\\n……用這種感覺可以嗎！？', 1, 0, '');
insert into login_bonus_message_data values (334, 80005, 4, 1, 0, 0, 105901, '可可蘿', '那在下也從後面\\n丟摔炮應戰。\\n啊啊，好期待當天的到來。', 2, 0, '');
