insert into login_bonus_message_data values (336, 80005, 1, 1, 0, 0, 106001, '凱留', '畢竟雪菲是第一次嘛。\\n我們是因為那一天快到了，\\n所以在進行預演。\\n這樣很性急對吧～', 1, 0, '');
insert into login_bonus_message_data values (337, 80005, 0, 1, 0, 0, 106401, '雪菲', '那一天……？', 3, 0, '');
insert into login_bonus_message_data values (338, 80005, 3, 1, 0, 0, 105801, '貪吃佩可', '沒錯……\\n值得紀念的那一天\\n終於快要到了！', 2, 0, '');
insert into login_bonus_message_data values (339, 80005, 3, 1, 0, 0, 105801, '貪吃佩可', '「公主連結！\\nRe:Dive」3.5周年！', 3, 0, '');
insert into login_bonus_message_data values (340, 80005, 2, 1, 0, 0, 190801, '花凜', '太讚了吧☆', 1, 0, '');
insert into login_bonus_message_data values (341, 80005, 1, 1, 0, 0, 106001, '凱留', '呃，妳怎麼隨隨便便\\n跑進來啊，花凜！', 2, 0, '');
insert into login_bonus_message_data values (342, 80005, 2, 1, 0, 0, 190801, '花凜', '呵呵，又沒關係。\\n請讓我以【公會管理協會】\\n職員的身分，\\n跟大家一起慶祝吧！', 2, 0, '');
insert into login_bonus_message_data values (343, 80005, 2, 1, 0, 0, 190801, '花凜', '而且……當紀念日接近時，\\n我還會送上珍藏的情報♪', 3, 0, '');
insert into login_bonus_message_data values (344, 80005, 3, 1, 0, 0, 105801, '貪吃佩可', '因此，\\n從今天起，雪菲也一起進行\\n倒數計時吧！\\n一直到3.5周年當天！', 4, 0, '');
insert into login_bonus_message_data values (345, 80005, 0, 1, 0, 0, 106401, '雪菲', '咦！？我想想……！\\n還、還剩下14天……！', 4, 0, '');
insert into login_bonus_message_data values (346, 80005, 3, 2, 0, 0, 105801, '貪吃佩可', '距離3.5周年還剩下13天。\\n我想豪華且盛大地\\n慶祝一番呢☆', 5, 0, '');
insert into login_bonus_message_data values (347, 80005, 1, 2, 0, 0, 105901, '可可蘿', '說到豪華，之前似乎有某位貴族\\n舉辦了舞會，\\n而且主人還為那個舞會\\n幫了不少忙。', 3, 0, '');
insert into login_bonus_message_data values (348, 80005, 1, 2, 0, 0, 105901, '可可蘿', '主人在那裡學會了何謂美姿美儀，\\n所以連在下也受教了。\\n優雅優雅……\\n在下表現得如何呢？', 4, 0, '');
insert into login_bonus_message_data values (349, 80005, 3, 2, 0, 0, 105801, '貪吃佩可', '妳表現得很棒喔☆\\n看起來就像是公主一樣。\\n難得有這個機會，我也來表現一下，\\n優雅優雅～', 6, 0, '');
insert into login_bonus_message_data values (350, 80005, 1, 2, 0, 0, 105901, '可可蘿', '真是了不起，\\n貪吃佩可小姐。', 5, 0, '');
insert into login_bonus_message_data values (351, 80005, 0, 2, 0, 0, 106001, '凱留', '不，\\n妳非會不可吧。\\n畢竟是真正的公主。', 3, 0, '');
insert into login_bonus_message_data values (352, 80005, 2, 2, 0, 0, 106401, '雪菲', '優雅優雅……\\n呵呵，意外地簡單呢。', 5, 0, '');
insert into login_bonus_message_data values (353, 80005, 3, 2, 0, 0, 105801, '貪吃佩可', '哇！\\n雪菲的姿勢好美♪', 7, 0, '');
insert into login_bonus_message_data values (354, 80005, 1, 2, 0, 0, 105901, '可可蘿', '好了，\\n接著換凱留小姐囉。', 6, 0, '');
insert into login_bonus_message_data values (355, 80005, 0, 2, 0, 0, 106001, '凱留', '我、我就不用了！\\n那種事我根本不懂……', 4, 0, '');
insert into login_bonus_message_data values (356, 80005, 2, 2, 0, 0, 106401, '雪菲', '不用擔心，\\n實際嘗試就會知道很開心喔。\\n來吧來吧。', 6, 0, '');
insert into login_bonus_message_data values (357, 80005, 0, 2, 0, 0, 106001, '凱留', '既、既然妳都那麼說了……\\n那就沒辦……呀！', 5, 0, '');
insert into login_bonus_message_data values (358, 80005, 0, 2, 0, 0, 106001, '凱留', '痛痛痛……討厭～！\\n為什麼我會遇到這種事啊～！', 6, 0, '');
insert into login_bonus_message_data values (359, 80005, 1, 2, 0, 0, 105901, '可可蘿', '別灰心別灰心，\\n只要在紀念日之前好好練習，\\n或許就學得會了。\\n不嫌棄的話，在下可以教您喔。', 7, 0, '');
insert into login_bonus_message_data values (360, 80005, 0, 2, 0, 0, 106001, '凱留', '好！\\n妳們會我卻不會的感覺\\n讓人很不爽，\\n我絕對要學會！！', 7, 0, '');
insert into login_bonus_message_data values (361, 80005, 1, 3, 0, 0, 105901, '可可蘿', '距離3.5周年還剩下12天。\\n紀念日當天就不用說了，\\n等待當天到來的時間\\n也很令人興奮。', 8, 0, '');
insert into login_bonus_message_data values (362, 80005, 3, 3, 0, 0, 105801, '貪吃佩可', '我懂那種感覺！\\n光是思考當天能吃到哪些特別料理，\\n就令人很興奮對吧！', 8, 0, '');
insert into login_bonus_message_data values (363, 80005, 1, 3, 0, 0, 105901, '可可蘿', '說到特別的料理，就會想到祭典呢。\\n對了，在下聽說主人\\n曾經以學生的身分\\n參加聖學祭……', 9, 0, '');
insert into login_bonus_message_data values (364, 80005, 2, 3, 0, 0, 106401, '雪菲', '聖學祭……是什麼樣的祭典啊？', 7, 0, '');
insert into login_bonus_message_data values (365, 80005, 1, 3, 0, 0, 105901, '可可蘿', '那是聖德蕾莎女子學院的祭典喔。\\n主旨似乎是由兩個隊伍相互競爭，\\n藉以充分享受青春。', 10, 0, '');
insert into login_bonus_message_data values (366, 80005, 0, 3, 0, 0, 106001, '凱留', '那是指有慈樂之音的\\n驚喜演唱會\\n的祭典對吧？\\n我有點想看看……', 8, 0, '');
insert into login_bonus_message_data values (367, 80005, 0, 3, 0, 0, 106001, '凱留', '慈樂之音之後的\\n演唱會似乎也大受好評不是？\\n我記得是叫\\n好朋友社來著……？', 9, 0, '');
insert into login_bonus_message_data values (368, 80005, 3, 3, 0, 0, 105801, '貪吃佩可', '好朋友社……\\n感覺社員的感情都很好，\\n太讚了吧☆', 9, 0, '');
insert into login_bonus_message_data values (369, 80005, 0, 3, 0, 0, 106001, '凱留', '不，名字也取得太直接了吧……', 10, 0, '');
insert into login_bonus_message_data values (370, 80005, 2, 3, 0, 0, 106401, '雪菲', '聖學祭嗎……總覺得會很開心呢。\\n而且好朋友社的成員\\n也很令人在意。', 8, 0, '');
insert into login_bonus_message_data values (371, 80005, 3, 3, 0, 0, 105801, '貪吃佩可', '那為了不輸給她們，\\n我們也好好相處吧♪\\n來嘛來嘛，小雪菲，\\n我們來抱抱吧♪', 10, 0, '');
insert into login_bonus_message_data values (372, 80005, 2, 3, 0, 0, 106401, '雪菲', '咦？咦咦……\\n抱抱……？', 9, 0, '');
insert into login_bonus_message_data values (373, 80005, 0, 4, 0, 0, 106001, '凱留', '距離3.5周年還剩下11天！？\\n我根本沒準備慶祝的事情……\\n啊啊～好想回到昨天……', 11, 0, '');
insert into login_bonus_message_data values (374, 80005, 2, 4, 0, 0, 106401, '雪菲', '這麼說來，\\n我曾經聽說世上確實有那種魔導具。\\n我記得名字是叫時間儀……', 10, 0, '');
insert into login_bonus_message_data values (375, 80005, 0, 4, 0, 0, 106001, '凱留', '那是怎樣，太不妙了吧。\\n不過，若真的有那種東西，\\n比起準備，\\n我有更多想重新來過的事情……', 12, 0, '');
insert into login_bonus_message_data values (376, 80005, 1, 4, 0, 0, 105901, '可可蘿', '那難道是指新年那件事嗎……？\\n那時沒有錢，\\n真的很辛苦。', 11, 0, '');
insert into login_bonus_message_data values (377, 80005, 0, 4, 0, 0, 106001, '凱留', '那是……\\n如果當時有辦法阻止\\n隨便揮霍的貪吃佩可，\\n就不用辛苦成那樣了。', 13, 0, '');
insert into login_bonus_message_data values (378, 80005, 3, 4, 0, 0, 105801, '貪吃佩可', '確實是有過那種事呢！\\n聽妳說想重新來過，\\n我還以為會是指\\n「凱留凱留」那件事耶！', 11, 0, '');
insert into login_bonus_message_data values (379, 80005, 0, 4, 0, 0, 106001, '凱留', '不要揭穿別人的祕密啦，\\n妳這白癡佩可！\\n雪菲可不知道那件事！', 14, 0, '');
insert into login_bonus_message_data values (380, 80005, 0, 4, 0, 0, 106001, '凱留', '既然如此，\\n我絕對要設法找到時間儀，\\n消除剛才的對話……\\n呃，雪菲妳怎麼了？', 15, 0, '');
insert into login_bonus_message_data values (381, 80005, 2, 4, 0, 0, 106401, '雪菲', '嗯……如果是我……\\n我會想看看我不曾體驗過，\\n跟大家一起度過的時間……', 11, 0, '');
insert into login_bonus_message_data values (382, 80005, 3, 4, 0, 0, 105801, '貪吃佩可', '小雪菲！就算沒有時間儀\\n那種東西也沒關係的！\\n只要接下來\\n不斷製造共同的回憶就行了！', 12, 0, '');
insert into login_bonus_message_data values (383, 80005, 1, 4, 0, 0, 105901, '可可蘿', '是的，現在這個瞬間\\n應該也已經成為\\n未來會覺得無可取代的回憶了。', 12, 0, '');
insert into login_bonus_message_data values (384, 80005, 0, 4, 0, 0, 106001, '凱留', '所以妳不要擺出那種表情。\\n好了，我們來進行慶祝的準備吧！', 16, 0, '');
insert into login_bonus_message_data values (385, 80005, 2, 4, 0, 0, 106401, '雪菲', '大家……說得也是。\\n就一起把它變成最棒的慶祝，\\n最棒的回憶吧！', 12, 0, '');
