insert into room_unit_comments values (1087, 102902, 2, 101, 8, 0, 1, '聽過我的新歌了嗎？\\n我把和你發生的事情\\n作成主題曲了喔\\n至於是在什麼地方那是祕密♪', 0);
insert into room_unit_comments values (1088, 102902, 2, 102, 8, 0, 1, '吶，即使我──\\n成為了超級偶像\\n也要一直在我身邊\\n不要離開哦？', 0);
insert into room_unit_comments values (1089, 102902, 2, 1, 1, 1, 1, '我想來做\\n早晨的發聲練習，\\n你要不要一起嗎？', 0);
insert into room_unit_comments values (1090, 102902, 2, 2, 1, 2, 1, '要不要去吃午餐♪\\n有一間沒有人\\n知道的餐廳！', 0);
insert into room_unit_comments values (1091, 102902, 2, 3, 1, 3, 1, '夜幕包裹住了兩人\\n……這種\\n歌詞也不錯呢。', 0);
insert into room_unit_comments values (1092, 102902, 3, 1, 1, 0, 1, '儀容檢查完畢！\\n偶像無時無刻\\n都必須保持完美！', 0);
insert into room_unit_comments values (1093, 102902, 3, 2, 1, 0, 1, '唉……\\n這副德性，可不能\\n讓粉絲們看見啊~', 0);
insert into room_unit_comments values (1094, 102902, 3, 3, 1, 0, 1, '好可愛～♪\\n强大的對手\\n登場了！', 0);
insert into room_unit_comments values (1095, 102902, 3, 4, 1, 0, 1, '大家印象中的\\n偶像的味道\\n是什麼樣的呢～', 0);
insert into room_unit_comments values (1096, 102902, 3, 5, 1, 0, 1, '身體都暖和起來了！\\n來進行\\n舞蹈課吧♪', 0);
insert into room_unit_comments values (1097, 102902, 3, 6, 1, 0, 1, '刷新最佳記錄了！\\n為了演唱會的演出\\n不增強體力可不行……', 0);
insert into room_unit_comments values (1098, 102902, 3, 7, 1, 0, 1, '胖起來了的話\\n可怎麼辦……？\\n但我的手根本停不下來啊~', 0);
insert into room_unit_comments values (1099, 102902, 3, 8, 1, 0, 1, '這本愛情小說真不錯！\\n如果我不是\\n偶像的話……', 0);
insert into room_unit_comments values (1101, 103001, 1, 1, 1, 0, 1, '手裏劍的數量\\n不太對是也！\\n是丟在\\n什麼地方了嗎……？', 0);
insert into room_unit_comments values (1102, 103001, 1, 2, 1, 0, 1, '可惡之人，你就算是藏匿起來\\n也是沒用的是也！\\n……是錯覺啊。', 0);
insert into room_unit_comments values (1103, 103001, 1, 3, 1, 0, 1, '金錢\\n乃循環天下之物！\\n話說，\\n這是什麼意思啊？', 0);
insert into room_unit_comments values (1104, 103001, 1, 4, 1, 0, 1, '嗯，此處\\n有惡代官的氣息是也。', 0);
insert into room_unit_comments values (1105, 103001, 1, 5, 1, 0, 1, '領土爭奪之戰，\\n我最期待了是也！', 0);
insert into room_unit_comments values (1106, 103001, 1, 101, 4, 0, 1, '能夠\\n遇上將軍，\\n我真是走運的人是也！', 0);
insert into room_unit_comments values (1107, 103001, 1, 102, 8, 0, 1, '最近，待在將軍身旁時\\n感覺心裡\\n撲通撲通的是也……', 0);
insert into room_unit_comments values (1108, 103001, 2, 1, 1, 0, 1, '無論有什麼命令\\n都儘管下達是也！\\n將軍的性命\\n是絕對要保護的是也！', 0);
insert into room_unit_comments values (1109, 103001, 2, 2, 1, 0, 1, '啊，將軍，\\n在下何時能\\n上陣呢！？', 0);
insert into room_unit_comments values (1110, 103001, 2, 3, 1, 0, 1, '不，不虧是\\n將軍是也……\\n是什麼時候到我背後的！？', 0);
insert into room_unit_comments values (1111, 103001, 2, 4, 1, 0, 1, '是上陣的信號，\\n準備好了是也！\\n我的海螺\\n跑去哪兒了！？', 0);
insert into room_unit_comments values (1112, 103001, 2, 101, 4, 0, 1, '只要被注視著，\\n心裡就撲通撲通地跳著……\\n這是新的忍法嗎？', 0);
insert into room_unit_comments values (1113, 103001, 2, 102, 8, 0, 1, '將軍！\\n欸嘿嘿……今後\\n也請讓我隨待\\n您身旁好嗎？', 0);
insert into room_unit_comments values (1114, 103001, 2, 1, 1, 1, 1, '早安是也！\\n今天也是\\n適合修煉忍法的日子是也！', 0);
insert into room_unit_comments values (1115, 103001, 2, 2, 1, 2, 1, '肚子也\\n差不多餓了是也。\\n來吃午飯了是也！', 0);
insert into room_unit_comments values (1116, 103001, 2, 3, 1, 3, 1, '明天肯定\\n也會是好日子哦，\\n將軍！', 0);
insert into room_unit_comments values (1117, 103001, 3, 1, 1, 0, 1, '梳個髮髻\\n似乎也\\n挺有意思的是也！', 0);
