insert into room_unit_comments values (1151, 103101, 1, 1, 1, 0, 1, '爸爸，是不是該是時候\\n讓孩子獨立……不要？\\n這樣啊……', 0);
insert into room_unit_comments values (1152, 103101, 1, 2, 1, 0, 1, '爸爸呀，對男生的標準\\n總是很嚴格呢', 0);
insert into room_unit_comments values (1153, 103101, 1, 3, 1, 0, 1, '想要算命占卜\\n的人，請隨時\\n告訴我。', 0);
