insert into room_unit_comments values (1158, 103101, 2, 1, 1, 0, 1, '看出有\\n桃花劫耶……\\n請小心點啊。', 0);
insert into room_unit_comments values (1159, 103101, 2, 2, 1, 0, 1, '你今日的運勢是\\n大凶……\\n是大吉。', 0);
insert into room_unit_comments values (1160, 103101, 2, 3, 1, 0, 1, '跟爸爸散步中。\\n若沒有按照\\n每天固定的行程散步，\\n會累積壓力的。', 0);
insert into room_unit_comments values (1161, 103101, 2, 4, 1, 0, 1, '小伙子！\\n竟然敢碰忍！\\n你真是好大的膽子。', 0);
insert into room_unit_comments values (1162, 103101, 2, 101, 4, 0, 1, '只被你觸碰到，\\n心就跳的很快。\\n難道這是靈異現象？', 0);
insert into room_unit_comments values (1163, 103101, 2, 102, 8, 0, 1, '今天也受到很多幽靈\\n的歡迎呢。\\n真可愛。', 0);
