insert into room_unit_comments values (1165, 103101, 2, 2, 1, 2, 1, '今天的幸運物\\n是骷髏頭喔，\\n如果需要的話，\\n可以把父親大人借給你喔？', 0);
insert into room_unit_comments values (1166, 103101, 2, 3, 1, 3, 1, '你今天的運勢\\n超旺的。\\n今天就快要\\n結束了。', 0);
insert into room_unit_comments values (1167, 103101, 3, 1, 1, 0, 1, '爸爸。\\n雖然總是，\\n很注重打扮，\\n但卻沒什麼變化呢。', 0);
insert into room_unit_comments values (1168, 103101, 3, 2, 1, 0, 1, '小憩一下吧。\\n被附身好累。', 0);
insert into room_unit_comments values (1169, 103101, 3, 3, 1, 0, 1, '好可愛……\\n不知道付喪神願不願意附身呢。', 0);
insert into room_unit_comments values (1170, 103101, 3, 4, 1, 0, 1, '爸爸，\\n如果太過放鬆的話\\n就可能會去投胎轉世喔？', 0);
insert into room_unit_comments values (1171, 103101, 3, 5, 1, 0, 1, '很舒適呀……\\n像極樂淨土一樣……', 0);
insert into room_unit_comments values (1172, 103101, 3, 6, 1, 0, 1, '呼呼……\\n爸，爸爸\\n來代替我運動一下……', 0);
insert into room_unit_comments values (1173, 103101, 3, 7, 1, 0, 1, '爸爸也\\n想吃嗎?\\n即使已經死了還是想吃?', 0);
insert into room_unit_comments values (1174, 103101, 3, 8, 1, 0, 1, '這本書……\\n該不會是本預言書吧?', 0);
insert into room_unit_comments values (1176, 103102, 1, 1, 1, 0, 1, '在這裡的幽靈\\n好像都很幸福呢。', 0);
insert into room_unit_comments values (1177, 103102, 1, 2, 1, 0, 1, '爸爸都\\n只跟\\n女生搭話\\n有點困擾呢。', 0);
insert into room_unit_comments values (1178, 103102, 1, 3, 1, 0, 1, '雖然投胎轉世是\\n令人開心的事……但是\\n少了朋友卻很令人寂寞呢。', 0);
insert into room_unit_comments values (1179, 103102, 1, 4, 1, 0, 1, '會被幽靈喜歡的人\\n都是很棒的人呦。\\n當然，你也是呦。', 0);
