insert into room_unit_comments values (1210, 103201, 2, 3, 1, 0, 1, '常常被人說是\\n籠統帳……\\n但舉例又太細！', 0);
insert into room_unit_comments values (1211, 103201, 2, 4, 1, 0, 1, '用金錢解決\\n並不是\\n壞事吧？', 0);
insert into room_unit_comments values (1212, 103201, 2, 101, 4, 0, 1, '那個，如果可以的話，\\n要不要和我一起\\n散步呢？', 0);
insert into room_unit_comments values (1213, 103201, 2, 102, 8, 0, 1, '如果你\\n迷路的話，\\n我也會陪在你身邊！', 0);
insert into room_unit_comments values (1214, 103201, 2, 1, 1, 1, 1, '俗話說\\n早起的話就可以得到三盧幣，\\n原來還得到更多呢。', 0);
