insert into room_unit_comments values (1862, 105201, 2, 101, 4, 0, 1, '真想變人類呀～\\n或者是，你也變成\\n我這樣子……♪', 0);
insert into room_unit_comments values (1863, 105201, 2, 102, 8, 0, 1, '真拿你沒辦法呢，\\n能讓我這麼軟綿綿的，\\n就只有你喲？', 0);
insert into room_unit_comments values (1864, 105201, 2, 1, 1, 1, 1, '今天的毛髮睡的好難看\\n像是尾巴附近等等～', 0);
insert into room_unit_comments values (1865, 105201, 2, 2, 1, 2, 1, '午餐\\n想去去看\\n時髦的咖啡廳呢～♪', 0);
insert into room_unit_comments values (1866, 105201, 2, 3, 1, 3, 1, '為了保養肌膚\\n敷著面膜\\n睡覺吧～', 0);
insert into room_unit_comments values (1867, 105201, 3, 1, 1, 0, 1, '討厭，\\n又睡壞毛髮了！\\n要弄好才行！', 0);
insert into room_unit_comments values (1868, 105201, 3, 2, 1, 0, 1, '剛剛地板吱吱響了！\\n是不是因為我，太重了！？', 0);
insert into room_unit_comments values (1869, 105201, 3, 3, 1, 0, 1, '啊！\u3000你也是\\n美人胚子呢～♪', 0);
insert into room_unit_comments values (1870, 105201, 3, 4, 1, 0, 1, '哈，悠哉悠哉……\\n差點就要用\\n四條腿走路了呢……', 0);
insert into room_unit_comments values (1871, 105201, 3, 5, 1, 0, 1, '真~是暖和♪……我，可能因為這毛皮的關係變得太熱了。', 0);
insert into room_unit_comments values (1872, 105201, 3, 6, 1, 0, 1, '怎，怎麼覺得\\n快回歸大自然了！', 0);
insert into room_unit_comments values (1873, 105201, 3, 7, 1, 0, 1, '嗑嗑，嚼嚼，\\n呸呸……\\n真討厭，沒水準！', 0);
insert into room_unit_comments values (1874, 105201, 3, 8, 1, 0, 1, '呀ーーー！\\n這本愛情小說\\n非~常\\n讓人心跳加速～', 0);
insert into room_unit_comments values (1876, 105202, 1, 1, 1, 0, 1, '好可愛～\\n感覺只要待在這房間\\n就會有女人味～', 0);
insert into room_unit_comments values (1877, 105202, 1, 2, 1, 0, 1, '我還好吧？\\n其他孩子會不會\\n怕我呢……？', 0);
insert into room_unit_comments values (1878, 105202, 1, 3, 1, 0, 1, '動物苑……\\n真是個討厭的公會名呢……', 0);
insert into room_unit_comments values (1879, 105202, 1, 4, 1, 0, 1, '喜歡，不喜歡，喜歡……\\n呀！\u3000不小心\\n把花占卜用的花\\n吃掉了', 0);
insert into room_unit_comments values (1880, 105202, 1, 5, 1, 0, 1, '這裡有好多\\n有女人味的東西呢～！\\n心跳加速呢～♪', 0);
insert into room_unit_comments values (1881, 105202, 1, 101, 8, 0, 1, '說到體力\\n我是有自信啦……\\n戀愛方面\\n就有點膽怯了。', 0);
insert into room_unit_comments values (1882, 105202, 1, 102, 8, 0, 1, '在那個人\\n來之前\\n來砍砍柴吧？', 0);
insert into room_unit_comments values (1883, 105202, 2, 1, 1, 0, 1, '要不要一起出門呢？\\n雖然很開心……\\n但還沒做好心理準備！\\n哇哇哇哇！', 0);
insert into room_unit_comments values (1884, 105202, 2, 2, 1, 0, 1, '怎麼了嗎？\\n怎麼突然抱住我……\\n想被療癒嗎？', 0);
insert into room_unit_comments values (1885, 105202, 2, 3, 1, 0, 1, '呸呸！\\n……真、真不好意思！', 0);
insert into room_unit_comments values (1886, 105202, 2, 4, 1, 0, 1, '如果在這裡\\n吃掉變身蘋果的話，\\n大家會嚇一大跳的。', 0);
insert into room_unit_comments values (1887, 105202, 2, 101, 8, 0, 1, '真拿你沒辦法呢，\\n能讓我這麼軟綿綿的，\\n就只有你喲？', 0);
insert into room_unit_comments values (1888, 105202, 2, 102, 8, 0, 1, '真想變人類呀～\\n或者是，你也變成\\n我這樣子……♪', 0);
insert into room_unit_comments values (1889, 105202, 2, 1, 1, 1, 1, '今天的毛髮睡的好難看\\n像是尾巴附近等等～', 0);
insert into room_unit_comments values (1890, 105202, 2, 2, 1, 2, 1, '因為在減肥中，\\n所以不吃午餐呦～', 0);
insert into room_unit_comments values (1891, 105202, 2, 3, 1, 3, 1, '為了保養肌膚\\n敷著面膜\\n睡覺吧～', 0);
insert into room_unit_comments values (1892, 105202, 3, 1, 1, 0, 1, '如果不可愛一點\\n就會被剃毛……\\n或是被捕獵的！', 0);
insert into room_unit_comments values (1893, 105202, 3, 2, 1, 0, 1, '剛剛地板吱吱響了！\\n是不是因為我，太重了！？', 0);
insert into room_unit_comments values (1894, 105202, 3, 3, 1, 0, 1, '啊！\u3000你也是\\n美人胚子呢～♪', 0);
insert into room_unit_comments values (1895, 105202, 3, 4, 1, 0, 1, '好香～\\n好像在森林一般\\n真令人安心～', 0);
insert into room_unit_comments values (1896, 105202, 3, 5, 1, 0, 1, '真可愛～♪……\\n哇呀！\u3000我的毛\\n怎麼變這樣～！', 0);
insert into room_unit_comments values (1897, 105202, 3, 6, 1, 0, 1, '怎，怎麼覺得\\n快回歸大自然了！', 0);
insert into room_unit_comments values (1898, 105202, 3, 7, 1, 0, 1, '比起吃什麼東西\\n和誰吃\\n更重要呢。', 0);
insert into room_unit_comments values (1899, 105202, 3, 8, 1, 0, 1, '呀ーーー！\\n這本愛情小說\\n非~常\\n讓人心跳加速～', 0);
insert into room_unit_comments values (1901, 105301, 1, 1, 1, 0, 1, '讓我們一起討論\\n讓純白之翼\\n更加興盛的方法吧！', 0);
insert into room_unit_comments values (1902, 105301, 1, 2, 1, 0, 1, '我可不是小孩……！\\n但，\\n點心我就收下吧！', 0);
insert into room_unit_comments values (1903, 105301, 1, 3, 1, 0, 1, '房間得要好好整理。\\n俗話說房間的髒亂\\n就等同內心的髒亂。', 0);
insert into room_unit_comments values (1904, 105301, 1, 4, 1, 0, 1, '唔唔唔……明天\\n當我一覺醒來時，\\n身高會不會長高呢……', 0);
insert into room_unit_comments values (1905, 105301, 1, 5, 1, 0, 1, '隨時都放有點心\\n的房間。我最喜歡\\n這種地方了……', 0);
insert into room_unit_comments values (1906, 105301, 1, 101, 4, 0, 1, '和他一起鍛鍊新招式，\\n真是讓人\\n迫不及待呢……！', 0);
insert into room_unit_comments values (1907, 105301, 1, 102, 8, 0, 1, '最近和他\\n每天都在一起，\\n過得非常充實呢……', 0);
insert into room_unit_comments values (1908, 105301, 2, 1, 1, 0, 1, '軍人的職責是\\n每天不斷地\\n自我鑽研。\\n今天也去鍛鍊吧？', 0);
insert into room_unit_comments values (1909, 105301, 2, 2, 1, 0, 1, '儘管每天都有喝牛奶，\\n但怎麼\\n就是長不高呢……', 0);
insert into room_unit_comments values (1910, 105301, 2, 3, 1, 0, 1, '閣下，\\n也給你一點糖果吧？\\n很甜喔～！\\n很好吃喔～！', 0);
insert into room_unit_comments values (1911, 105301, 2, 4, 1, 0, 1, '跟我一起\\n飛往更高的地方吧！', 0);
insert into room_unit_comments values (1912, 105301, 2, 101, 4, 0, 1, '閣下真的是個值得\\n依靠的男人呢……不，\\n我只是突然想到而已。', 0);
insert into room_unit_comments values (1913, 105301, 2, 102, 8, 0, 1, '能和閣下在一起\\n我真的很開心。\\n今後也請多多指教。', 0);
insert into room_unit_comments values (1914, 105301, 2, 1, 1, 1, 1, '嗯，美好的早晨。\\n不禁讓我想起\\n祖國的黎明！', 0);
insert into room_unit_comments values (1915, 105301, 2, 2, 1, 2, 1, '差不多該吃午餐了吧。\\n因為肚子餓\\n可無法戰鬥呢。', 0);
insert into room_unit_comments values (1916, 105301, 2, 3, 1, 3, 1, '呼啊啊……\\n雖然我不是小孩……\\n但是睏了就是睏啊……', 0);
insert into room_unit_comments values (1917, 105301, 3, 1, 1, 0, 1, '儀容將會反映\\n內心的樣貌，\\n所以不得不謹慎呢。', 0);
insert into room_unit_comments values (1918, 105301, 3, 2, 1, 0, 1, '呼……雖然身為軍人\\n平時不能鬆懈，\\n但這……\\n真讓人放鬆……', 0);
insert into room_unit_comments values (1919, 105301, 3, 3, 1, 0, 1, '嗯……這麼可愛……\\n讓我好想\\n帶回祖國……', 0);
insert into room_unit_comments values (1920, 105301, 3, 4, 1, 0, 1, '這盞燈火，\\n真是療癒了我們\\n日夜爭戰的心呢……', 0);
insert into room_unit_comments values (1921, 105301, 3, 5, 1, 0, 1, '哈啊～暖呼呼……\\n不，不！\\n我可不會鬆懈喔！', 0);
insert into room_unit_comments values (1922, 105301, 3, 6, 1, 0, 1, '唔喔喔喔喔喔喔！\\n鍛鍊，鍛鍊，\\n鍛鍊！！', 0);
insert into room_unit_comments values (1923, 105301, 3, 7, 1, 0, 1, '為了能夠隨時應戰，\\n一定要保持\\n肚子八分飽的狀態！', 0);
insert into room_unit_comments values (1924, 105301, 3, 8, 1, 0, 1, '我要讀這本書，\\n想出新的戰術！', 0);
insert into room_unit_comments values (1926, 105302, 1, 1, 1, 0, 1, '偶爾也來\\n打掃一下好了。\\n房間變乾淨是件\\n很開心的事呢。', 0);
insert into room_unit_comments values (1927, 105302, 1, 2, 1, 0, 1, '這房間\\n美中不足的地方\\n在於沒空間能揮劍……', 0);
insert into room_unit_comments values (1928, 105302, 1, 3, 1, 0, 1, '隨時都放有點心\\n的房間。我最喜歡\\n這種地方了……', 0);
insert into room_unit_comments values (1929, 105302, 1, 4, 1, 0, 1, '儘管每天都有喝牛奶，\\n但怎麼\\n就是長不高呢……', 0);
insert into room_unit_comments values (1930, 105302, 1, 5, 1, 0, 1, '連家庭都無法守護的人\\n不可能夠格守護國家。\\n這個房間\\n也由我來守護！', 0);
insert into room_unit_comments values (1931, 105302, 1, 101, 8, 0, 1, '他現在\\n在做什麼呢……\\n呵呵。', 0);
insert into room_unit_comments values (1932, 105302, 1, 102, 8, 0, 1, '沒想到我會變得\\n如此期待\\n和他見面呢……', 0);
insert into room_unit_comments values (1933, 105302, 2, 1, 1, 0, 1, '軍人的職責是\\n每天不斷地\\n自我鑽研。\\n今天也去鍛鍊吧？', 0);
insert into room_unit_comments values (1934, 105302, 2, 2, 1, 0, 1, '喔喔，今天也得\\n鍛鍊呢！\\n深感愧歉，\\n我現在就去！', 0);
insert into room_unit_comments values (1935, 105302, 2, 3, 1, 0, 1, '我可不是小孩！\\n但是……偶爾也有想\\n回歸童心的時候……', 0);
insert into room_unit_comments values (1936, 105302, 2, 4, 1, 0, 1, '閣下，\\n也給你一點糖果吧？\\n很甜喔～！\\n很好吃喔～！', 0);
insert into room_unit_comments values (1937, 105302, 2, 101, 8, 0, 1, '想和我一起散步……？\\n好啊。我們兩人\\n一起去散步吧。', 0);
insert into room_unit_comments values (1938, 105302, 2, 102, 8, 0, 1, '能和閣下在一起\\n我真的很開心。\\n今後也請多多指教。', 0);
insert into room_unit_comments values (1939, 105302, 2, 1, 1, 1, 1, '嗯，美好的早晨。\\n不禁讓我想起\\n祖國的黎明！', 0);
insert into room_unit_comments values (1940, 105302, 2, 2, 1, 2, 1, '差不多是中午了呢。\\n先把鍛鍊緩緩，\\n吃個午餐吧。', 0);
insert into room_unit_comments values (1941, 105302, 2, 3, 1, 3, 1, '睡前……再稍微，\\n鍛鍊一下吧……', 0);
insert into room_unit_comments values (1942, 105302, 3, 1, 1, 0, 1, '為了不讓\\n純白之翼蒙羞，\\n身為成員的我必須\\n把自己打理好才行。', 0);
insert into room_unit_comments values (1943, 105302, 3, 2, 1, 0, 1, '呼……雖然身為軍人\\n平時不能鬆懈，\\n但這……\\n真讓人放鬆……', 0);
insert into room_unit_comments values (1944, 105302, 3, 3, 1, 0, 1, '嗯……這麼可愛……\\n讓我好想\\n帶回祖國……', 0);
insert into room_unit_comments values (1945, 105302, 3, 4, 1, 0, 1, '這盞燈火，\\n真是療癒了我們\\n日夜爭戰的心呢……', 0);
insert into room_unit_comments values (1946, 105302, 3, 5, 1, 0, 1, '哈啊～暖呼呼……\\n不，不！\\n我可不會鬆懈喔！', 0);
insert into room_unit_comments values (1947, 105302, 3, 6, 1, 0, 1, '唔喔喔喔喔喔喔！\\n鍛鍊，鍛鍊，\\n鍛鍊！！', 0);
insert into room_unit_comments values (1948, 105302, 3, 7, 1, 0, 1, '為了能夠隨時應戰，\\n一定要保持\\n肚子八分飽的狀態！', 0);
insert into room_unit_comments values (1949, 105302, 3, 8, 1, 0, 1, '嗯……腦細胞\\n開始活化了。\\n讀書真是重要呢。', 0);
insert into room_unit_comments values (1951, 105701, 1, 1, 1, 0, 1, '我們的騎空艇\\n要採用什麼樣的\\n設計呢……？', 0);
insert into room_unit_comments values (1952, 105701, 1, 2, 1, 0, 1, '來製作\\n團員募集的\\n海報吧♪', 0);
insert into room_unit_comments values (1953, 105701, 1, 3, 1, 0, 1, '爸爸…\\n現在在哪裡\\n又在做什麼呢？', 0);
insert into room_unit_comments values (1954, 105701, 1, 4, 1, 0, 1, '好想坐上騎空艇，\\n尋找誰也沒去過\\n的地方哦。', 0);
insert into room_unit_comments values (1955, 105701, 1, 5, 1, 0, 1, '我要成為\\n超越爸爸的，\\n世界第一的騎空士！', 0);
insert into room_unit_comments values (1956, 105701, 1, 101, 4, 0, 1, '和同伴一起空中旅行…\\n無論想像幾次，\\n都很興奮啊！', 0);
insert into room_unit_comments values (1957, 105701, 1, 102, 8, 0, 1, '爸爸……\\n我啊，也找到\\n重要的夥伴囉。', 0);
insert into room_unit_comments values (1958, 105701, 2, 1, 1, 0, 1, '工作委託嗎？\\n我馬上去\\n做好準備！', 0);
insert into room_unit_comments values (1959, 105701, 2, 2, 1, 0, 1, '喂喂！\\n一起來製作\\n團員募集的海報吧！', 0);
insert into room_unit_comments values (1960, 105701, 2, 3, 1, 0, 1, '什麼啦？\\n怎麼了嗎？\\n剛剛有叫我吧？', 0);
insert into room_unit_comments values (1961, 105701, 2, 4, 1, 0, 1, '現在是休息時間哦？\\n所以你也\\n好好放鬆一下吧。', 0);
insert into room_unit_comments values (1962, 105701, 2, 101, 4, 0, 1, '對我而言\\n你可是非常可靠，\\n令人驕傲的團員哦！', 0);
insert into room_unit_comments values (1963, 105701, 2, 102, 8, 0, 1, '要是見到爸爸，\\n我會向他介紹\\n你這位重要的同伴！', 0);
insert into room_unit_comments values (1964, 105701, 2, 1, 1, 1, 1, '早安！\\n身體狀況怎麼樣？\\n今天能夠好好加油嗎？', 0);
insert into room_unit_comments values (1965, 105701, 2, 2, 1, 2, 1, '喂喂，我們去\\n吃午飯吧！\\n肚子餓了啦。', 0);
insert into room_unit_comments values (1966, 105701, 2, 3, 1, 3, 1, '…咦？\\n不過聊一聊而已\\n已經這種時間了？', 0);
insert into room_unit_comments values (1967, 105701, 3, 1, 1, 0, 1, '要說的話，\\n真希望被稱為\\n時髦的騎空士啊', 0);
insert into room_unit_comments values (1968, 105701, 3, 2, 1, 0, 1, '呼，好累哦～……\\n我說，稍微\\n休息一下吧', 0);
insert into room_unit_comments values (1969, 105701, 3, 3, 1, 0, 1, '好！\u3000從現在起\\n你就是代理團長了！\\n…開玩笑的啦♪', 0);
insert into room_unit_comments values (1970, 105701, 3, 4, 1, 0, 1, '好香的味道♪\\n讓人心情\\n平靜下來了～……', 0);
insert into room_unit_comments values (1971, 105701, 3, 5, 1, 0, 1, '這個\\n能放在\\n騎空艇上嗎…？', 0);
insert into room_unit_comments values (1972, 105701, 3, 6, 1, 0, 1, '需要具備\\n什麼工作都做得來的體力\\n才行呢……！', 0);
insert into room_unit_comments values (1973, 105701, 3, 7, 1, 0, 1, '阿呣…嗯！\\n好好吃哦！\\n下次也來做做看吧♪', 0);
insert into room_unit_comments values (1974, 105701, 3, 8, 1, 0, 1, '要在空中冒險的話\\n有很多事情是\\n非知道不可的哦……', 0);
insert into room_unit_comments values (1976, 105702, 1, 1, 1, 0, 1, '……如果無法回去的話\\n該怎麼\\n還原呢……？', 0);
insert into room_unit_comments values (1977, 105702, 1, 2, 1, 0, 1, '好無聊呦……\\n有沒有\\n很強的人呀？', 0);
insert into room_unit_comments values (1978, 105702, 1, 3, 1, 0, 1, '轉換職業！\\n……就算再怎麼喊\\n也無法回去的呦。', 0);
insert into room_unit_comments values (1979, 105702, 1, 4, 1, 0, 1, '趁現在\\n去做擊退魔物\\n的工作呦！', 0);
insert into room_unit_comments values (1980, 105702, 1, 5, 1, 0, 1, '哈～…\\n嗯，氣的循環\\n沒有問題呢！', 0);
insert into room_unit_comments values (1981, 105702, 1, 101, 8, 0, 1, '我的力氣的來源是\\n與同伴間的\\n羈絆呦！', 0);
insert into room_unit_comments values (1982, 105702, 1, 102, 8, 0, 1, '總有一天想把\\n這拳頭穿越天空\\n直達星星呢。', 0);
insert into room_unit_comments values (1983, 105702, 2, 1, 1, 0, 1, '不夠強的人\\n該怎麼辦才好呢？\\n當然就是修行了呦！', 0);
insert into room_unit_comments values (1984, 105702, 2, 2, 1, 0, 1, '我打算像\\n平常一樣的\\n說話呦？', 0);
insert into room_unit_comments values (1985, 105702, 2, 3, 1, 0, 1, '怎麼了？\\n難不成\\n想要交手？', 0);
insert into room_unit_comments values (1986, 105702, 2, 4, 1, 0, 1, '無論是身為騎空士\\n還是拳法家\\n都把目標放在世界第一呢！', 0);
insert into room_unit_comments values (1987, 105702, 2, 101, 8, 0, 1, '就\\n交給你了呦！\\n請多指教唷！', 0);
insert into room_unit_comments values (1988, 105702, 2, 102, 8, 0, 1, '只要和你一起\\n一定能變得比現在\\n更強的！', 0);
insert into room_unit_comments values (1989, 105702, 2, 1, 1, 1, 1, '有乖乖起床嗎？\\n睡昏頭\\n可是不行的呦？', 0);
insert into room_unit_comments values (1990, 105702, 2, 2, 1, 2, 1, '肚子餓了呢……\\n我要去\\n吃點什麼！', 0);
insert into room_unit_comments values (1991, 105702, 2, 3, 1, 3, 1, '修行過度也\\n不太好呦？\\n我會小心的。', 0);
