insert into room_unit_comments values (2989, 105501, 2, 101, 4, 0, 1, '能和前輩在同個空間裡\\n……簡、簡直像作夢一般', 0);
insert into room_unit_comments values (2990, 105501, 2, 102, 8, 0, 1, '能、能在\\n前輩的身邊、我\\n現在非常\\n幸福……', 0);
insert into room_unit_comments values (2991, 105501, 2, 1, 1, 1, 1, '前、前輩……！\\n早！\\n早安！', 0);
insert into room_unit_comments values (2992, 105501, 2, 2, 1, 2, 1, '前輩，一起去\\n吃午餐吧！\\n來吧，要認真練習', 0);
insert into room_unit_comments values (2993, 105501, 2, 3, 1, 3, 1, '啊、前、前輩。\\n咦、外、外面…\\n已經很暗了', 0);
insert into room_unit_comments values (2995, 105501, 3, 2, 1, 0, 1, '前、前輩也\\n會在這休息……哈！\\n我、我在幹嘛！', 0);
insert into room_unit_comments values (2996, 105501, 3, 3, 1, 0, 1, '真可愛～\\n你要不要一起\\n觀察前輩？', 0);
insert into room_unit_comments values (2997, 105501, 3, 4, 1, 0, 1, '這裡可以\\n聞到前輩的香味喔', 0);
insert into room_unit_comments values (2998, 105501, 3, 5, 1, 0, 1, '如果能和前輩一起\\n取暖\\n就太棒了呢', 0);
insert into room_unit_comments values (3000, 105501, 3, 7, 1, 0, 1, '真好吃～\\n連前輩\\n都會喜歡吧', 0);
insert into room_unit_comments values (3001, 105501, 3, 8, 1, 0, 1, '接、接近\\n喜歡的人的方法…', 0);
insert into room_unit_comments values (3003, 109201, 1, 1, 1, 0, 1, '希望我的力量\\n能保護到\\n重要的人', 0);
insert into room_unit_comments values (3004, 109201, 1, 2, 1, 0, 1, '美麗的景色和\\n美味的食物！\\n這個世界也好棒', 0);
insert into room_unit_comments values (3005, 109201, 1, 3, 1, 0, 1, '想和這個世界的大家\\n感情\\n變得更好！', 0);
insert into room_unit_comments values (3006, 109201, 1, 4, 1, 0, 1, '哇！\\n這個也、那個也\\n全都好在意！', 0);
insert into room_unit_comments values (3007, 109201, 1, 5, 1, 0, 1, '即使知道\\n得振作，\\n但只要在古蕾婭身邊就不小心\\n想撒嬌～', 0);
insert into room_unit_comments values (3008, 109201, 1, 101, 4, 0, 1, '即使在不同的世界裡\\n但還是交得到朋友，\\n我真的非常開心呦！', 0);
insert into room_unit_comments values (3009, 109201, 1, 102, 8, 0, 1, '真想和大家一起\\n上課呢～\\n一定很好玩！', 0);
insert into room_unit_comments values (3010, 109201, 2, 1, 1, 0, 1, '走路的方式很漂亮？\\n欸嘿嘿，是嗎……？', 0);
insert into room_unit_comments values (3011, 109201, 2, 2, 1, 0, 1, '我很喜歡唸書喔！\\n因為增加知識\\n是很開心的事嘛', 0);
insert into room_unit_comments values (3012, 109201, 2, 3, 1, 0, 1, '我們的國家馬納歷亞，\\n是最早人們開始\\n會使用魔法\\n的地方喔！', 0);
insert into room_unit_comments values (3013, 109201, 2, 4, 1, 0, 1, '這個房間，\\n能不能\\n有更多的書架呢～……？', 0);
insert into room_unit_comments values (3014, 109201, 2, 101, 4, 0, 1, '你要\\n學學看魔法嗎？\\n我可以教你呦！', 0);
insert into room_unit_comments values (3015, 109201, 2, 102, 8, 0, 1, '真想喝\\n下午茶～……\\n要不要一起？', 0);
insert into room_unit_comments values (3016, 109201, 2, 1, 1, 1, 1, '早安！\\n今天也要\\n過超棒的一天喔！', 0);
insert into room_unit_comments values (3017, 109201, 2, 2, 1, 2, 1, '午餐\\n吃過了嗎？\\n下午也要加油呦！', 0);
insert into room_unit_comments values (3018, 109201, 2, 3, 1, 3, 1, '太在意\\n書的後續發展，一不注意就\\n熬夜了呢～……', 0);
insert into room_unit_comments values (3020, 109201, 3, 2, 1, 0, 1, '哈、真舒服～……\\n稍微\\n睡一下好了……', 0);
insert into room_unit_comments values (3021, 109201, 3, 3, 1, 0, 1, '哇、這好可愛！\\n真想讓古蕾婭\\n也看看呢', 0);
insert into room_unit_comments values (3022, 109201, 3, 4, 1, 0, 1, '好棒的香味～！\\n總覺得\\n讓人很安心呢', 0);
insert into room_unit_comments values (3023, 109201, 3, 5, 1, 0, 1, '啊，好溫暖\\n真幸福～♪\u3000身體都\\n暖活起來了！', 0);
insert into room_unit_comments values (3025, 109201, 3, 7, 1, 0, 1, '這、這個真好吃～！\\n停不下來啦！', 0);
insert into room_unit_comments values (3026, 109201, 3, 8, 1, 0, 1, '嗯嗯……\\n……哈！\u3000嗯，不小心\\n忘我了……', 0);
insert into room_unit_comments values (3028, 109301, 1, 1, 1, 0, 1, '只要進入新的建築就\\n一定會\\n迷路……', 0);
insert into room_unit_comments values (3029, 109301, 1, 2, 1, 0, 1, '點心在哪裡呢？\\n肚子\\n餓了', 0);
insert into room_unit_comments values (3030, 109301, 1, 3, 1, 0, 1, '有7份作業\\n忘了交，\\n但沒關係啦！', 0);
insert into room_unit_comments values (3031, 109301, 1, 4, 1, 0, 1, '淨發生些\\n不可思議的事……但\\n沒關係啦！\\n這也是青春的象徵啊', 0);
insert into room_unit_comments values (3032, 109301, 1, 5, 1, 0, 1, '有沒有什麼\\n有趣的事可做呢～？', 0);
insert into room_unit_comments values (3033, 109301, 1, 101, 4, 0, 1, '和大家\\n都已經成為朋友了！', 0);
insert into room_unit_comments values (3034, 109301, 1, 102, 8, 0, 1, '肚子餓了……', 0);
insert into room_unit_comments values (3035, 109301, 2, 1, 1, 0, 1, '食物也好吃，\\n這裡真是不錯的地方\\n露真幸福！', 0);
insert into room_unit_comments values (3036, 109301, 2, 2, 1, 0, 1, '你在說魔法的事嗎？\\n希望你能去問\\n安前輩……', 0);
insert into room_unit_comments values (3037, 109301, 2, 3, 1, 0, 1, '你也不太會\\n讀書嗎？', 0);
insert into room_unit_comments values (3038, 109301, 2, 4, 1, 0, 1, '露的帽子\\n可愛嗎？\\n我很喜歡♪', 0);
insert into room_unit_comments values (3039, 109301, 2, 101, 4, 0, 1, '有很多朋友，\\n露覺得很高興♪', 0);
insert into room_unit_comments values (3040, 109301, 2, 102, 8, 0, 1, '一起讀書……！？\\n哎呀，才不要！', 0);
insert into room_unit_comments values (3041, 109301, 2, 1, 1, 1, 1, '早上了呢……唔哇哇…\\n露還想\\n待在夢鄉裡……', 0);
insert into room_unit_comments values (3042, 109301, 2, 2, 1, 2, 1, '下午也要\\n大玩特玩喔～！', 0);
insert into room_unit_comments values (3043, 109301, 2, 3, 1, 3, 1, '熬夜什麼的\\n讓人心臟噗通噗通地跳！\\n真青春！', 0);
insert into room_unit_comments values (3045, 109301, 3, 2, 1, 0, 1, '鬆鬆軟軟的～♪\\n讓人好想\\n睡個午覺！', 0);
insert into room_unit_comments values (3046, 109301, 3, 3, 1, 0, 1, '這個好鬆軟！\\n坐在桌子前開始迷迷糊糊時\\n很適合當枕頭呢', 0);
insert into room_unit_comments values (3047, 109301, 3, 4, 1, 0, 1, '哇，好香的味道……\\n覺得心情\\n變好了呢', 0);
insert into room_unit_comments values (3048, 109301, 3, 5, 1, 0, 1, '好溫暖～！\\n真溫暖\\n我會再去玩的', 0);
insert into room_unit_comments values (3050, 109301, 3, 7, 1, 0, 1, '很、很好吃！\\n全部都\\n給露！', 0);
insert into room_unit_comments values (3051, 109301, 3, 8, 1, 0, 1, '只要……一看書、\\n睡意馬上就…\\n……呼……', 0);
insert into room_unit_comments values (3053, 109401, 1, 1, 1, 0, 1, '鋼琴……\\n有鋼琴嗎？', 0);
insert into room_unit_comments values (3054, 109401, 1, 2, 1, 0, 1, '嗯……\\n真安心……', 0);
insert into room_unit_comments values (3055, 109401, 1, 3, 1, 0, 1, '馬納歷亞總是\\n很熱鬧呢…', 0);
insert into room_unit_comments values (3056, 109401, 1, 4, 1, 0, 1, '大家現在……\\n在做些什麼呢？', 0);
insert into room_unit_comments values (3057, 109401, 1, 5, 1, 0, 1, '竟然來到\\n另一個世界……\\n真的很驚訝……', 0);
insert into room_unit_comments values (3058, 109401, 1, 101, 4, 0, 1, '我不太擅長\\n和別人說話……', 0);
insert into room_unit_comments values (3059, 109401, 1, 102, 8, 0, 1, '如果能和大家\\n一起上學的話……\\n也許會很有趣', 0);
insert into room_unit_comments values (3060, 109401, 2, 1, 1, 0, 1, '果然很在意我的翅膀和\\n尾巴……？', 0);
insert into room_unit_comments values (3061, 109401, 2, 2, 1, 0, 1, '最擅長的應該\\n還是炎之魔法吧……\\n雖然還是有點困難……', 0);
insert into room_unit_comments values (3062, 109401, 2, 3, 1, 0, 1, '我的故鄉是龍之國……\\n就在越過險峻山脈\\n的另一端', 0);
insert into room_unit_comments values (3063, 109401, 2, 4, 1, 0, 1, '安……？\\n啊、抱、抱歉呀！\\n我弄錯了……', 0);
insert into room_unit_comments values (3064, 109401, 2, 101, 4, 0, 1, '嗯……嗯……♪\\n……哇！\\n你聽到那聲音了嗎……？\\n好、好丟臉……', 0);
insert into room_unit_comments values (3065, 109401, 2, 102, 8, 0, 1, '有什麼事\\n是我能幫的嗎……？\\n儘管告訴我呦', 0);
insert into room_unit_comments values (3066, 109401, 2, 1, 1, 1, 1, '早晨的空氣呀，\\n非常的\\n沉靜又清新……\\n讓人能靜下心來呢', 0);
insert into room_unit_comments values (3067, 109401, 2, 2, 1, 2, 1, '雖然學院食堂的午餐\\n也很好吃……\\n但我也很喜歡這邊的食物呢', 0);
insert into room_unit_comments values (3068, 109401, 2, 3, 1, 3, 1, '差不多得\\n睡了呢\\n……今天會\\n好好注意睡相的……！', 0);
insert into room_unit_comments values (3070, 109401, 3, 2, 1, 0, 1, '想好好\\n解放……呵呵', 0);
insert into room_unit_comments values (3071, 109401, 3, 3, 1, 0, 1, '這、這對我來說\\n會不會太可愛……\\n但是真棒呢', 0);
insert into room_unit_comments values (3072, 109401, 3, 4, 1, 0, 1, '好香……\\n感覺\\n安也會喜歡……', 0);
insert into room_unit_comments values (3073, 109401, 3, 5, 1, 0, 1, '只要盯著火焰，\\n總覺得能安心下來……\\n會、會很怪嗎？', 0);
insert into room_unit_comments values (3075, 109401, 3, 7, 1, 0, 1, '哇、真好吃……！\\n這個是、怎麼\\n做的呢……？', 0);
insert into room_unit_comments values (3076, 109401, 3, 8, 1, 0, 1, '這本書……如果拿給\\n安看的話，\\n她會很開心的吧、嘻嘻', 0);
insert into room_unit_comments values (3078, 109501, 1, 1, 1, 0, 1, '雖然和服很漂亮，\\n但穿著很難行走……\\n乾脆脫了……嘻嘻嘻', 0);
insert into room_unit_comments values (3079, 109501, 1, 2, 1, 0, 1, '啊，不行\\n脫掉和服……哈\\n該不會虐待狂先生他……！', 0);
insert into room_unit_comments values (3080, 109501, 1, 3, 1, 0, 1, '這個房間最適合\\n拿來幻想……\\n呵呵呵……流口水', 0);
insert into room_unit_comments values (3081, 109501, 1, 4, 1, 0, 1, '慢慢展開扇子，\\n這樣……總覺得\\n有情色感', 0);
insert into room_unit_comments values (3082, 109501, 1, 5, 1, 0, 1, '「虐待狂先生\\n一邊說要按摩，\\n手一邊」…嘻嘻嘻', 0);
insert into room_unit_comments values (3083, 109501, 1, 101, 4, 0, 1, '…一邊想著虐待狂先生\\n的視線一邊練習\\n跳舞……太棒了！', 0);
insert into room_unit_comments values (3084, 109501, 1, 102, 8, 0, 1, '這不是很棒嘛，\\n這不是很棒嘛～\\n忍不住啦～', 0);
insert into room_unit_comments values (3085, 109501, 2, 1, 1, 0, 1, '咦～咦～～～！\\n虐待狂先生也是\\n我喜歡的人呢……嘻嘻嘻', 0);
insert into room_unit_comments values (3086, 109501, 2, 2, 1, 0, 1, '不管身在何處\\n都能感受到\\n虐待狂先生的氣息……', 0);
insert into room_unit_comments values (3087, 109501, 2, 3, 1, 0, 1, '在這種地方，\\n玩這種！\\n……只是妄想', 0);
insert into room_unit_comments values (3088, 109501, 2, 4, 1, 0, 1, '把空花撲倒，\\n然後直接……流口水\\n啊啊，和服被……！', 0);
insert into room_unit_comments values (3089, 109501, 2, 101, 4, 0, 1, '虐待狂先生真溫柔，\\n雖然很溫柔但虐待狂\\n先生果然是神經病……！', 0);
insert into room_unit_comments values (3090, 109501, 2, 102, 8, 0, 1, '我明白虐待狂先生\\n在想什麼事情了\\n好的……空花會脫的', 0);
insert into room_unit_comments values (3091, 109501, 2, 1, 1, 1, 1, '刺眼的朝陽的把空花的\\n身體曬的刺痛\\n……那也很棒！', 0);
insert into room_unit_comments values (3092, 109501, 2, 2, 1, 2, 1, '說什麼午餐\\n有空花就行，\\n太露骨了……流口水', 0);
insert into room_unit_comments values (3093, 109501, 2, 3, 1, 3, 1, '……今晚的虐待狂先生\\n真大膽……嘻嘻嘻……\\n啊，視線……！？', 0);
insert into room_unit_comments values (3095, 109501, 3, 2, 1, 0, 1, '這舒服的感覺，\\n心情太過舒適了，\\n我去把身子弄疼……！', 0);
insert into room_unit_comments values (3096, 109501, 3, 3, 1, 0, 1, '太可愛了……\\n可愛到\\n難以妄想……流口水', 0);
insert into room_unit_comments values (3097, 109501, 3, 4, 1, 0, 1, '只要被這股香氣\\n包圍就變得\\n好……舒服……', 0);
insert into room_unit_comments values (3098, 109501, 3, 5, 1, 0, 1, '「暖呼呼的身體\\n無法自拔\\n渴望者某人……」嘻嘻嘻', 0);
insert into room_unit_comments values (3100, 109501, 3, 7, 1, 0, 1, '肚子\\n膨了起來……那簡直\\n像是……嘻嘻嘻', 0);
insert into room_unit_comments values (3101, 109501, 3, 8, 1, 0, 1, '空花在找的\\n類型\\n在哪裡呀？', 0);
insert into room_unit_comments values (3103, 109601, 1, 1, 1, 0, 1, '花鳥風月\\n在東國代表\\n心的意思是也', 0);
insert into room_unit_comments values (3104, 109601, 1, 2, 1, 0, 1, '哦！\u3000我想到\\n新的忍法了是也！\\n趕緊來試試吧！', 0);
insert into room_unit_comments values (3105, 109601, 1, 3, 1, 0, 1, '東國的天氣\\n也真是\\n陰晴不定是也……', 0);
insert into room_unit_comments values (3106, 109601, 1, 4, 1, 0, 1, '我們將軍\\n永遠不滅，\\n出陣吧！', 0);
insert into room_unit_comments values (3107, 109601, 1, 5, 1, 0, 1, '隔牆有耳\\n隔紙窗有……嗯？\\n誰是瑪莉？', 0);
insert into room_unit_comments values (3108, 109601, 1, 101, 4, 0, 1, '只要有將軍在\\n無論是任何忍法\\n我都會使出來的是也', 0);
insert into room_unit_comments values (3109, 109601, 1, 102, 8, 0, 1, '不守規矩的傢伙\\n在何方？\u3000看我來用\\n忍法治治你們！', 0);
insert into room_unit_comments values (3110, 109601, 2, 1, 1, 0, 1, '將軍！\u3000我學會\\n新忍法了是也！\\n想讓你看看是也！', 0);
insert into room_unit_comments values (3111, 109601, 2, 2, 1, 0, 1, '看我的手裏劍！\\n看我把他\\n投出去！', 0);
insert into room_unit_comments values (3112, 109601, 2, 3, 1, 0, 1, '就由我來\\n除掉這些阻礙！\\n碰碰！', 0);
insert into room_unit_comments values (3113, 109601, 2, 4, 1, 0, 1, '密藏的忍法\\n狂風暴雨是也！\\n不可以偷看呦？', 0);
insert into room_unit_comments values (3114, 109601, 2, 101, 4, 0, 1, '將軍想蓋\\n幾層的\\n城堡呢？', 0);
insert into room_unit_comments values (3115, 109601, 2, 102, 8, 0, 1, '真想從\\n天守閣眺望\\n和平的城鎮是也……', 0);
insert into room_unit_comments values (3116, 109601, 2, 1, 1, 1, 1, '早安！\\n今天天象不錯\\n看來會是不錯的一天呢', 0);
insert into room_unit_comments values (3117, 109601, 2, 2, 1, 2, 1, '肚子餓了是也……\\n將軍也\\n一起吃飯吧！', 0);
insert into room_unit_comments values (3118, 109601, 2, 3, 1, 3, 1, '為避免夜襲，\\n在晚上走路時\\n要特別小心呦？', 0);
