insert into room_unit_comments values (3121, 109601, 3, 3, 1, 0, 1, '可愛到\\n讓我想用通靈之術\\n召喚出來', 0);
insert into room_unit_comments values (3122, 109601, 3, 4, 1, 0, 1, '好～香\\n香到可以\\n當作開發香豆的參考呢', 0);
insert into room_unit_comments values (3123, 109601, 3, 5, 1, 0, 1, '比起我的懷抱，\\n用這個更能溫暖草鞋……\\n出現競爭對手了……！', 0);
