insert into room_unit_comments values (3135, 109701, 2, 1, 1, 0, 1, '若您要出門的話，\\n請務必讓\\n雷姆陪同', 0);
insert into room_unit_comments values (3136, 109701, 2, 2, 1, 0, 1, '如果是雷姆\\n做得到的事\\n請盡管開口', 0);
insert into room_unit_comments values (3137, 109701, 2, 3, 1, 0, 1, '盯～～。沒什麼啦，\\n只是我對您頭髮的長度\\n有那麼點、稍微有點、\\n非常有點在意罷了', 0);
insert into room_unit_comments values (3138, 109701, 2, 4, 1, 0, 1, '鐵球嗎？\\n那是護身用的，\\n所以沒有問題喔？', 0);
insert into room_unit_comments values (3139, 109701, 2, 101, 4, 0, 1, '在學習數學方面\\n有什麼進展嗎？\\n若可以的話，\\n雷姆隨時都能教你喔', 0);
insert into room_unit_comments values (3140, 109701, 2, 102, 8, 0, 1, '接下來想要挑戰\\n更大的魔物料理。\\n整隻的烤飛龍\\n您覺得怎麼樣呢', 0);
insert into room_unit_comments values (3141, 109701, 2, 1, 1, 1, 1, '早安，\\n早餐已經\\n準備好了，\\n請到餐桌用餐', 0);
insert into room_unit_comments values (3142, 109701, 2, 2, 1, 2, 1, '已經到了午餐時間呢，\\n有沒有特別\\n想吃什麼呢？', 0);
insert into room_unit_comments values (3143, 109701, 2, 3, 1, 3, 1, '還沒準備就寢嗎？\\n因為女僕\\n必須是屋內\\n最晚睡的人', 0);
insert into room_unit_comments values (3145, 109701, 3, 2, 1, 0, 1, '真柔軟……\\n這柔軟度，\\n連鬼都無話可說了……', 0);
insert into room_unit_comments values (3146, 109701, 3, 3, 1, 0, 1, '呵呵……就算在\\n異世界，可愛的東西\\n就是可愛嘛', 0);
insert into room_unit_comments values (3147, 109701, 3, 4, 1, 0, 1, '好香的味道啊，\\n和魔女教的惡臭\\n可真是天差地別', 0);
insert into room_unit_comments values (3148, 109701, 3, 5, 1, 0, 1, '真暖和…哇！\\n這樣可不行！\\n會讓人……不、\\n會讓鬼都廢掉的！', 0);
insert into room_unit_comments values (3150, 109701, 3, 7, 1, 0, 1, '嚼嚼嚼嚼……真好吃！\\n如果能夠\\n重現這個味道，\\n或許會被誇獎……！', 0);
insert into room_unit_comments values (3151, 109701, 3, 8, 1, 0, 1, '好多畫……\\n異世界的書\\n真有趣呢', 0);
insert into room_unit_comments values (3153, 109801, 1, 1, 1, 0, 1, '即使身處異世界，\\n拉姆該做的事\\n也不會改變呢，\\n那就先休息吧', 0);
insert into room_unit_comments values (3154, 109801, 1, 2, 1, 0, 1, '有美乃滋這件事，\\n也許就代表著\\n離巴魯斯的故鄉\\n不遠了呢', 0);
insert into room_unit_comments values (3155, 109801, 1, 3, 1, 0, 1, '在這個世界裡\\n不時會聽到巴魯斯\\n所使用的辭彙呢，\\n真令人生氣', 0);
insert into room_unit_comments values (3156, 109801, 1, 4, 1, 0, 1, '除了拉姆之外，\\n還有其他的女僕吧，\\n拉姆想……不，\\n你這是硬塞工作吧', 0);
insert into room_unit_comments values (3157, 109801, 1, 5, 1, 0, 1, '……用千里眼觀看後，\\n周圍沒任何異變呢，\\n如果沒有危險的話，\\n接下來就交給雷姆了喔', 0);
insert into room_unit_comments values (3158, 109801, 1, 101, 4, 0, 1, '……拉姆還\\n能夠有機會\\n再被叫一聲\\n「姊姊」嗎', 0);
insert into room_unit_comments values (3159, 109801, 1, 102, 8, 0, 1, '哈！\u3000拉姆做的\\n蒸番薯可真完美呢\\n……若下次心情好時，\\n就再做一次吧', 0);
insert into room_unit_comments values (3160, 109801, 2, 1, 1, 0, 1, '如果有空管拉姆閒事，\\n還不如好好工作，\\n這麼一來，\\n拉姆也樂得輕鬆啦', 0);
insert into room_unit_comments values (3161, 109801, 2, 2, 1, 0, 1, '不管對拉姆抱有什麼\\n期待都是枉然喔，\\n能夠命令拉姆的\\n只有羅茲瓦爾大人唷', 0);
insert into room_unit_comments values (3162, 109801, 2, 3, 1, 0, 1, '如果很閒的話，\\n陪我去買東西吧，\\n我會讓你\\n幫忙提東西喔', 0);
insert into room_unit_comments values (3163, 109801, 2, 4, 1, 0, 1, '找拉姆有什麼事嗎？\\n如果是不正經的事，\\n我會把你扭斷喔', 0);
insert into room_unit_comments values (3164, 109801, 2, 101, 4, 0, 1, '這麼說來，我記得你\\n喪失了記憶嘛……\\n請努力想起來\\n……不然家人會悲傷的', 0);
insert into room_unit_comments values (3165, 109801, 2, 102, 8, 0, 1, '想吃蒸番薯嗎？\\n還真是厚臉皮呢\\n……因為還有剩，\\n所以儘管吃吧', 0);
insert into room_unit_comments values (3166, 109801, 2, 1, 1, 1, 1, '還真邋遢呢，\\n快洗把臉，\\n打起精神', 0);
insert into room_unit_comments values (3167, 109801, 2, 2, 1, 2, 1, '肚子差不多該餓了吧，\\n那就讓你為拉姆\\n準備午餐吧', 0);
insert into room_unit_comments values (3168, 109801, 2, 3, 1, 3, 1, '女僕必須是\\n屋子裡最後睡的人……\\n為了讓拉姆能去睡，\\n請你快點就寢', 0);
insert into room_unit_comments values (3170, 109801, 3, 2, 1, 0, 1, '……讓人想許下\\n永不離開這裡\\n的誓約呢', 0);
insert into room_unit_comments values (3171, 109801, 3, 3, 1, 0, 1, '這還……真可愛呢，\\n不過當然，\\n還是不及拉姆就是了', 0);
insert into room_unit_comments values (3172, 109801, 3, 4, 1, 0, 1, '……這是香爐嗎？\\n這個世界也有\\n高品質的東西嘛', 0);
insert into room_unit_comments values (3173, 109801, 3, 5, 1, 0, 1, '我搞不好\\n找到了喔……\\n拉姆的樂園！', 0);
insert into room_unit_comments values (3175, 109801, 3, 7, 1, 0, 1, '……嚼，\\n雖然比不上\\n拉姆的蒸番薯，\\n但還真不錯呢', 0);
insert into room_unit_comments values (3176, 109801, 3, 8, 1, 0, 1, '有些只有在異世界\\n才會出現的有趣內容，\\n真讓人深感興趣呢', 0);
insert into room_unit_comments values (3178, 109901, 1, 1, 1, 0, 1, '欸帕克、\\n先喵喵一下，\\n然後再喵喵怎麼樣', 0);
insert into room_unit_comments values (3179, 109901, 1, 2, 1, 0, 1, '反正還有時間，\\n就用這個世界的石頭\\n來堆一些東西吧', 0);
insert into room_unit_comments values (3180, 109901, 1, 3, 1, 0, 1, '而且我還是姊姊，\\n得向咲戀\\n學習才行……嗯！\\n我會非～常努力！', 0);
insert into room_unit_comments values (3181, 109901, 1, 4, 1, 0, 1, '這個世界\\n接受銀髮的\\n半精靈族呢……\\n總覺得好開心', 0);
insert into room_unit_comments values (3182, 109901, 1, 5, 1, 0, 1, '今天的飯菜……\\n沒有放\\n青椒吧……？', 0);
insert into room_unit_comments values (3183, 109901, 1, 101, 4, 0, 1, '真想\\n再和大家一起\\n去購物呢', 0);
insert into room_unit_comments values (3184, 109901, 1, 102, 8, 0, 1, '除了帕克之外，\\n第一次被\\n稱為家人……嘻嘻♪\\n好像有點開心！', 0);
insert into room_unit_comments values (3185, 109901, 2, 1, 1, 0, 1, '我正在幫\\n帕克理毛……\\n你要一起嗎？', 0);
insert into room_unit_comments values (3186, 109901, 2, 2, 1, 0, 1, '我也來幫點什麼忙……\\n啊，對了！\\n料理怎麼樣！', 0);
insert into room_unit_comments values (3187, 109901, 2, 3, 1, 0, 1, '吶，可以\\n在這邊\\n練習唱歌嗎？', 0);
insert into room_unit_comments values (3188, 109901, 2, 4, 1, 0, 1, '……啊，抱歉！\\n我不太習慣\\n被人搭話……', 0);
insert into room_unit_comments values (3189, 109901, 2, 101, 4, 0, 1, '因為很努力讀書了，\\n所以接下來也要\\n繼續陪伴喔', 0);
insert into room_unit_comments values (3190, 109901, 2, 102, 8, 0, 1, '預備，勝利～！\\n呵呵……\\n再一起做\\n廣播體操吧♪', 0);
insert into room_unit_comments values (3191, 109901, 2, 1, 1, 1, 1, '起來了嗎？\\n嘻嘻……太好了。\\n啊、我、我不是\\n在擔心你喔？', 0);
insert into room_unit_comments values (3192, 109901, 2, 2, 1, 2, 1, '請期待午餐♪\\n啊，這個世界\\n有美乃滋嗎？', 0);
insert into room_unit_comments values (3193, 109901, 2, 3, 1, 3, 1, '差不多該睡了喔。\\n熬夜是絕對\\n不行的！\u3000所以說！', 0);
insert into room_unit_comments values (3195, 109901, 3, 2, 1, 0, 1, '書也讀完了……\\n稍、稍微休息一下\\n沒問題吧……？', 0);
insert into room_unit_comments values (3196, 109901, 3, 3, 1, 0, 1, '好、好可愛……！\\n可愛度甚至和\\n帕克不相上下……！', 0);
insert into room_unit_comments values (3197, 109901, 3, 4, 1, 0, 1, '真香……\\n羅茲瓦爾的\\n宅邸裡也放有\\n這個吧？', 0);
insert into room_unit_comments values (3198, 109901, 3, 5, 1, 0, 1, '好溫暖……真想一直\\n待在這……啊！\\n不行不行！\u3000為了王選\\n得努力讀書才行！', 0);
insert into room_unit_comments values (3200, 109901, 3, 7, 1, 0, 1, '啊……這個真好吃！\\n這個世界有著\\n各種各樣的食物呢。', 0);
insert into room_unit_comments values (3201, 109901, 3, 8, 1, 0, 1, '上面寫著好多\\n我不懂的事呢……\\n異世界的書，\\n還真有趣呢', 0);
insert into room_unit_comments values (3203, 110001, 1, 1, 1, 0, 1, '海邊最棒了！\\n海灘拍攝\\n也超好玩♪', 0);
insert into room_unit_comments values (3204, 110001, 1, 2, 1, 0, 1, '20％是多少？\\n下次問問\\n秀才吧！', 0);
insert into room_unit_comments values (3205, 110001, 1, 3, 1, 0, 1, '真想和秀才\\n一起製造\\n夏日回憶呢～', 0);
insert into room_unit_comments values (3206, 110001, 1, 4, 1, 0, 1, '得到讓人興致高昂\\n的泳裝了，\\n超幸運啦～♪', 0);
insert into room_unit_comments values (3207, 110001, 1, 5, 1, 0, 1, '休息結束後，\\n得回去拍攝才行！\\n一鼓作氣♪', 0);
insert into room_unit_comments values (3208, 110001, 1, 101, 4, 0, 1, '真想和秀才在海灘\\n開派對呢～\\n感覺就超好玩對吧！', 0);
insert into room_unit_comments values (3209, 110001, 1, 102, 8, 0, 1, '和秀才在一起的時候，\\n真的非常好玩開心……\\n交男朋友\\n就是這種感覺嗎？', 0);
insert into room_unit_comments values (3210, 110001, 2, 1, 1, 0, 1, '你看你看～！\\n我畫了富有夏日風情的\\n指甲唷～♪', 0);
insert into room_unit_comments values (3211, 110001, 2, 2, 1, 0, 1, '秀才～\\n這件泳裝如何？\\n不錯吧♪', 0);
insert into room_unit_comments values (3212, 110001, 2, 3, 1, 0, 1, '泳裝照？\\n當然可以！\\n要拍得可愛一點喔！', 0);
insert into room_unit_comments values (3213, 110001, 2, 4, 1, 0, 1, '人家會和\\n夏日的海一樣\\n閃閃發亮喔！', 0);
insert into room_unit_comments values (3214, 110001, 2, 101, 4, 0, 1, '秀才和我\\n感覺\\n超級配的吧♪', 0);
insert into room_unit_comments values (3215, 110001, 2, 102, 8, 0, 1, '欸欸秀才！\\n一起游泳嘛！\\n快嘛，牽起手♪', 0);
insert into room_unit_comments values (3216, 110001, 2, 1, 1, 1, 1, '泳裝OK！\\n指甲也完美！\\n從早開始玩吧♪', 0);
insert into room_unit_comments values (3217, 110001, 2, 2, 1, 2, 1, '吃完午餐後\\n睡午覺，\\n讓自己煥然一新吧♪', 0);
insert into room_unit_comments values (3218, 110001, 2, 3, 1, 3, 1, '泳裝果然最棒……\\n還可以直接\\n穿去泡澡♪', 0);
insert into room_unit_comments values (3220, 110001, 3, 2, 1, 0, 1, '真是太舒服了♪\\n這個真的是\\n超級療癒的～！', 0);
insert into room_unit_comments values (3221, 110001, 3, 3, 1, 0, 1, '啊啊啊啊！\\n太可愛了啦！\\n真的太喜歡了啦～♪', 0);
insert into room_unit_comments values (3222, 110001, 3, 4, 1, 0, 1, '啊，我超愛這個香味！\\n好想分享給\\n模特兒的同伴們～♪', 0);
insert into room_unit_comments values (3223, 110001, 3, 5, 1, 0, 1, '真溫～暖♪\\n彷彿就像夏天的太陽！', 0);
insert into room_unit_comments values (3225, 110001, 3, 7, 1, 0, 1, '這真的超好吃的♪\\n搞不好會在\\n海灘上流行起來！', 0);
insert into room_unit_comments values (3226, 110001, 3, 8, 1, 0, 1, '為了迎接夏天，\\n來研究一下\\n最新的時尚趨勢吧！', 0);
insert into room_unit_comments values (3228, 110001, 3, 10, 1, 0, 1, '這是什麼超可愛的～♪\\n是什麼生物？\\n秀才知道是什麼嗎？', 0);
insert into room_unit_comments values (3229, 110001, 3, 11, 1, 0, 1, '哇超級鬆軟～\\n總覺得人家\\n開始想睡了……', 0);
insert into room_unit_comments values (3230, 110101, 1, 1, 1, 0, 1, '一起去海之家\\n悠閒地度過吧。\\n但要小心中暑喔', 0);
insert into room_unit_comments values (3231, 110101, 1, 2, 1, 0, 1, '嗯～該怎麼樣\\n才能學會\\n游泳呢～', 0);
insert into room_unit_comments values (3232, 110101, 1, 3, 1, 0, 1, '在海邊\\n如果沒有塗防曬的話\\n下場會很慘喔', 0);
insert into room_unit_comments values (3233, 110101, 1, 4, 1, 0, 1, '久違的穿了泳裝\\n但是會不會\\n看起來怪怪的……', 0);
insert into room_unit_comments values (3234, 110101, 1, 5, 1, 0, 1, '海浪聲真令人安心，\\n如果在上課時聽到，\\n搞不好大家都會睡著？', 0);
insert into room_unit_comments values (3235, 110101, 1, 101, 4, 0, 1, '會覺得海邊很棒，\\n也是托了\\n那孩子的福', 0);
insert into room_unit_comments values (3236, 110101, 1, 102, 8, 0, 1, '能和那孩子單獨\\n來到海邊真是太棒了，\\n一天過得還真快呢', 0);
insert into room_unit_comments values (3237, 110101, 2, 1, 1, 0, 1, '和男生一起去海邊，\\n真像是少女漫畫呢，\\n我可是一直嚮往著呢～', 0);
insert into room_unit_comments values (3238, 110101, 2, 2, 1, 0, 1, '暑假作業有\\n好好做完嗎？\\n不可以光顧著玩喔', 0);
insert into room_unit_comments values (3239, 110101, 2, 3, 1, 0, 1, '你比較喜歡白天還是\\n晚上的海呢？\u3000我喜歡\\n和你在一起的海邊喔', 0);
insert into room_unit_comments values (3240, 110101, 2, 4, 1, 0, 1, '呼，好累呀～\\n用冰涼的果汁來\\n恢復精神吧！', 0);
insert into room_unit_comments values (3241, 110101, 2, 101, 4, 0, 1, '在海邊時你的背影，\\n看起來非常的寬闊唷，\\n不愧是男生', 0);
insert into room_unit_comments values (3242, 110101, 2, 102, 8, 0, 1, '和你一起看的\\n夜晚的海邊……\\n應該永遠忘不了呢', 0);
insert into room_unit_comments values (3243, 110101, 2, 1, 1, 1, 1, '準備好的話，\\n就出發去海邊吧，\\n今天也要玩得開心喔', 0);
insert into room_unit_comments values (3244, 110101, 2, 2, 1, 2, 1, '啊，已經中午了嗎？\\n一起去海之家\\n吃午餐吧♪', 0);
insert into room_unit_comments values (3245, 110101, 2, 3, 1, 3, 1, '你願意陪我去夜晚的\\n海邊嗎？\u3000因為很暗，\\n一個人會有點怕……', 0);
insert into room_unit_comments values (3247, 110101, 3, 2, 1, 0, 1, '練習完游泳後，\\n還真累啊～\\n稍微休息一下……', 0);
insert into room_unit_comments values (3248, 110101, 3, 3, 1, 0, 1, '真可愛～\\n真想坐在充氣泳圈上，\\n浮在海裡喔～', 0);
insert into room_unit_comments values (3249, 110101, 3, 4, 1, 0, 1, '這味道……\\n總讓人想起\\n夏天的海邊呢～', 0);
insert into room_unit_comments values (3250, 110101, 3, 5, 1, 0, 1, '為了避免夜晚的海風\\n讓人著涼，\\n得先暖好身子才是', 0);
insert into room_unit_comments values (3252, 110101, 3, 7, 1, 0, 1, '去海邊遊玩前，\\n先好好吃飯，\\n儲備體力吧', 0);
insert into room_unit_comments values (3253, 110101, 3, 8, 1, 0, 1, '即使是暑假，\\n也得好好的\\n讀書才是！', 0);
insert into room_unit_comments values (3255, 110101, 3, 10, 1, 0, 1, '只要乘上去，\\n就連在海裡，\\n也能前進嗎', 0);
insert into room_unit_comments values (3256, 110101, 3, 11, 1, 0, 1, '維納斯的誕生！\\n……開玩笑的\\n好像興奮過頭了？', 0);
insert into room_unit_comments values (3257, 110301, 1, 1, 1, 0, 1, '夏天的海邊\\n一定有商機……\\n可不能錯過了♪', 0);
insert into room_unit_comments values (3258, 110301, 1, 2, 1, 0, 1, '這是我做的\\n防曬乳……\\n看來會大賣呢！', 0);
insert into room_unit_comments values (3259, 110301, 1, 3, 1, 0, 1, '為了孩子們，\\n得準備\\n充氣泳圈才行……', 0);
insert into room_unit_comments values (3260, 110301, 1, 4, 1, 0, 1, '海邊真棒呢！\\n真希望大家能一起\\n去放風一下！', 0);
insert into room_unit_comments values (3261, 110301, 1, 5, 1, 0, 1, '聽著海浪的聲音，\\n悠悠哉哉的\\n也不錯呢……', 0);
insert into room_unit_comments values (3262, 110301, 1, 101, 4, 0, 1, '只不過是玩沙而已，\\n和你在一起就好開心，\\n覺得真不可思議呢……', 0);
insert into room_unit_comments values (3263, 110301, 1, 102, 8, 0, 1, '你是我的\\n該怎麼說呢、\\n非常重要的存在呢……', 0);
insert into room_unit_comments values (3264, 110301, 2, 1, 1, 0, 1, '夏天的海邊\\n還真多人呢……\\n別迷路唷？', 0);
insert into room_unit_comments values (3265, 110301, 2, 2, 1, 0, 1, '輕鬆的浮在海面上，\\n隨著海浪搖擺，\\n真不錯呢？', 0);
insert into room_unit_comments values (3266, 110301, 2, 3, 1, 0, 1, '真想到沙灘上\\n和孩子們\\n一起堆沙堡呢♪', 0);
insert into room_unit_comments values (3267, 110301, 2, 4, 1, 0, 1, '在海邊吃的\\n炒麵麵包……\\n感覺真不錯呢', 0);
insert into room_unit_comments values (3268, 110301, 2, 101, 4, 0, 1, '我的泳裝\\n還可以嗎？\\n是嗎？謝謝♪', 0);
insert into room_unit_comments values (3269, 110301, 2, 102, 8, 0, 1, '只要你在，就會很開心\\n……呃、那是孩子們\\n說的唷！', 0);
insert into room_unit_comments values (3270, 110301, 2, 1, 1, 1, 1, '早晨的沙灘，\\n真不錯呢，\\n真想去散散步', 0);
insert into room_unit_comments values (3271, 110301, 2, 2, 1, 2, 1, '海之家的料理，\\n怎麼會\\n那麼好吃呢♪', 0);
insert into room_unit_comments values (3272, 110301, 2, 3, 1, 3, 1, '夜晚的海邊，\\n還真浪漫呢。\\n希望能一直看下去……', 0);
insert into room_unit_comments values (3274, 110301, 3, 2, 1, 0, 1, '又軟\\n又舒服……\\n好像要睡著了', 0);
insert into room_unit_comments values (3275, 110301, 3, 3, 1, 0, 1, '有沒有能夠\\n帶下海的\\n布偶之類的東西？', 0);
insert into room_unit_comments values (3276, 110301, 3, 4, 1, 0, 1, '好夏天的香味喔……\\n這是什麼味道呢？', 0);
insert into room_unit_comments values (3277, 110301, 3, 5, 1, 0, 1, '真暖和……\\n正好適合\\n曬乾身體呢♪', 0);
insert into room_unit_comments values (3279, 110301, 3, 7, 1, 0, 1, '游完泳後的食物，\\n總是比平常\\n好吃好幾倍呢！', 0);
insert into room_unit_comments values (3280, 110301, 3, 8, 1, 0, 1, '去海邊之前\\n得查很多資料。\\n有備無患嘛！', 0);
insert into room_unit_comments values (3282, 110301, 3, 10, 1, 0, 1, '海的守護神？\\n感覺是很可愛的\\n神明呢♪', 0);
insert into room_unit_comments values (3283, 110301, 3, 11, 1, 0, 1, '如果拿到\\n這麼大顆的貝的話，\\n應該可以省點飯錢吧？', 0);
insert into room_unit_comments values (3284, 110401, 1, 1, 1, 0, 1, '為什麼在海邊吃的食物，\\n總能那麼\\n好吃呢～', 0);
insert into room_unit_comments values (3285, 110401, 1, 2, 1, 0, 1, '好痛……\\n好像有點\\n曬過頭了呢', 0);
insert into room_unit_comments values (3286, 110401, 1, 3, 1, 0, 1, '在海邊跑步\\n好像是個很不錯的\\n訓練呢', 0);
insert into room_unit_comments values (3287, 110401, 1, 4, 1, 0, 1, '夏天\\n必吃的料理嗎……\\n是哪道呢……', 0);
insert into room_unit_comments values (3288, 110401, 1, 5, 1, 0, 1, '這裡果然\\n涼快又舒適', 0);
insert into room_unit_comments values (3289, 110401, 1, 101, 4, 0, 1, '如果想練習游泳，\\n我隨時\\n奉陪呦！', 0);
insert into room_unit_comments values (3290, 110401, 1, 102, 8, 0, 1, '在房間\\n穿泳衣，\\n感覺真怪呢……', 0);
insert into room_unit_comments values (3291, 110401, 2, 1, 1, 0, 1, '哦！\\n不、不要\\n突然碰我啦……\\n嚇我一跳', 0);
insert into room_unit_comments values (3292, 110401, 2, 2, 1, 0, 1, '咦、你說我沾到沙了？\\n幫我拍掉了嗎？\\n謝謝你', 0);
insert into room_unit_comments values (3293, 110401, 2, 3, 1, 0, 1, '是不是中暑了？\\n在這裡休息一下嘛', 0);
insert into room_unit_comments values (3294, 110401, 2, 4, 1, 0, 1, '……被這樣一看，\\n該說是害羞嗎……', 0);
insert into room_unit_comments values (3295, 110401, 2, 101, 4, 0, 1, '想不想吃些\\n剉冰之類的食物？\\n我可以幫你準備冰呦', 0);
insert into room_unit_comments values (3296, 110401, 2, 102, 8, 0, 1, '可以和你\\n留下夏天的回憶，\\n真是太棒了', 0);
insert into room_unit_comments values (3297, 110401, 2, 1, 1, 1, 1, '呼……\\n一大早起床游泳，\\n真舒服呢', 0);
insert into room_unit_comments values (3298, 110401, 2, 2, 1, 2, 1, '一天之中最熱的時刻，\\n一定要\\n時常補充水分喔！', 0);
insert into room_unit_comments values (3299, 110401, 2, 3, 1, 3, 1, '今天也辛苦你了，\\n明天的巡邏\\n也拜託你囉', 0);
insert into room_unit_comments values (3301, 110401, 3, 2, 1, 0, 1, '因為有點游累了，\\n稍微休息一下……', 0);
insert into room_unit_comments values (3302, 110401, 3, 3, 1, 0, 1, '好、好可愛……\\n不、沒事啦！', 0);
insert into room_unit_comments values (3303, 110401, 3, 4, 1, 0, 1, '這個香味真令人安心，\\n這種感覺\\n也不錯呢……', 0);
insert into room_unit_comments values (3304, 110401, 3, 5, 1, 0, 1, '好溫暖……！\\n游完泳後，\\n身體涼了不少……', 0);
insert into room_unit_comments values (3306, 110401, 3, 7, 1, 0, 1, '在海邊吃\\n別有一番風味呢', 0);
insert into room_unit_comments values (3307, 110401, 3, 8, 1, 0, 1, '夏日生存特集……\\n來看一下好了', 0);
insert into room_unit_comments values (3309, 110401, 3, 10, 1, 0, 1, '咦！\u3000這傢伙，\\n做得不錯嘛！\\n真好，若真品可以\\n乘坐就好了', 0);
insert into room_unit_comments values (3310, 110401, 3, 11, 1, 0, 1, '好、好可愛…！\\n不、不是，\\n我、我可以\\n睡在這裡嗎……？', 0);
insert into room_unit_comments values (3311, 110501, 1, 1, 1, 0, 1, '可以用故鄉的花朵\\n來裝飾嗎～？\\n房間會變得更有意思喔～', 0);
insert into room_unit_comments values (3312, 110501, 1, 2, 1, 0, 1, '只要穿著泳裝走路，\\n就會想游泳呢～\\n真令人按耐不住～！', 0);
insert into room_unit_comments values (3313, 110501, 1, 3, 1, 0, 1, '夏日會讓人眩目\\n的原因在於，每個人都\\n笑的很燦爛～！', 0);
insert into room_unit_comments values (3314, 110501, 1, 4, 1, 0, 1, '游累了就在\\n涼快的樹蔭下\\n睡午覺～', 0);
insert into room_unit_comments values (3315, 110501, 1, 5, 1, 0, 1, '無論是自由式\\n還是狗爬式，\\n我都非常拿手～！', 0);
insert into room_unit_comments values (3316, 110501, 1, 101, 4, 0, 1, '總覺得\\n好想唱歌～\\n要不要跟我一起～？', 0);
insert into room_unit_comments values (3317, 110501, 1, 102, 8, 0, 1, '住在離海很近的地方，\\n和你一起唱歌跳舞，\\n就是我的理想～！', 0);
insert into room_unit_comments values (3318, 110501, 2, 1, 1, 0, 1, '我們來比\\n我們之中\\n誰比較能游～！', 0);
insert into room_unit_comments values (3319, 110501, 2, 2, 1, 0, 1, '你也對三線琴\\n有興趣嗎～？\\n我可以教你喔～！', 0);
insert into room_unit_comments values (3320, 110501, 2, 3, 1, 0, 1, '即使有不開心的事，\\n只要和我一起唱歌跳舞\\n就船到橋頭自然直～！', 0);
insert into room_unit_comments values (3321, 110501, 2, 4, 1, 0, 1, '偶爾忘卻時間，\\n和我一起\\n悠閒看海嘛～', 0);
insert into room_unit_comments values (3322, 110501, 2, 101, 4, 0, 1, '咕嚕咕嚕咕嚕！\\n毛髮弄濕了\\n也沒關係啦～！', 0);
insert into room_unit_comments values (3323, 110501, 2, 102, 8, 0, 1, '游泳前得做熱身操～！\\n我會幫你的，\\n所以要好好做喔～！', 0);
insert into room_unit_comments values (3324, 110501, 2, 1, 1, 1, 1, '早晨的海邊\\n真美啊～\\n一起散散步嘛！', 0);
insert into room_unit_comments values (3325, 110501, 2, 2, 1, 2, 1, '決定\\n午餐要吃什麼了嗎～？\\n我要吃塔可飯～！', 0);
insert into room_unit_comments values (3326, 110501, 2, 3, 1, 3, 1, '在星星及鼓音之中\\n彈奏三線，\\n也很有情調～', 0);
insert into room_unit_comments values (3328, 110501, 3, 2, 1, 0, 1, '反正時機剛好，\\n先好好休息之後，\\n再去游泳吧～！', 0);
insert into room_unit_comments values (3329, 110501, 3, 3, 1, 0, 1, '我想拿來\\n裝飾海灘，～\\n一定會很可愛的～！', 0);
insert into room_unit_comments values (3330, 110501, 3, 4, 1, 0, 1, '嗅嗅……哇\\n真療癒……\\n是故鄉夏天的味道～', 0);
insert into room_unit_comments values (3331, 110501, 3, 5, 1, 0, 1, '哦……！\\n濕掉的頭髮\\n漸漸乾了～', 0);
insert into room_unit_comments values (3333, 110501, 3, 7, 1, 0, 1, '嗯～！\u3000這個\\n如果在海邊吃\\n一定會更好吃的說～', 0);
insert into room_unit_comments values (3334, 110501, 3, 8, 1, 0, 1, '海洋生物圖鑑上，\\n也記載著\\n我們上次看到的魚喔～？', 0);
insert into room_unit_comments values (3336, 110501, 3, 10, 1, 0, 1, '哇～！\u3000快看！\\n可以騎在牠背上！\\n真想和你\\n一起騎～！', 0);
insert into room_unit_comments values (3337, 110501, 3, 11, 1, 0, 1, '浮浮沉沉～\\n彷彿變成了海底的\\n人魚一般～', 0);
insert into room_unit_comments values (3338, 105801, 1, 103, 12, 0, 1, '如果那個人\\n是我的騎士\\n就好了……', 0);
insert into room_unit_comments values (3339, 105801, 2, 103, 12, 0, 1, '多虧了你，\\n讓我的心\\n總是好滿足♪', 0);
insert into room_unit_comments values (3340, 105901, 1, 103, 12, 0, 1, '如果可以\\n我現在就想\\n趕到主人的\\n身邊……', 0);
insert into room_unit_comments values (3341, 105901, 2, 103, 12, 0, 1, '主人……\\n我好仰慕你……\\n請讓我\\n一直待在你身邊……', 0);
insert into room_unit_comments values (3342, 106001, 1, 103, 12, 0, 1, '最近好像\\n滿腦子都想著\\n那傢伙的事……\\n我……該如何是好……', 0);
insert into room_unit_comments values (3343, 106001, 2, 103, 12, 0, 1, '如、如果你要出門的話，\\n請帶上我吧\\n那個……因為我\\n會守護你的……', 0);
insert into room_unit_comments values (3344, 110601, 1, 1, 1, 0, 1, '房間裡好涼爽，\\n真舒服啊，\\n根本是天國啊', 0);
insert into room_unit_comments values (3345, 110601, 1, 2, 1, 0, 1, '水母啊\\n長得真像\\n有著荷葉邊的裙子', 0);
insert into room_unit_comments values (3346, 110601, 1, 3, 1, 0, 1, '蟬在外面\\n有精神的鳴叫著呢，\\n今天也會是好天氣呢', 0);
insert into room_unit_comments values (3347, 110601, 1, 4, 1, 0, 1, '雖然室內很熱，\\n但一進海裡，\\n就會非常涼快呢', 0);
insert into room_unit_comments values (3348, 110601, 1, 5, 1, 0, 1, '之後也在\\n真步真步王國\\n打造海之家吧♪', 0);
insert into room_unit_comments values (3349, 110601, 1, 101, 4, 0, 1, '人家\\n也陪你去吧，\\n一起練習游泳吧♪', 0);
insert into room_unit_comments values (3350, 110601, 1, 102, 8, 0, 1, '等我回故鄉時，\\n王子先生\\n也一起來吧？', 0);
insert into room_unit_comments values (3351, 110601, 2, 1, 1, 0, 1, '比起曬得黑黑的樣子，\\n更喜歡\\n皮膚白皙的美女吧？', 0);
insert into room_unit_comments values (3352, 110601, 2, 2, 1, 0, 1, '要不要和人家一起\\n用泳圈\\n在海中漂浮呢？', 0);
insert into room_unit_comments values (3353, 110601, 2, 3, 1, 0, 1, '等天色暗下，\\n我想要玩\\n仙女棒♪', 0);
insert into room_unit_comments values (3354, 110601, 2, 4, 1, 0, 1, '在樹蔭下悠閒的\\n睡午覺……\\n真是太棒了～', 0);
insert into room_unit_comments values (3355, 110601, 2, 101, 4, 0, 1, '就算在祭典中走散了，\\n王子先生\\n應該也會找到我？', 0);
insert into room_unit_comments values (3356, 110601, 2, 102, 8, 0, 1, '真是開心的夏天，\\n希望可以\\n一直繼續下去呢', 0);
insert into room_unit_comments values (3357, 110601, 2, 1, 1, 1, 1, '對人家來說算是早起的？\\n因為天氣太熱了，\\n很早就醒了', 0);
insert into room_unit_comments values (3358, 110601, 2, 2, 1, 2, 1, '中午就吃\\n冰涼的素麵\\n如何', 0);
insert into room_unit_comments values (3359, 110601, 2, 3, 1, 3, 1, '夜晚中的星星還真美呢，\\n要不要和我一起\\n去海邊走走呢？', 0);
insert into room_unit_comments values (3361, 110601, 3, 2, 1, 0, 1, '嗯～對玩累的\\n身體來說非常剛好，\\n真舒暢呀', 0);
insert into room_unit_comments values (3362, 110601, 3, 3, 1, 0, 1, '每個可愛的人\\n全部都是\\n真步真步王國的\\n住民呦♪', 0);
insert into room_unit_comments values (3363, 110601, 3, 4, 1, 0, 1, '這香味……\\n彷彿身處南國一般，\\n真療癒～', 0);
insert into room_unit_comments values (3364, 110601, 3, 5, 1, 0, 1, '如此一來，\\n寒冷的身體也能\\n變溫暖呢', 0);
insert into room_unit_comments values (3366, 110601, 3, 7, 1, 0, 1, '外面看起來還很熱，\\n為了不要中暑，\\n得吃多點才行', 0);
insert into room_unit_comments values (3367, 110601, 3, 8, 1, 0, 1, '海裡\\n也有城堡呢，\\n真想去看看呢', 0);
insert into room_unit_comments values (3369, 110601, 3, 10, 1, 0, 1, '這孩子軟呼呼的，\\n就騎著去\\n真步真步王國怎麼樣？', 0);
insert into room_unit_comments values (3370, 110601, 3, 11, 1, 0, 1, '即使成為人魚公主，\\n最後也能和王子先生\\n有著\\n幸福快樂的結局～', 0);
insert into room_unit_comments values (3371, 107001, 1, 1, 1, 0, 1, '這裡有好多人\\n讓我想\\n模仿', 0);
insert into room_unit_comments values (3372, 107001, 1, 2, 1, 0, 1, '混入了\\n一個冒牌貨\\n……開玩笑的', 0);
insert into room_unit_comments values (3373, 107001, 1, 3, 1, 0, 1, '變身為動物\\n去那邊\\n散個步', 0);
insert into room_unit_comments values (3374, 107001, 1, 4, 1, 0, 1, '失去的記憶\\n恢復到\\n什麼程度了呢', 0);
insert into room_unit_comments values (3375, 107001, 1, 5, 1, 0, 1, '不只是人與物，\\n總有一天\\n我能模仿世界給你看', 0);
insert into room_unit_comments values (3376, 107001, 1, 101, 4, 0, 1, '接下來該教他\\n什麼事\\n好呢……', 0);
insert into room_unit_comments values (3377, 107001, 1, 102, 8, 0, 1, '不用說知識，\\n也還需要\\n我的力量吧', 0);
insert into room_unit_comments values (3378, 107001, 2, 1, 1, 0, 1, '怎麼了？\\n……沒什麼？\\n……這樣啊', 0);
insert into room_unit_comments values (3379, 107001, 2, 2, 1, 0, 1, '我覺得好無聊，\\n你表演個什麼\\n來逗樂我吧', 0);
insert into room_unit_comments values (3380, 107001, 2, 3, 1, 0, 1, '這可是為了研究，\\n陪我\\n說說話', 0);
insert into room_unit_comments values (3381, 107001, 2, 4, 1, 0, 1, '你想不想讓我來\\n模仿點什麼？你以為\\n我就這麼點實力？', 0);
insert into room_unit_comments values (3382, 107001, 2, 101, 4, 0, 1, '看來\\n得教你的\\n還多著呢', 0);
insert into room_unit_comments values (3383, 107001, 2, 102, 8, 0, 1, '你認識\\n很多女生呢，\\n嚇我一大跳', 0);
insert into room_unit_comments values (3384, 107001, 2, 1, 1, 1, 1, '……\\n早安\\n……還在早上呦', 0);
insert into room_unit_comments values (3385, 107001, 2, 2, 1, 2, 1, '我要吃飯\\n……怎麼了？\\n趕快拿來', 0);
insert into room_unit_comments values (3386, 107001, 2, 3, 1, 3, 1, '快點睡\\n如果熬夜\\n會長不高呦？', 0);
insert into room_unit_comments values (3388, 107001, 3, 2, 1, 0, 1, '比真軌\\n要來的\\n好坐吧', 0);
insert into room_unit_comments values (3389, 107001, 3, 3, 1, 0, 1, '構造意外的\\n很複雜呢', 0);
insert into room_unit_comments values (3390, 107001, 3, 4, 1, 0, 1, '如果要模仿，\\n就得連香味\\n都重現才行', 0);
insert into room_unit_comments values (3391, 107001, 3, 5, 1, 0, 1, '燃燒現象\\n很難\\n模仿呢', 0);
insert into room_unit_comments values (3393, 107001, 3, 7, 1, 0, 1, '要製造冒牌貨和分身\\n能量是\\n不可或缺的', 0);
insert into room_unit_comments values (3394, 107001, 3, 8, 1, 0, 1, '剛好適合用來\\n打發時間\\n……', 0);
insert into room_unit_comments values (3396, 110701, 1, 1, 1, 0, 1, '學院啊…\\n不知道有沒有\\n教人交友的課程呢？', 0);
insert into room_unit_comments values (3397, 110701, 1, 2, 1, 0, 1, '得找找\\n在學校\\n落單時的\\n避難場所了…', 0);
insert into room_unit_comments values (3398, 110701, 1, 3, 1, 0, 1, '邊緣人的我去學院…\\n總、總覺得\\n頭暈了起來', 0);
insert into room_unit_comments values (3399, 110701, 1, 4, 1, 0, 1, '和同學\\n搭話的練習…\\n太、太難了…！', 0);
insert into room_unit_comments values (3400, 110701, 1, 5, 1, 0, 1, '目標朋友100人…\\n太勉強了…10人…\\n還、還是一個人就好！', 0);
insert into room_unit_comments values (3401, 110701, 1, 101, 4, 0, 1, '可以和你共度\\n學院生活，\\n真的感到\\n非常開心！', 0);
insert into room_unit_comments values (3402, 110701, 1, 102, 8, 0, 1, '欸嘿嘿…\\n即使是學院，\\n只要能和你在一起，\\n就很開心了♪', 0);
insert into room_unit_comments values (3403, 110701, 2, 1, 1, 0, 1, '但是，學院原來\\n有那麼多人呢…\\n得重新在腦中\\n模擬練習才行！', 0);
insert into room_unit_comments values (3404, 110701, 2, 2, 1, 0, 1, '對我來說\\n社團活動就是BB團！\\n今天也多多進行\\n活動吧！', 0);
insert into room_unit_comments values (3405, 110701, 2, 3, 1, 0, 1, '休息時間來裝睡…\\n午飯就在廁所吃…\\n完美的邊緣人策略', 0);
insert into room_unit_comments values (3406, 110701, 2, 4, 1, 0, 1, '在學院練習交友\\n成果很完美喔！！\\n在販賣部買麵包，\\n然後交給他對吧！', 0);
insert into room_unit_comments values (3407, 110701, 2, 101, 4, 0, 1, '在學院裡\\n也持續進行\\nBB團的活動吧♪', 0);
insert into room_unit_comments values (3408, 110701, 2, 102, 8, 0, 1, '對邊緣人來說學院生活\\n很辛苦呢…但是只要\\n和你在一起，就好像\\n可以越過難關一般', 0);
insert into room_unit_comments values (3409, 110701, 2, 1, 1, 1, 1, '已經這麼晚了…\\n那個…能不能陪我\\n一起去學院呢？', 0);
insert into room_unit_comments values (3410, 110701, 2, 2, 1, 2, 1, '午餐時間到了！\\n只要你說一聲，\\n不管是麵包還是飯糰，\\n我都可以買回來喔！', 0);
insert into room_unit_comments values (3411, 110701, 2, 3, 1, 3, 1, '今天也是孤單一人…\\n希望有天\\n能和朋友一起\\n舉行睡衣派對呢…', 0);
insert into room_unit_comments values (3413, 110701, 3, 2, 1, 0, 1, '和森林裡不同\\n好舒服…好像\\n快睡著了…', 0);
insert into room_unit_comments values (3414, 110701, 3, 3, 1, 0, 1, '好可愛…\\n就把這孩子當作\\n說話練習的對象吧？', 0);
insert into room_unit_comments values (3415, 110701, 3, 4, 1, 0, 1, '好香…\\n為了迎接交到朋友\\n的那天，我的房間\\n也放一個好了', 0);
insert into room_unit_comments values (3416, 110701, 3, 5, 1, 0, 1, '這、這個…\\n對我這種邊緣\\n又見不得人的人來說\\n實在太溫暖了！', 0);
insert into room_unit_comments values (3418, 110701, 3, 7, 1, 0, 1, '好好吃…！\\n可不可以當作便當，\\n帶去學院吃呢？', 0);
insert into room_unit_comments values (3419, 110701, 3, 8, 1, 0, 1, '為了跟上\\n學校的課程，\\n來讀一下書吧…', 0);
insert into room_unit_comments values (3421, 110801, 1, 1, 1, 0, 1, '你在做什麼。\\n算了，沒事', 0);
insert into room_unit_comments values (3422, 110801, 1, 2, 1, 0, 1, '擋路了。讓開。\\n我沒在生氣啊。\\n這很正常吧', 0);
insert into room_unit_comments values (3423, 110801, 1, 3, 1, 0, 1, '算了，但只要\\n看到髒亂的房間，就會\\n想要收拾一番呢…', 0);
insert into room_unit_comments values (3424, 110801, 1, 4, 1, 0, 1, '看起來雖然很閒，其實\\n是在想明天的穿搭喔。\\n反正想想不用錢嘛', 0);
