insert into room_unit_comments values (3493, 111201, 3, 5, 1, 0, 1, '如果把得到的點心\\n拿去加熱\\n會好吃嗎～？', 0);
insert into room_unit_comments values (3495, 111201, 3, 7, 1, 0, 1, '光吃點心\\n也會膩吧！\\n也吃點正餐吧！', 0);
insert into room_unit_comments values (3496, 111201, 3, 8, 1, 0, 1, '不知道書裡面\\n有沒有藏著\\n惡作劇的靈感呢～？', 0);
