insert into room_unit_comments values (4280, 113501, 2, 3, 1, 3, 1, '得去休息才行了，\\n讓老師來唱\\n搖籃曲給你聽吧？', 0);
insert into room_unit_comments values (4282, 113501, 3, 2, 1, 0, 1, '呵呵，真舒服，\\n老師就稍微\\n休息一下好了。', 0);
insert into room_unit_comments values (4283, 113501, 3, 3, 1, 0, 1, '哎呀真可愛♪\\n希望我們隊伍\\n也有這種吉祥物。', 0);
insert into room_unit_comments values (4284, 113501, 3, 4, 1, 0, 1, '哎呀，是海洋的香味？\\n好像甚至聽得到\\n海浪拍打的聲音呢。', 0);
insert into room_unit_comments values (4285, 113501, 3, 5, 1, 0, 1, '呵呵呵，\\n用這個的話就能\\n輕鬆烘乾濕掉的泳衣♪', 0);
insert into room_unit_comments values (4287, 113501, 3, 7, 1, 0, 1, '這個真好吃♪\\n但是可不行\\n吃太多唷。', 0);
insert into room_unit_comments values (4288, 113501, 3, 8, 1, 0, 1, '老師每天也在學習喔，\\n吸收新知識是一件\\n非常快樂的事♪', 0);
insert into room_unit_comments values (4290, 113501, 3, 10, 1, 0, 1, '哎呀，真開心呢，\\n大家一定也\\n玩得很盡興呢。', 0);
insert into room_unit_comments values (4291, 113501, 3, 11, 1, 0, 1, '呵呵，這樣就能\\n好好享受\\n夏天的氛圍了♪', 0);
insert into room_unit_comments values (4292, 102301, 1, 103, 12, 0, 1, '三明治\\n也做好了，\\n已經完美做好\\n迎接哥哥的準備了♪', 0);
insert into room_unit_comments values (4293, 102301, 2, 103, 12, 0, 1, '欸嘿嘿，\\n總覺得只要有\\n哥哥和噗吉在身邊，\\n就會好開心♪', 0);
insert into room_unit_comments values (4294, 113601, 1, 1, 1, 0, 1, '有點曬紅了呢，\\n上次曬傷不知道\\n是什麼時候的事了。', 0);
insert into room_unit_comments values (4295, 113601, 1, 2, 1, 0, 1, '要穿這樣\\n可是需要\\n一些勇氣呢……', 0);
insert into room_unit_comments values (4296, 113601, 1, 3, 1, 0, 1, '雖然能承受炎熱的溫度\\n不過倒是還沒習慣肌膚\\n暴露在外的感覺……', 0);
insert into room_unit_comments values (4297, 113601, 1, 4, 1, 0, 1, '就算是這身打扮，\\n只要表現得光明正大\\n就不會覺得害羞了……\\n是嗎？', 0);
insert into room_unit_comments values (4298, 113601, 1, 5, 1, 0, 1, '哎呀……可能因為這樣\\n和穿著鎧甲時重心不同\\n所以感覺有點不穩呢。', 0);
insert into room_unit_comments values (4299, 113601, 1, 101, 4, 0, 1, '這身泳衣……\\n有適合\\n我嗎……？\\n還真難開口問少年呢。', 0);
insert into room_unit_comments values (4300, 113601, 1, 102, 8, 0, 1, '好想再和少年\\n一起去……\\n海邊\\n度假呢。', 0);
insert into room_unit_comments values (4301, 113601, 2, 1, 1, 0, 1, '拜託不要一直\\n盯著我看……\\n這不是\\n超害羞的嗎……', 0);
insert into room_unit_comments values (4302, 113601, 2, 2, 1, 0, 1, '怎麼了？\\n若不介意的話，\\n讓我來\\n聽你說說吧。', 0);
insert into room_unit_comments values (4303, 113601, 2, 3, 1, 0, 1, '啊！\\n抱、抱歉\\n我好像有點\\n過敏。', 0);
insert into room_unit_comments values (4304, 113601, 2, 4, 1, 0, 1, '……哈啾！\\n抱歉，\\n我不太習慣\\n穿得那麼少呢。', 0);
insert into room_unit_comments values (4305, 113601, 2, 101, 4, 0, 1, '嘛、不過若是少年的話\\n就算被看到也……\\n啊、沒事！\\n當我沒說！', 0);
insert into room_unit_comments values (4306, 113601, 2, 102, 8, 0, 1, '我能像這樣\\n平心靜氣，\\n正是因為\\n有少年在我身邊喔。', 0);
insert into room_unit_comments values (4307, 113601, 2, 1, 1, 1, 1, '這個時間\\n應該沒什麼人\\n在海灘上吧……\\n要去玩嗎？', 0);
insert into room_unit_comments values (4308, 113601, 2, 2, 1, 2, 1, '你約我吃午餐嗎？\\n好啊，一起去吧。', 0);
insert into room_unit_comments values (4309, 113601, 2, 3, 1, 3, 1, '已經晚上了啊……\\n一天\\n一下子就過去了呢。', 0);
insert into room_unit_comments values (4311, 113601, 3, 2, 1, 0, 1, '現在這樣很輕鬆呢……\\n之前總是穿著鎧甲，\\n為了不弄壞家具\\n只能小心翼翼地使用。', 0);
insert into room_unit_comments values (4312, 113601, 3, 3, 1, 0, 1, '嗯……\\n能感受到\\n這柔軟的觸感……', 0);
insert into room_unit_comments values (4313, 113601, 3, 4, 1, 0, 1, '平時總是\\n會煩惱著\\n香味會\\n附著在鎧甲上……', 0);
insert into room_unit_comments values (4314, 113601, 3, 5, 1, 0, 1, '唔呣……\\n雖然涼快卻很暖和，\\n好奇妙的感覺。', 0);
insert into room_unit_comments values (4316, 113601, 3, 7, 1, 0, 1, '吃東西時能\\n不用在意頭盔\\n感覺真輕鬆……', 0);
insert into room_unit_comments values (4317, 113601, 3, 8, 1, 0, 1, '嗯……沒有穿盔甲\\n翻頁變真輕鬆。', 0);
insert into room_unit_comments values (4319, 113601, 3, 10, 1, 0, 1, '好輕盈好棒……\\n穿著鎧甲\\n做騎乘訓練時\\n都覺得對馬好抱歉。', 0);
insert into room_unit_comments values (4320, 113601, 3, 11, 1, 0, 1, '只要好好利用貝殼，\\n即使在水中\\n也能做好防守喔。', 0);
insert into room_unit_comments values (4321, 106801, 1, 1, 1, 0, 1, '要選哪裡\\n當作據點呢……\\n就選這裡好了♪', 0);
insert into room_unit_comments values (4322, 106801, 1, 2, 1, 0, 1, '這棟建築長得\\n還真奇怪呢。\\n應該有妖精在裡面吧？', 0);
insert into room_unit_comments values (4323, 106801, 1, 3, 1, 0, 1, '能觀察形形色色的人們\\n真是大飽眼福……♪', 0);
insert into room_unit_comments values (4324, 106801, 1, 4, 1, 0, 1, '至於改變家具配置這事\\n就交給我吧……\\n要改變家裡的樣貌嗎？', 0);
insert into room_unit_comments values (4325, 106801, 1, 5, 1, 0, 1, '如果有人對於\\n新的口味有想法的話，\\n要告訴我喔！', 0);
insert into room_unit_comments values (4326, 106801, 1, 101, 4, 0, 1, '少年會不會\\n早點回來呢……\\n好，我去迎接他吧！', 0);
insert into room_unit_comments values (4327, 106801, 1, 102, 8, 0, 1, '結果還是得\\n到處漂泊……\\n真想見見少年啊～', 0);
insert into room_unit_comments values (4328, 106801, 2, 1, 1, 0, 1, '少年，你怎麼了？\\n什麼事都能和\\n姐姐說喔～？', 0);
insert into room_unit_comments values (4329, 106801, 2, 2, 1, 0, 1, '這個就那樣弄……\\n哎呀少年！\u3000抱歉呢\\n沒立刻發現你！', 0);
insert into room_unit_comments values (4330, 106801, 2, 3, 1, 0, 1, '什麼？\u3000克莉絲\\n正在找我？\\n……你跟她說我不在！', 0);
insert into room_unit_comments values (4331, 106801, 2, 4, 1, 0, 1, '可以的話\\n讓璃乃和靜流\\n也看看你嘛。', 0);
insert into room_unit_comments values (4332, 106801, 2, 101, 4, 0, 1, '少年～！\\n我已經精疲力盡了～！\\n拜託你治癒我一下～！', 0);
insert into room_unit_comments values (4333, 106801, 2, 102, 8, 0, 1, '……能讓我敞開心胸\\n尋求幫助的\\n只有少年你唷。', 0);
insert into room_unit_comments values (4334, 106801, 2, 1, 1, 1, 1, '已經早上囉，少年！\\n清爽的早晨，\\n要不要來份可麗餅啊？', 0);
insert into room_unit_comments values (4335, 106801, 2, 2, 1, 2, 1, '肚子好餓啊～\\n午餐要不要吃點\\n可麗餅啊少年？', 0);
insert into room_unit_comments values (4336, 106801, 2, 3, 1, 3, 1, '你說不要連晚餐\\n都吃可麗餅？\u3000抱歉\\n只有這點我辦不到…！', 0);
insert into room_unit_comments values (4338, 106801, 3, 2, 1, 0, 1, '我就像這樣\\n那麼悠閒\\n沒問題嗎～？', 0);
insert into room_unit_comments values (4339, 106801, 3, 3, 1, 0, 1, '……這個\\n不是由似似花\\n變身的吧？', 0);
insert into room_unit_comments values (4340, 106801, 3, 4, 1, 0, 1, '好香喔……\\n分析完成，\\n下次試著製作看看吧♪', 0);
insert into room_unit_comments values (4341, 106801, 3, 5, 1, 0, 1, '真令人安心……\\n據點沒辦法放，\\n還真是羨慕呢。', 0);
insert into room_unit_comments values (4343, 106801, 3, 7, 1, 0, 1, '消化食物的模式\\n到底怎麼做的啊，\\n真是在意……', 0);
insert into room_unit_comments values (4344, 106801, 3, 8, 1, 0, 1, '雖然我會速讀，\\n但在讀書的時候\\n還是希望能好好\\n沉浸在文字裡面呢……', 0);
insert into room_unit_comments values (4346, 113701, 1, 1, 1, 0, 1, '身為天使，希望\\n能讓大家打起精神…！\\n茜里會加油的♪', 0);
insert into room_unit_comments values (4347, 113701, 1, 2, 1, 0, 1, '和姊姊一起\\n把哥哥……欸嘿嘿\\n哥哥還沒好嗎？', 0);
insert into room_unit_comments values (4348, 113701, 1, 3, 1, 0, 1, '以天使般的音色\\n為目標，今天也要\\n練習薩克斯風♪', 0);
insert into room_unit_comments values (4349, 113701, 1, 4, 1, 0, 1, '和哥哥的特訓\\n也得好好加油才行……\\n請對我嚴格一點唷。', 0);
insert into room_unit_comments values (4350, 113701, 1, 5, 1, 0, 1, '打扮成可愛天使的模樣\\n如果是哥哥的話\\n很應該會高興吧♪', 0);
insert into room_unit_comments values (4351, 113701, 1, 101, 4, 0, 1, '借助天使的力量，\\n哥哥的心\\n也會馬上屬於我♪', 0);
insert into room_unit_comments values (4352, 113701, 1, 102, 8, 0, 1, '茜里最希望\\n能讓哥哥\\n打起精神唷♪', 0);
insert into room_unit_comments values (4353, 113701, 2, 1, 1, 0, 1, '哥哥？\\n如果想要觸碰的話……\\n這邊怎麼樣呢♪', 0);
insert into room_unit_comments values (4354, 113701, 2, 2, 1, 0, 1, '特訓的時間到了嗎？\\n茜里可是\\n非常期待的喔♪', 0);
insert into room_unit_comments values (4355, 113701, 2, 3, 1, 0, 1, '啊，哥哥！\\n要不要和茜里\\n休息……一下呢♪', 0);
insert into room_unit_comments values (4356, 113701, 2, 4, 1, 0, 1, '這對翅膀\\n摸起來很舒服嗎？\u3000\\n啊、好癢喔！', 0);
insert into room_unit_comments values (4357, 113701, 2, 101, 4, 0, 1, '哥哥也想要\\n打起精神嗎？\\n就交給茜里吧♪', 0);
insert into room_unit_comments values (4358, 113701, 2, 102, 8, 0, 1, '哎呀♪\\n哥哥真是的\\n真大膽呢♪', 0);
insert into room_unit_comments values (4359, 113701, 2, 1, 1, 1, 1, '哥哥，早上囉，\\n天氣非常地\\n舒服喔……♪', 0);
insert into room_unit_comments values (4360, 113701, 2, 2, 1, 2, 1, '要不要睡個午覺呢，\\n哥哥？\\n枕在茜里的大腿上…♪', 0);
insert into room_unit_comments values (4361, 113701, 2, 3, 1, 3, 1, '晚上了呢，哥哥，\\n不知道為什麼\\n茜里的心臟跳得好快。', 0);
insert into room_unit_comments values (4363, 113701, 3, 2, 1, 0, 1, '好柔軟喔……\\n要是哥哥\\n在我身旁就好了……♪', 0);
insert into room_unit_comments values (4364, 113701, 3, 3, 1, 0, 1, '欸嘿嘿，茜里\\n也想要變得\\n這麼可愛呢～', 0);
insert into room_unit_comments values (4365, 113701, 3, 4, 1, 0, 1, '這是哥哥\\n喜歡的味道嗎？\\n讓我參考參考！', 0);
insert into room_unit_comments values (4366, 113701, 3, 5, 1, 0, 1, '身體感覺好熱\\n茜里覺得……\\n有點暈……', 0);
insert into room_unit_comments values (4368, 113701, 3, 7, 1, 0, 1, '啊呣、嗯……啊\\n沾到臉上了，\\n黏呼呼的～', 0);
insert into room_unit_comments values (4369, 113701, 3, 8, 1, 0, 1, '天使出現在\\n很多故事當中呢，\\n得更認真調查才行！', 0);
insert into room_unit_comments values (4371, 103201, 1, 103, 12, 0, 1, '在那位大人面前，\\n總希望能\\n保持高雅呢。', 0);
insert into room_unit_comments values (4372, 103201, 2, 103, 12, 0, 1, '我有絕對的自信，\\n就算迷失了方向\\n也絕對能夠\\n回到您身邊。', 0);
insert into room_unit_comments values (4373, 102801, 1, 103, 12, 0, 1, '和你在一起的時候\\n說不定是我\\n最快樂的時光呢。', 0);
insert into room_unit_comments values (4374, 102801, 2, 103, 12, 0, 1, '希望有一天能和你一起\\n打理咲戀育幼院\\n……開玩笑的♪', 0);
insert into room_unit_comments values (4375, 113801, 1, 1, 1, 0, 1, '身為天使，\\n今天也要\\n為了大家的幸福\\n而努力才行。', 0);
insert into room_unit_comments values (4376, 113801, 1, 2, 1, 0, 1, '難得長了翅膀\\n卻不能飛行，\\n好可惜喔。', 0);
insert into room_unit_comments values (4377, 113801, 1, 3, 1, 0, 1, '這身打扮莫名地\\n很受小孩子的歡迎，\\n嘛、這也不賴就是了。', 0);
insert into room_unit_comments values (4378, 113801, 1, 4, 1, 0, 1, '果、果然\\n受到了大家的矚目……\\n不過因為天使很少見，\\n好像也是沒辦法的事。', 0);
insert into room_unit_comments values (4379, 113801, 1, 5, 1, 0, 1, '成為天使之後\\n就不會那麼怕生了！\\n……不可能的嗎。', 0);
insert into room_unit_comments values (4380, 113801, 1, 101, 4, 0, 1, '雖然天使\\n要做的事情很辛苦，\\n但是感受到自己的成長\\n還是很令人開心呢。', 0);
insert into room_unit_comments values (4381, 113801, 1, 102, 8, 0, 1, '要是能讓某人的\\n願望得以實現，\\n我要讓那傢伙的……\\n咦、我在想些什麼啦！', 0);
insert into room_unit_comments values (4382, 113801, 2, 1, 1, 0, 1, '你在發什麼呆啊！\\n該到街上\\n去幫助他人囉！', 0);
insert into room_unit_comments values (4383, 113801, 2, 2, 1, 0, 1, '天使之箭！\\n開玩笑的……咦、\\n你從什麼時候\\n開始看的！', 0);
insert into room_unit_comments values (4384, 113801, 2, 3, 1, 0, 1, '真是的！\\n別摸我的翅膀啦！\\n壞掉的話該怎麼辦。', 0);
insert into room_unit_comments values (4385, 113801, 2, 4, 1, 0, 1, '雖然你不是天使，\\n但你總是在\\n幫助別人對吧？\\n我有點敬佩你呢。', 0);
insert into room_unit_comments values (4386, 113801, 2, 101, 4, 0, 1, '如果是穿這件衣服，\\n比起其它人，可能會\\n更注意我吧……咦、\\n你、你在看什麼啦！', 0);
insert into room_unit_comments values (4387, 113801, 2, 102, 8, 0, 1, '吶、吶，如果我有困難\\n你要第一個\\n來我身邊……果、果然\\n還是沒事啦！', 0);
insert into room_unit_comments values (4388, 113801, 2, 1, 1, 1, 1, '早安……那、那個，\\n身為天使，\\n我要去城裡看看……\\n你要一起去嗎？', 0);
insert into room_unit_comments values (4389, 113801, 2, 2, 1, 2, 1, '呼，做完好事之後，\\n吃的午餐總是\\n特別美味呢。', 0);
insert into room_unit_comments values (4390, 113801, 2, 3, 1, 3, 1, '都已經這麼晚了……\\n別熬夜了，\\n快點睡吧。', 0);
insert into room_unit_comments values (4392, 113801, 3, 2, 1, 0, 1, '坐起來真舒服～\\n舒服到\\n好像要飛上天一樣～', 0);
insert into room_unit_comments values (4393, 113801, 3, 3, 1, 0, 1, '呵呵，好可愛……\\n啊，加上翅膀的話，\\n會不會\\n變得更可愛呢？', 0);
insert into room_unit_comments values (4394, 113801, 3, 4, 1, 0, 1, '哈啊～心情好沉靜～\\n好像回復道具一樣，\\n真療癒～', 0);
insert into room_unit_comments values (4395, 113801, 3, 5, 1, 0, 1, '哇、啊啊……\\n好危險，\\n翅膀差點就要\\n燒焦了呢。', 0);
insert into room_unit_comments values (4397, 113801, 3, 7, 1, 0, 1, '要幫上別人的忙是一件\\n很辛苦的事情，所以\\n靠這個恢復生命值吧！', 0);
insert into room_unit_comments values (4398, 113801, 3, 8, 1, 0, 1, '不知道有沒有和天使\\n相關的書……啊！\\n這是正在找的攻略本！', 0);
insert into room_unit_comments values (4400, 113901, 1, 1, 1, 0, 1, '嘻嘻嘻，\\n像個吸血鬼一樣\\n來嚇嚇他吧。', 0);
insert into room_unit_comments values (4401, 113901, 1, 2, 1, 0, 1, '獵物在哪裡呢～！\\n嗯～還想要再\\n更有魄力一點呢。', 0);
insert into room_unit_comments values (4402, 113901, 1, 3, 1, 0, 1, 'Trick or Treat\\n……因為你沒有回答\\n所以我要搗蛋囉。', 0);
insert into room_unit_comments values (4403, 113901, 1, 4, 1, 0, 1, '今天是萬聖節，\\n偶爾也約一下\\n騎士先生好了？', 0);
insert into room_unit_comments values (4404, 113901, 1, 5, 1, 0, 1, '穿著斗篷讓舉手投足間\\n的動作很有抑揚頓挫，\\n真不錯呢。', 0);
insert into room_unit_comments values (4405, 113901, 1, 101, 4, 0, 1, '可以看到各式各樣的\\n扮裝，萬聖節果然是\\n太棒了。', 0);
insert into room_unit_comments values (4406, 113901, 1, 102, 8, 0, 1, '吸血鬼般的行動嘛，\\n像是要咬住他……不，\\n那也太過大膽了！', 0);
insert into room_unit_comments values (4407, 113901, 2, 1, 1, 0, 1, '你找我嗎？\\n請你有事快說喔，\\n我很忙的！', 0);
insert into room_unit_comments values (4408, 113901, 2, 2, 1, 0, 1, '咦？\u3000我衣服破洞了？\\n謝謝你告訴我\\n…你還看得真仔細呢。', 0);
insert into room_unit_comments values (4409, 113901, 2, 3, 1, 0, 1, '呼哇……就算是吸血鬼\\n的樣貌，想睡的時候\\n還是會想睡呢……', 0);
insert into room_unit_comments values (4410, 113901, 2, 4, 1, 0, 1, '萬聖節有著和演唱會\\n不同的熱鬧氣氛，\\n真令人興奮。', 0);
insert into room_unit_comments values (4411, 113901, 2, 101, 4, 0, 1, '就算想嚇我\\n也是沒有用的喔！\\n我馬上就會發現了。', 0);
insert into room_unit_comments values (4412, 113901, 2, 102, 8, 0, 1, '騎士先生，要不要\\n一起過萬聖節……不、\\n沒事啦！', 0);
