insert into room_unit_comments values (4568, 114401, 2, 102, 8, 0, 1, '要是明年的聖誕節\\n也能和那位大人\\n一起度過……', 0);
insert into room_unit_comments values (4569, 114401, 2, 1, 1, 1, 1, '時間就是金錢\\n難得的早晨，\\n來有效利用\\n這段時間吧！', 0);
insert into room_unit_comments values (4570, 114401, 2, 2, 1, 2, 1, '已經中午了呢，\\n來吧，\\n開始為派對\\n做準備囉！', 0);
insert into room_unit_comments values (4571, 114401, 2, 3, 1, 3, 1, '真是安靜的夜晚……\\n我還想\\n再多和你\\n說一下話呢。', 0);
insert into room_unit_comments values (4573, 114401, 3, 2, 1, 0, 1, '適當的休息\\n是通往成功的秘訣！', 0);
insert into room_unit_comments values (4574, 114401, 3, 3, 1, 0, 1, '多麼可愛呀……\\n怎麼看\\n都看不膩呢♪', 0);
insert into room_unit_comments values (4575, 114401, 3, 4, 1, 0, 1, '如果要招待別人，\\n就連這些方面也要顧慮到喔。', 0);
insert into room_unit_comments values (4576, 114401, 3, 5, 1, 0, 1, '真溫暖……\\n這種小憩片刻的時光，\\n讓人倍感珍惜呢。', 0);
insert into room_unit_comments values (4578, 114401, 3, 7, 1, 0, 1, '這深奧的氣味……\\n是不是有使用\\n什麼隱藏配方啊？', 0);
insert into room_unit_comments values (4579, 114401, 3, 8, 1, 0, 1, '聖誕料理的食譜\\n有這麼多種，\\n真讓人苦惱呢。', 0);
insert into room_unit_comments values (4581, 114401, 3, 14, 1, 0, 1, '這個繪本的設計\\n很有趣呢，\\n我都想\\n讓它成為我們的商品了。', 0);
insert into room_unit_comments values (4582, 114401, 3, 15, 1, 0, 1, '真大的玩偶……\\n職人的技術\\n在發著光呢。', 0);
insert into room_unit_comments values (4583, 114601, 1, 1, 1, 0, 1, '說到聖誕節\\n就是派對！\\n我要喝個夠～♪', 0);
insert into room_unit_comments values (4584, 114601, 1, 2, 1, 0, 1, '要是把我想成\\n只喝麥酒的女人，\\n可就大錯特錯囉♪', 0);
insert into room_unit_comments values (4585, 114601, 1, 3, 1, 0, 1, '符合聖誕氣氛的飲料\\n也不錯呢，\\n我下次\\n也來做做看吧。', 0);
insert into room_unit_comments values (4586, 114601, 1, 4, 1, 0, 1, '在溫暖的房間裡\\n喝的麥酒\\n特別好喝呢……', 0);
insert into room_unit_comments values (4587, 114601, 1, 5, 1, 0, 1, '反正今天休假，\\n一大早就開始喝酒\\n應該不會被罵吧？', 0);
insert into room_unit_comments values (4588, 114601, 1, 101, 4, 0, 1, '和他一起\\n在聖誕約會聖地……\\n啊不不，\\n妳在想什麼啊優花梨！', 0);
insert into room_unit_comments values (4589, 114601, 1, 102, 8, 0, 1, '在他面前\\n能夠不逞強，\\n做最真實的自己，\\n真的……很幸福……', 0);
insert into room_unit_comments values (4590, 114601, 2, 1, 1, 0, 1, '沒事啦～\\n我沒喝酒，\\n偶沒有喝啦～', 0);
insert into room_unit_comments values (4591, 114601, 2, 2, 1, 0, 1, '聖誕節\\n這麼特別的日子，\\n當然也要喝上\\n特別的一杯♪', 0);
insert into room_unit_comments values (4592, 114601, 2, 3, 1, 0, 1, '聖誕快樂！\\n來嘛來嘛，\\n我們來乾杯♪', 0);
insert into room_unit_comments values (4593, 114601, 2, 4, 1, 0, 1, '姐姐會表現出\\n可靠的一面\\n給你看的。', 0);
insert into room_unit_comments values (4594, 114601, 2, 101, 4, 0, 1, '要是發生了什麼事，\\n可以來依靠我喔？', 0);
insert into room_unit_comments values (4595, 114601, 2, 102, 8, 0, 1, '呵呵，今後\\n我也會繼續……\\n依賴你唷。', 0);
insert into room_unit_comments values (4596, 114601, 2, 1, 1, 1, 1, '真是清爽的早晨\\n……原來沒有宿醉的早晨，\\n是這麼讓人\\n心情舒暢的啊。', 0);
insert into room_unit_comments values (4597, 114601, 2, 2, 1, 2, 1, '白天喝酒\\n特別好喝呢……不對，\\n要忍耐、忍耐……', 0);
insert into room_unit_comments values (4598, 114601, 2, 3, 1, 3, 1, '已經這麼晚啦……\\n除了睡覺\\n也沒其他事了，\\n喝一點的話應該……', 0);
insert into room_unit_comments values (4600, 114601, 3, 2, 1, 0, 1, '耍廢\\n最棒了～\\n要是有麥酒\\n就更完美了……', 0);
insert into room_unit_comments values (4601, 114601, 3, 3, 1, 0, 1, '就算是我……\\n也可以喜歡\\n可愛的東西吧？', 0);
insert into room_unit_comments values (4602, 114601, 3, 4, 1, 0, 1, '嗯～真香，\\n平日累積的疲勞\\n都消失了呢。', 0);
insert into room_unit_comments values (4603, 114601, 3, 5, 1, 0, 1, '暖洋洋的～\\n開始覺得\\n口渴了呢。', 0);
insert into room_unit_comments values (4605, 114601, 3, 7, 1, 0, 1, '嚼嚼……\\n勞動過後的白飯\\n果然很美味……', 0);
insert into room_unit_comments values (4606, 114601, 3, 8, 1, 0, 1, '聖誕約會\\n完全攻略手冊？\\n這個必須讀讀。', 0);
insert into room_unit_comments values (4608, 114601, 3, 14, 1, 0, 1, '哎呀，這幅畫\\n看起來好立體……\\n應該不是因為\\n我喝得太醉了吧……？', 0);
insert into room_unit_comments values (4609, 114601, 3, 15, 1, 0, 1, '觸感真好～\\n好想擺一個在家裡，\\n但要是麥酒撒上去了\\n應該會很麻煩……', 0);
insert into room_unit_comments values (4610, 101601, 1, 103, 12, 0, 1, '真想永遠\\n獨占秀才的視線～', 0);
insert into room_unit_comments values (4611, 101601, 2, 103, 12, 0, 1, '只要跟秀才待在一起\\n我就超開心！\\n秀才也這麼覺得吧～？', 0);
insert into room_unit_comments values (4612, 114501, 1, 1, 1, 0, 1, '聖誕快樂！\\n呵呵，光是說這句話\\n就讓人感到開心呢。', 0);
insert into room_unit_comments values (4613, 114501, 1, 2, 1, 0, 1, '雖然我平時是\\n照顧孩子們的一方，\\n但今天放開玩\\n也沒關係吧？', 0);
insert into room_unit_comments values (4614, 114501, 1, 3, 1, 0, 1, '聖誕樹、料理\\n和禮物！\\n無論到了幾歲，這些東西都還是\\n很讓人雀躍呢♪', 0);
insert into room_unit_comments values (4615, 114501, 1, 4, 1, 0, 1, '裝飾品和料理\\n都很有品味呢，\\n就當作育幼院\\n舉辦派對的參考吧。', 0);
insert into room_unit_comments values (4616, 114501, 1, 5, 1, 0, 1, '要怎麼做才能讓\\n炒麵麵包變成\\n聖誕節的必吃食物……', 0);
insert into room_unit_comments values (4617, 114501, 1, 101, 4, 0, 1, '這好像是那傢伙\\n會喜歡的料理，\\n留給他吃好了♪', 0);
insert into room_unit_comments values (4618, 114501, 1, 102, 8, 0, 1, '給那傢伙的禮物\\n花了不少錢……\\n不知道他\\n會不會喜歡。', 0);
insert into room_unit_comments values (4619, 114501, 2, 1, 1, 0, 1, '這套禮服\\n適合我嗎？\\n現在的話，你就算看得太入迷，\\n我也會原諒你哦♪', 0);
insert into room_unit_comments values (4620, 114501, 2, 2, 1, 0, 1, '你看這個蛋糕！\\n看起來很好吃吧。\\n偶爾奢侈點\\n也是可以的吧♪', 0);
insert into room_unit_comments values (4621, 114501, 2, 3, 1, 0, 1, '料理和飲料\\n都很美味呢！\\n我要多吃點\\n吃回本♪', 0);
insert into room_unit_comments values (4622, 114501, 2, 4, 1, 0, 1, '跟孩子們一起玩雪\\n應該會很開心，\\n你要不要也加入？', 0);
insert into room_unit_comments values (4623, 114501, 2, 101, 4, 0, 1, '難得的聖誕節，\\n就麻煩你\\n做我的護花使者啦♪', 0);
insert into room_unit_comments values (4624, 114501, 2, 102, 8, 0, 1, '很冷？ 這樣啊……\\n那麼，你可以\\n靠過來一點哦……？', 0);
insert into room_unit_comments values (4625, 114501, 2, 1, 1, 1, 1, '好啦，快點起來吧？\\n陪我去買\\n派對要用的東西。', 0);
insert into room_unit_comments values (4626, 114501, 2, 2, 1, 2, 1, '我在準備\\n派對料理的時候，\\n也順便把午餐做好了，\\n我真是能幹呢。', 0);
insert into room_unit_comments values (4627, 114501, 2, 3, 1, 3, 1, '熱鬧的派對\\n固然好，\\n但感受平安夜的餘韻\\n也很棒呢。', 0);
insert into room_unit_comments values (4629, 114501, 3, 2, 1, 0, 1, '坐起來真舒服～\\n職人的手藝很好呢，\\n真想在家裡也擺一個。', 0);
insert into room_unit_comments values (4630, 114501, 3, 3, 1, 0, 1, '好可愛！\\n要是當成禮物\\n送給孩子們，\\n他們應該會很開心。', 0);
insert into room_unit_comments values (4631, 114501, 3, 4, 1, 0, 1, '真是華麗的香氣，\\n跟聖誕節很搭呢。', 0);
insert into room_unit_comments values (4632, 114501, 3, 5, 1, 0, 1, '寒冷的天氣\\n果然不能少了它……\\n哈，真是天堂♪', 0);
insert into room_unit_comments values (4634, 114501, 3, 7, 1, 0, 1, '嗯──真好吃！\\n不知道這能不能\\n做成炒麵麵包口味？', 0);
insert into room_unit_comments values (4635, 114501, 3, 8, 1, 0, 1, '偶爾來\\n悠閒地讀個書吧……總覺得\\n全都是浪漫愛情的書耶，\\n因為是聖誕節嗎？', 0);
insert into room_unit_comments values (4637, 114501, 3, 14, 1, 0, 1, '這個真有趣！\\n很適合當作\\n孩子們的禮物呢！', 0);
insert into room_unit_comments values (4638, 114501, 3, 15, 1, 0, 1, '我第一次見到\\n這麼漂亮的毛色呢……\\n這是從哪裡\\n得到的呢？', 0);
insert into room_unit_comments values (4639, 114701, 1, 1, 1, 0, 1, '我也能\\n和大家一起\\n享受新年嗎？', 0);
insert into room_unit_comments values (4640, 114701, 1, 2, 1, 0, 1, '希望今年\\n能稍微\\n長高一點點……', 0);
insert into room_unit_comments values (4641, 114701, 1, 3, 1, 0, 1, '呃！ 壓歲錢\\n全都沒了耶！？\\n怎麼會，騙人的吧……？', 0);
insert into room_unit_comments values (4642, 114701, 1, 4, 1, 0, 1, '和很多人\\n一起過新年啊……雖然是第一次，\\n但感覺還不壞呢。', 0);
insert into room_unit_comments values (4643, 114701, 1, 5, 1, 0, 1, '嗯──看來這裡\\n沒有天樓霸斷劍的\\n出場機會啊。', 0);
insert into room_unit_comments values (4644, 114701, 1, 101, 4, 0, 1, '真想和他們\\n共創多一點\\n除了戰鬥以外的回憶呢。', 0);
insert into room_unit_comments values (4645, 114701, 1, 102, 8, 0, 1, '這麼熱鬧的新年\\n還是第一次呢，\\n……很溫暖，真好。', 0);
insert into room_unit_comments values (4646, 114701, 2, 1, 1, 0, 1, '就算沒有記憶，\\n應該也能創造\\n新的快樂回憶吧？', 0);
insert into room_unit_comments values (4647, 114701, 2, 2, 1, 0, 1, '快點出發囉！\\n我還想更加\\n享受新年呢！', 0);
insert into room_unit_comments values (4648, 114701, 2, 3, 1, 0, 1, '欸，你應該\\n已經記住「諾維姆」\\n這個名字了吧？', 0);
insert into room_unit_comments values (4649, 114701, 2, 4, 1, 0, 1, '這套衣服\\n適合我嗎？\\n……這樣啊，\\n謝啦！', 0);
insert into room_unit_comments values (4650, 114701, 2, 101, 4, 0, 1, '和你們一起見到的朝陽\\n我一定不會忘記！\\n留下新的回憶了呢！', 0);
insert into room_unit_comments values (4651, 114701, 2, 102, 8, 0, 1, '要是尾狗刀\\n有一天也能和大家一起\\n共度新年就好了……', 0);
insert into room_unit_comments values (4652, 114701, 2, 1, 1, 1, 1, '一大早就精神滿滿呢！\\n我也來學學你吧！', 0);
insert into room_unit_comments values (4653, 114701, 2, 2, 1, 2, 1, '中午要吃什麼？\\n新年好吃的東西太多了，\\n很難決定啊！', 0);
insert into room_unit_comments values (4654, 114701, 2, 3, 1, 3, 1, '真美好的夜晚！\\n要和我一起熬夜嗎？', 0);
insert into room_unit_comments values (4656, 114701, 3, 2, 1, 0, 1, '偶爾像過新年這樣，\\n悠閒地度過\\n也還不賴……', 0);
insert into room_unit_comments values (4657, 114701, 3, 3, 1, 0, 1, '怎麼？\\n你也想去\\n新年參拜嗎？', 0);
insert into room_unit_comments values (4658, 114701, 3, 4, 1, 0, 1, '嘶──哈──\\n有神殿的味道呢，\\n感覺會有好事發生！', 0);
insert into room_unit_comments values (4659, 114701, 3, 5, 1, 0, 1, '有這傢伙的話，\\n應該能\\n烤出好吃的魚……', 0);
insert into room_unit_comments values (4661, 114701, 3, 7, 1, 0, 1, '唔唔──\\n吃太飽了，\\n腰帶變得好緊……', 0);
insert into room_unit_comments values (4662, 114701, 3, 8, 1, 0, 1, '籤紙上\\n寫的內容\\n真難懂啊……', 0);
insert into room_unit_comments values (4664, 180101, 1, 1, 1, 0, 1, '這裡看起來沒有\\n遇到困難的人……\\n那就在這稍微\\n進行下特訓吧♪', 0);
insert into room_unit_comments values (4665, 180101, 1, 2, 1, 0, 1, '我能在空中飛，\\n就能幫助\\n更多更多的人！\\n……在這裡不能飛呢。', 0);
insert into room_unit_comments values (4666, 180101, 1, 3, 1, 0, 1, '火焰翅膀\\n真帥氣呢──！\\n它能不能\\n張得更大呀？', 0);
insert into room_unit_comments values (4667, 180101, 1, 4, 1, 0, 1, '果然變身後\\n肚子就會很餓呢……\\n我現在能明白佩可姐\\n食慾的來源了。', 0);
insert into room_unit_comments values (4668, 180101, 1, 5, 1, 0, 1, '雖然顏色好像不太一樣\\n但有披風的話，\\n就和騎士先生\\n成對了吧♪', 0);
insert into room_unit_comments values (4669, 180101, 1, 101, 4, 0, 1, '騎士先生不在的話，\\n就提不起勁呢……\\n真想快點見到他……', 0);
insert into room_unit_comments values (4670, 180101, 1, 102, 8, 0, 1, '變成這副姿態，\\n感覺騎士先生\\n就在身邊……\\n怎麼回事，\\n心跳的好快……', 0);
insert into room_unit_comments values (4671, 180101, 2, 1, 1, 0, 1, '騎士先生、騎士先生！\\n你現在有空嗎？\\n來幫忙我一起\\n想訓練菜單！', 0);
insert into room_unit_comments values (4672, 180101, 2, 2, 1, 0, 1, '……嗚喵！？\\n即使是這副姿態，\\n也不能摸\\n我的尾巴──！', 0);
insert into room_unit_comments values (4673, 180101, 2, 3, 1, 0, 1, '你該不會\\n很在意這套服裝吧？\\n那麼，你可以\\n靠近點看唷♪', 0);
insert into room_unit_comments values (4674, 180101, 2, 4, 1, 0, 1, '啊，騎士先生！\\n我要出去巡邏，\\n看有沒有人需要幫助，\\n希望你也能一起去！', 0);
insert into room_unit_comments values (4675, 180101, 2, 101, 4, 0, 1, '想摸我的耳朵？\\n那你也摸摸我的頭！\\n這樣的話，\\n我就讓你摸個夠♪', 0);
insert into room_unit_comments values (4676, 180101, 2, 102, 8, 0, 1, '……我要變強，\\n保護騎士先生！\\n所以……你今後\\n也能在我身邊嗎？', 0);
insert into room_unit_comments values (4677, 180101, 2, 1, 1, 1, 1, '早安──！\\n來嘛來嘛騎士先生！\\n今天也要一起\\n打起精神出發──♪', 0);
insert into room_unit_comments values (4678, 180101, 2, 2, 1, 2, 1, '吃午餐、吃午餐～♪\\n今天的午餐是什麼呢～\\n啊，騎士先生！\\n快點去吃午餐吧！', 0);
insert into room_unit_comments values (4679, 180101, 2, 3, 1, 3, 1, '今天也辛苦啦──！\\n騎士先生，要不要我\\n幫你按摩呢？\\n我的按摩技術備受好評喔！', 0);
insert into room_unit_comments values (4681, 180101, 3, 2, 1, 0, 1, '嗚喵……\\n好舒服……\\n快融化了……\\n嗚喵……', 0);
insert into room_unit_comments values (4682, 180101, 3, 3, 1, 0, 1, '好、好可愛～\\n我可以\\n摸摸它吧……？\\n好乖好乖……♪', 0);
insert into room_unit_comments values (4683, 180101, 3, 4, 1, 0, 1, '好香的味道！\\n還很令人安心，\\n或許很適合\\n在特訓後用呢──！', 0);
insert into room_unit_comments values (4684, 180101, 3, 5, 1, 0, 1, '暖呼呼的……\\n好像\\n要變得\\n軟趴趴了……', 0);
insert into room_unit_comments values (4686, 180101, 3, 7, 1, 0, 1, '真、真好吃……！\\n這個狀態\\n會讓肚子很餓，\\n幫了大忙呢！', 0);
insert into room_unit_comments values (4687, 180101, 3, 8, 1, 0, 1, '我要讀這本書，\\n成為文武雙全的\\n超級無敵日和──！', 0);
insert into room_unit_comments values (4689, 111801, 1, 1, 1, 0, 1, '今年也想\\n和大家一起吃很多\\n好吃的東西。', 0);
insert into room_unit_comments values (4690, 111801, 1, 2, 1, 0, 1, '挑戰是很重要的事呢☆\\n所以說，\\n將魔物加入新年料理中……', 0);
insert into room_unit_comments values (4691, 111801, 1, 3, 1, 0, 1, '新年料理是一種吉祥物！\\n吃得越多，\\n就會變得越幸福才對！', 0);
insert into room_unit_comments values (4692, 111801, 1, 4, 1, 0, 1, '就沒有那種\\n裝了很多食物的\\n福袋嗎……？', 0);
insert into room_unit_comments values (4693, 111801, 1, 5, 1, 0, 1, '新年參拜\\n要許什麼願呢……\\n五穀豐登？', 0);
insert into room_unit_comments values (4694, 111801, 1, 101, 4, 0, 1, '想要一直像這樣\\n度過一年的開始呢。', 0);
insert into room_unit_comments values (4695, 111801, 1, 102, 8, 0, 1, '有朝一日，\\n希望能兩個人單獨看\\n新年的日出。', 0);
insert into room_unit_comments values (4696, 111801, 2, 1, 1, 0, 1, '再來點年糕！\\n無論多少我都\\n吃得下去哦！', 0);
insert into room_unit_comments values (4697, 111801, 2, 2, 1, 0, 1, '要不要去採集\\n新年料理的食材呀？\\n是很好的運動唷☆', 0);
insert into room_unit_comments values (4698, 111801, 2, 3, 1, 0, 1, '放風箏真難呢～\\n線都會纏在一起。', 0);
insert into room_unit_comments values (4699, 111801, 2, 4, 1, 0, 1, '一年之計在於元旦，\\n來制定今年\\n一年份的菜單吧！', 0);
insert into room_unit_comments values (4700, 111801, 2, 101, 4, 0, 1, '來來！\\n你可以再靠近一點\\n看我的和服唷☆', 0);
insert into room_unit_comments values (4701, 111801, 2, 102, 8, 0, 1, '你的新年初夢\\n是什麼呢？\\n我的是……祕密☆', 0);
insert into room_unit_comments values (4702, 111801, 2, 1, 1, 1, 1, '新年的早晨\\n讓人覺得\\n心情很好呢☆', 0);
insert into room_unit_comments values (4703, 111801, 2, 2, 1, 2, 1, '吃完年糕湯後，\\n輪到點心時間，\\n喝紅豆湯！', 0);
insert into room_unit_comments values (4704, 111801, 2, 3, 1, 3, 1, '做一個溫暖\\n又幸福的\\n新年初夢吧。', 0);
insert into room_unit_comments values (4706, 111801, 3, 2, 1, 0, 1, '呼～逛攤販\\n逛得真滿足☆\\n休息一下休息一下。', 0);
insert into room_unit_comments values (4707, 111801, 3, 3, 1, 0, 1, '擺一個\\n吉祥的玩偶在旁邊，\\n讓它帶來福氣！', 0);
insert into room_unit_comments values (4708, 111801, 3, 4, 1, 0, 1, '沒有紅豆湯味道\\n的香氛嗎？\\n既然沒有，只好動手做了……', 0);
insert into room_unit_comments values (4709, 111801, 3, 5, 1, 0, 1, '在這裡烤年糕的話\\n感覺會\\n很好吃呢～♪', 0);
insert into room_unit_comments values (4711, 111801, 3, 7, 1, 0, 1, '好吃到\\n我的手\\n完全停不下來～', 0);
insert into room_unit_comments values (4712, 111801, 3, 8, 1, 0, 1, '新的新年料理的\\n線索是……原來如此……\\n嗯哼嗯哼……', 0);
insert into room_unit_comments values (4714, 100601, 1, 103, 12, 0, 1, '想和哥哥進行這樣\\n和那樣的特訓呢，\\n欸嘿嘿♪', 0);
insert into room_unit_comments values (4715, 100601, 2, 103, 12, 0, 1, '只要\\n被哥哥誇獎，\\n我的身體就會\\n湧起一股熱意呢♪', 0);
insert into room_unit_comments values (4716, 115001, 1, 1, 1, 0, 1, '把漂亮服裝\\n穿在身上的感覺\\n還不壞呢。', 0);
insert into room_unit_comments values (4717, 115001, 1, 2, 1, 0, 1, '偶爾\\n利用吉祥物來招好運\\n也是可以啦。', 0);
insert into room_unit_comments values (4718, 115001, 1, 3, 1, 0, 1, '親手做料理這種事，\\n因為很沒效率，\\n所以我不太做，\\n但還是辦得到的。', 0);
insert into room_unit_comments values (4719, 115001, 1, 4, 1, 0, 1, '如果是為了研究，\\n有時遠赴千里\\n前往實地探查\\n也是必要的。', 0);
insert into room_unit_comments values (4720, 115001, 1, 5, 1, 0, 1, '金槍鮪的\\n突然變異……\\n我對此抱有\\n濃厚的興趣呢。', 0);
insert into room_unit_comments values (4721, 115001, 1, 101, 4, 0, 1, '藉由重現\\n漁夫的技法，\\n我的假說之一\\n又得到驗證了。', 0);
insert into room_unit_comments values (4722, 115001, 1, 102, 8, 0, 1, '就算是我，\\n在新年這天\\n也想悠閒地度過呢，\\n很意外嗎？', 0);
insert into room_unit_comments values (4723, 115001, 2, 1, 1, 0, 1, '即使不依靠模仿，\\n言行舉止要符合衣著\\n這種程度的小事，\\n能辦到也是理所當然的。', 0);
insert into room_unit_comments values (4724, 115001, 2, 2, 1, 0, 1, '你想好壓歲錢\\n要用在哪裡了嗎？\\n慢慢想\\n也沒關係喔。', 0);
insert into room_unit_comments values (4725, 115001, 2, 3, 1, 0, 1, '無論是撲克牌還是花牌\\n我都奉陪。\\n需要放水的話，\\n隨時都能跟我說。', 0);
insert into room_unit_comments values (4726, 115001, 2, 4, 1, 0, 1, '新年參拜時\\n祈求的內容？\\n……就容我\\n保密吧。', 0);
insert into room_unit_comments values (4727, 115001, 2, 101, 4, 0, 1, '我們把\\n病變的金槍鮪\\n釣起來吧？\\n……開玩笑的。', 0);
insert into room_unit_comments values (4728, 115001, 2, 102, 8, 0, 1, '不求回報的心願，\\n這就是祈禱\\n的精髓吧。', 0);
insert into room_unit_comments values (4729, 115001, 2, 1, 1, 1, 1, '……在你去洗那睡眼惺忪的臉\\n的這段時間，\\n我來泡個\\n溫暖的熱茶吧。', 0);
insert into room_unit_comments values (4730, 115001, 2, 2, 1, 2, 1, '好啦，要正式\\n重新開始活動了，\\n你也和我同行吧。\\n吃過午餐了嗎？', 0);
insert into room_unit_comments values (4731, 115001, 2, 3, 1, 3, 1, '我再\\n處理一下事情\\n就去睡。\\n你快去睡吧。', 0);
insert into room_unit_comments values (4733, 115001, 3, 2, 1, 0, 1, '我稍微休息一下……\\n時間到了請\\n叫醒我。', 0);
insert into room_unit_comments values (4734, 115001, 3, 3, 1, 0, 1, '偽裝成禮物\\n潛入……有值得一試\\n的價值呢。', 0);
insert into room_unit_comments values (4735, 115001, 3, 4, 1, 0, 1, '透過嗅覺能得到的情報量\\n不容小覷，\\n這是在模仿動物時\\n必須留意的一點哦。', 0);
insert into room_unit_comments values (4736, 115001, 3, 5, 1, 0, 1, '穩定跳動的火焰\\n會帶來放鬆效果，\\n或許是\\n本能所致呢。', 0);
insert into room_unit_comments values (4738, 115001, 3, 7, 1, 0, 1, '無論再強的人，\\n都不能沒有\\n後勤和補給。', 0);
insert into room_unit_comments values (4739, 115001, 3, 8, 1, 0, 1, '一切學問\\n都會成為完美模仿的\\n龐大基礎。', 0);
insert into room_unit_comments values (4741, 115501, 1, 1, 1, 0, 1, '在下會用這份新得到的力量\\n與夥伴們一同開創\\n理想的未來。', 0);
insert into room_unit_comments values (4742, 115501, 1, 2, 1, 0, 1, '這裡是大家……\\n與在下的歸宿。', 0);
insert into room_unit_comments values (4743, 115501, 1, 3, 1, 0, 1, '主人也想試著\\n召喚精靈嗎？', 0);
insert into room_unit_comments values (4744, 115501, 1, 4, 1, 0, 1, '主人，零用錢\\n還夠用嗎？\\n不需要客氣哦。', 0);
insert into room_unit_comments values (4745, 115501, 1, 5, 1, 0, 1, '穿著這套服裝\\n就能自然而然\\n提起幹勁。', 0);
insert into room_unit_comments values (4746, 115501, 1, 101, 4, 0, 1, '為了不輸給\\n進化的精靈，\\n在下要勤奮修行。', 0);
insert into room_unit_comments values (4747, 115501, 1, 102, 8, 0, 1, '希望和夥伴們在一起的\\n幸福時光，\\n能永遠持續下去……', 0);
insert into room_unit_comments values (4748, 115501, 2, 1, 1, 0, 1, '主人的手手\\n又大又溫暖呢。\\n握緊緊……呵呵♪', 0);
insert into room_unit_comments values (4749, 115501, 2, 2, 1, 0, 1, '下次也和主人\\n說說村子裡\\n流傳的故事吧。', 0);
insert into room_unit_comments values (4750, 115501, 2, 3, 1, 0, 1, '在下把從村裡採到的水果\\n帶來了。\\n請主人享用。', 0);
insert into room_unit_comments values (4751, 115501, 2, 4, 1, 0, 1, '把村裡的土產\\n分給這裡的大家吧。', 0);
insert into room_unit_comments values (4752, 115501, 2, 101, 4, 0, 1, '和夥伴們圍桌吃飯……這份溫暖\\n是來自大家\\n內心的溫度吧。', 0);
insert into room_unit_comments values (4753, 115501, 2, 102, 8, 0, 1, '被主人\\n呼喚名字時，\\n我的內心就充滿溫暖♪', 0);
insert into room_unit_comments values (4754, 115501, 2, 1, 1, 1, 1, '呵呵……主人\\n睡到頭髮\\n翹起來囉？', 0);
insert into room_unit_comments values (4755, 115501, 2, 2, 1, 2, 1, '今天的午餐是\\n村子的傳統料理。\\n還合您胃口嗎？', 0);
insert into room_unit_comments values (4756, 115501, 2, 3, 1, 3, 1, '為主人招來\\n能安穩入眠的微風……\\n晚安。', 0);
insert into room_unit_comments values (4758, 115501, 3, 2, 1, 0, 1, '這種療癒感……\\n真希望主人\\n也能感受到……', 0);
insert into room_unit_comments values (4759, 115501, 3, 3, 1, 0, 1, '真可愛……\\n有著和精靈們\\n相似的感覺呢……♪', 0);
insert into room_unit_comments values (4760, 115501, 3, 4, 1, 0, 1, '精靈啊……\\n將這沁人心脾的香氣，\\n傳遞至各個角落吧。', 0);
insert into room_unit_comments values (4761, 115501, 3, 5, 1, 0, 1, '這溫度\\n正好適合\\n進行冥想呢。', 0);
insert into room_unit_comments values (4763, 115501, 3, 7, 1, 0, 1, '為了驅使\\n進化的精靈，\\n要多吃點補充體力……！', 0);
insert into room_unit_comments values (4764, 115501, 3, 8, 1, 0, 1, '得更加了解\\n風之精靈\\n才行……', 0);
insert into room_unit_comments values (4766, 115601, 1, 1, 1, 0, 1, '回想起儀式時的感覺，\\n召喚出精靈……\\n出來吧～♪', 0);
insert into room_unit_comments values (4767, 115601, 1, 2, 1, 0, 1, '咿呀！真是的……\\n竟然突然從背後偷襲，\\n我可不記得有培養出\\n這種愛惡作劇的精靈。', 0);
insert into room_unit_comments values (4768, 115601, 1, 3, 1, 0, 1, '儀式用的服裝……\\n沒辦法進行\\n太激烈的運動呢……但\\n在戰鬥時也管不了那麼多！', 0);
insert into room_unit_comments values (4769, 115601, 1, 4, 1, 0, 1, '對了……\\n試著拜託精靈\\n留在騎士先生身邊\\n守護他好了……', 0);
insert into room_unit_comments values (4770, 115601, 1, 5, 1, 0, 1, '呵呵，希望像這樣\\n能和精靈開心對話\\n的平穩日子\\n能一直持續下去。', 0);
insert into room_unit_comments values (4771, 115601, 1, 101, 4, 0, 1, '騎、騎騎、騎士先生！\\n請和我一起，那個……！\\n嗚嗚，連邀請他一起練習\\n都說不出口……', 0);
insert into room_unit_comments values (4772, 115601, 1, 102, 8, 0, 1, '啊，精靈……\\n呵呵，謝謝你為我加油。\\n我的心意……總有一天\\n一定會傳達的。', 0);
insert into room_unit_comments values (4773, 115601, 2, 1, 1, 0, 1, '我做了\\n剛學會的料理，\\n騎士先生也吃吧？', 0);
insert into room_unit_comments values (4774, 115601, 2, 2, 1, 0, 1, '騎士先生，\\n你會累嗎？\\n我和精靈\\n能為你治療唷？', 0);
insert into room_unit_comments values (4775, 115601, 2, 3, 1, 0, 1, '真是的，別再惡作劇\\n……騎士先生！？\\n對、對不起！\\n我以為是精靈！', 0);
insert into room_unit_comments values (4776, 115601, 2, 4, 1, 0, 1, '騎士先生！\\n你有空的話，\\n要不要和精靈\\n說說話呢？', 0);
insert into room_unit_comments values (4777, 115601, 2, 101, 4, 0, 1, '……這是我第一次\\n穿這種服裝……\\n咦，很可愛……？\\n謝、謝謝誇獎……！', 0);
insert into room_unit_comments values (4778, 115601, 2, 102, 8, 0, 1, '有困難的話\\n要找我商量喔。\\n如果是為了騎士先生……\\n我可以做到很多事、很多很多哦！', 0);
insert into room_unit_comments values (4779, 115601, 2, 1, 1, 1, 1, '早安呀騎士先生！\\n我現在\\n要去外面冥想……\\n騎士先生要不要一起呢？', 0);
insert into room_unit_comments values (4780, 115601, 2, 2, 1, 2, 1, '午餐時間到了。\\n食物由我來\\n準備就好，\\n你就好好休息吧。', 0);
insert into room_unit_comments values (4781, 115601, 2, 3, 1, 3, 1, '已經這麼晚了……\\n騎士先生，來，給你。\\n睡前喝杯溫牛奶\\n能睡得更安穩唷♪', 0);
insert into room_unit_comments values (4783, 115601, 3, 2, 1, 0, 1, '……嗯，真讓人放鬆。\\n好想和精靈一起\\n施展像這種能讓人\\n放鬆的魔法。', 0);
insert into room_unit_comments values (4784, 115601, 3, 3, 1, 0, 1, '好可愛……\\n精靈也很喜歡……\\n對了，要不要做個\\n一模一樣的送給他？', 0);
insert into room_unit_comments values (4785, 115601, 3, 4, 1, 0, 1, '好香……\\n把這個放在我們家，\\n媽媽應該會很高興！', 0);
insert into room_unit_comments values (4786, 115601, 3, 5, 1, 0, 1, '好溫暖……\\n就像是被人\\n抱在懷裡\\n一樣……', 0);
insert into room_unit_comments values (4788, 115601, 3, 7, 1, 0, 1, '感謝\\n大自然的恩惠……\\n我開動了♪', 0);
insert into room_unit_comments values (4789, 115601, 3, 8, 1, 0, 1, '我想更加了解\\n和精靈有關的知識，\\n保護好大家……', 0);
insert into room_unit_comments values (4791, 102201, 1, 103, 12, 0, 1, '只要和他待在一起\\n就會心跳加速……\\n這該不會是……', 0);
insert into room_unit_comments values (4792, 102201, 2, 103, 12, 0, 1, '只要是和你一起，\\n無論玩什麼遊戲……\\n那、那個，一一、\\n一定都很開心！', 0);
insert into room_unit_comments values (4793, 106401, 1, 1, 1, 0, 1, '與各式各樣的人交談，\\n或許就能找到\\n恢復記憶的線索……？', 0);
insert into room_unit_comments values (4794, 106401, 1, 2, 1, 0, 1, '來做個\\n冰淇淋好了。\\n香草口味和薄荷口味……', 0);
insert into room_unit_comments values (4795, 106401, 1, 3, 1, 0, 1, '每次和涅比亞搭話，\\n她總會逃跑呢……\\n我明明已經不會襲擊她了……', 0);
insert into room_unit_comments values (4796, 106401, 1, 4, 1, 0, 1, '「可可蘿」、\\n「可可蘿」……\\n……不要叫錯了……', 0);
insert into room_unit_comments values (4797, 106401, 1, 5, 1, 0, 1, '我最近好像胖了一點。\\n一定是吃太多\\n好吃的東西了……', 0);
insert into room_unit_comments values (4798, 106401, 1, 101, 4, 0, 1, '待在這裡\\n讓人十分安心呢……\\n因為是自己的家嘛。', 0);
insert into room_unit_comments values (4799, 106401, 1, 102, 8, 0, 1, '想永遠記得\\n和大家在一起的時光\\n……我是這麼認為的。', 0);
insert into room_unit_comments values (4800, 106401, 2, 1, 1, 0, 1, '龍人族\\n有這麼稀奇嗎？\\n我倒是沒什麼實感……', 0);
insert into room_unit_comments values (4801, 106401, 2, 2, 1, 0, 1, '我在練習做料理\\n……你能幫我試吃嗎？\\n別抱有太大的期待哦。', 0);
insert into room_unit_comments values (4802, 106401, 2, 3, 1, 0, 1, '敲一敲頭，\\n會不會就能\\n恢復記憶了……？\\n你不想嘗試也沒關係喔？', 0);
insert into room_unit_comments values (4803, 106401, 2, 4, 1, 0, 1, '忘記我像嬰兒一樣的\\n那段日子吧……\\n拜託你了……', 0);
insert into room_unit_comments values (4804, 106401, 2, 101, 4, 0, 1, '咿呀！？\\n不、不能\\n抓我的尾巴唷？', 0);
insert into room_unit_comments values (4805, 106401, 2, 102, 8, 0, 1, '就算恢復了記憶……\\n我也想繼續\\n和你們在一起。', 0);
insert into room_unit_comments values (4806, 106401, 2, 1, 1, 1, 1, '早安。\\n你的頭髮翹起來了哦？\\n……呵呵，這樣就行了。', 0);
insert into room_unit_comments values (4807, 106401, 2, 2, 1, 2, 1, '差不多該吃午餐了吧？\\n美味的飯菜\\n就該和大家一起吃……對吧。', 0);
insert into room_unit_comments values (4808, 106401, 2, 3, 1, 3, 1, '已經這麼晚了呢。\\n差不多該睡了吧？\\n……呼啊……', 0);
insert into room_unit_comments values (4810, 106401, 3, 2, 1, 0, 1, '鬆鬆軟軟，抱緊～～\\n……啊，我這是那個……\\n在做伸展運動……', 0);
insert into room_unit_comments values (4811, 106401, 3, 3, 1, 0, 1, '我沒什麼興趣。\\n但是……但是……\\n……好、好可愛……', 0);
insert into room_unit_comments values (4812, 106401, 3, 4, 1, 0, 1, '真是好聞的香味。\\n讓人很安心呢……\\n……哈啾。', 0);
insert into room_unit_comments values (4813, 106401, 3, 5, 1, 0, 1, '雖然我擅長冰凍，\\n但也不討厭溫暖呢。', 0);
insert into room_unit_comments values (4815, 106401, 3, 7, 1, 0, 1, '我可不是\\n貪吃鬼哦？\\n但是，再一口就好……', 0);
insert into room_unit_comments values (4816, 106401, 3, 8, 1, 0, 1, '嗯……原來如此。\\n學到一課了……', 0);
insert into room_unit_comments values (4818, 115701, 1, 1, 1, 0, 1, '穿泳裝的偵探啊……\\n有點\\n害羞起來了……', 0);
insert into room_unit_comments values (4819, 115701, 1, 2, 1, 0, 1, '只要有人委託，\\n要我上山下海\\n都沒問題！', 0);
insert into room_unit_comments values (4820, 115701, 1, 3, 1, 0, 1, '趁現在\\n把助手先生的祕密……\\n我……我要幹什麼啊！？', 0);
insert into room_unit_comments values (4821, 115701, 1, 4, 1, 0, 1, '偶爾的休假是必要的。\\n在海邊悠閒地……\\n唔？有發生事件的感覺！', 0);
insert into room_unit_comments values (4822, 115701, 1, 5, 1, 0, 1, '偵探的祕密道具……\\n穿泳衣的話\\n就沒地方藏了……！', 0);
insert into room_unit_comments values (4823, 115701, 1, 101, 4, 0, 1, '差不多到助手先生\\n出現的時間了吧？\\n呵呵，真期待。', 0);
insert into room_unit_comments values (4824, 115701, 1, 102, 8, 0, 1, '只有我和助手先生\\n兩個人\\n才能將真相揭開。', 0);
insert into room_unit_comments values (4825, 115701, 2, 1, 1, 0, 1, '呀啊啊！？\\n竟、竟然\\n趁我不備……很不賴嘛，助手先生。', 0);
