insert into room_unit_comments values (4827, 115701, 2, 3, 1, 0, 1, '這個觸感……我知道了！\\n是助手先生！\\n呵呵，果然♪', 0);
insert into room_unit_comments values (4828, 115701, 2, 4, 1, 0, 1, '這套泳裝……怎麼樣？\\n我是有打算保留\\n偵探的要素啦。', 0);
insert into room_unit_comments values (4829, 115701, 2, 101, 4, 0, 1, '嗯？是委託嗎？\\n只要是助手先生的請求，\\n都是小事一樁啦。', 0);
insert into room_unit_comments values (4830, 115701, 2, 102, 8, 0, 1, '助手先生，\\n要不要和我一起去海邊？\\n……不是因為有事件哦？', 0);
insert into room_unit_comments values (4831, 115701, 2, 1, 1, 1, 1, '來泡杯咖啡\\n醒醒腦吧。\\n你要一起喝嗎？', 0);
