insert into room_unit_comments values (4848, 180601, 1, 4, 1, 0, 1, '隨心所欲、無憂無慮地\\n和那傢伙一起冒險……\\n這就是幸福吧。', 0);
insert into room_unit_comments values (4849, 180601, 1, 5, 1, 0, 1, '公主型態……\\n就像公主一樣呢。\\n我是公主……\\n這樣啊……呵呵♪', 0);
insert into room_unit_comments values (4850, 180601, 1, 101, 4, 0, 1, '和那傢伙產生連繫、\\n孕育而出的這段羈絆，\\n是我的寶物。', 0);
