insert into room_unit_comments values (4852, 180601, 2, 1, 1, 0, 1, '哼哼～♪\\n你找公主型態的\\n天才魔法師\\n凱留大人有什麼事嗎？', 0);
insert into room_unit_comments values (4853, 180601, 2, 2, 1, 0, 1, '從今以後\\n就靠我的力量來\\n快速解決\\n各種大小事吧！', 0);
insert into room_unit_comments values (4854, 180601, 2, 3, 1, 0, 1, '魔力不斷湧出……\\n我說，該不會\\n現在的我\\n超級強吧？', 0);
insert into room_unit_comments values (4855, 180601, 2, 4, 1, 0, 1, '就算因為變身的副作用\\n肚子變得很餓，\\n我還是不打算\\n吃魔物料理喔！？', 0);
insert into room_unit_comments values (4856, 180601, 2, 101, 4, 0, 1, '我說，如果是你的話，\\n好像能把我的心……\\n全部交出去。', 0);
