insert into room_unit_comments values (4862, 180601, 3, 2, 1, 0, 1, '啊──♪真棒！\\n要好好休息提升魔力……\\n幫上那傢伙的忙\\n才行。', 0);
insert into room_unit_comments values (4863, 180601, 3, 3, 1, 0, 1, '這是什麼啊，太可愛了吧……！\\n都讓人\\n想收藏了！', 0);
insert into room_unit_comments values (4864, 180601, 3, 4, 1, 0, 1, '這個香氣……讓人\\n非常放鬆呢……\\n啊，我好像想到\\n不錯的魔法了。', 0);
insert into room_unit_comments values (4865, 180601, 3, 5, 1, 0, 1, '好暖和……\\n真令人安心……\\n就像那傢伙\\n手心的溫度。', 0);
