insert into room_unit_comments values (5098, 116801, 3, 3, 1, 0, 1, '這個是……！\\n真是不輸貓咪的可愛喵……！', 0);
insert into room_unit_comments values (5099, 116801, 3, 4, 1, 0, 1, '好好聞啊喵～\\n感覺就快要\\n發出呼嚕聲了喵～', 0);
insert into room_unit_comments values (5100, 116801, 3, 5, 1, 0, 1, '呼喵～\\n真想就這樣縮成一團\\n不去工作喵……', 0);
insert into room_unit_comments values (5102, 116801, 3, 7, 1, 0, 1, '做生意就是打仗喵。\\n我要多吃點，\\n然後努力工作喵！', 0);
insert into room_unit_comments values (5103, 116801, 3, 8, 1, 0, 1, '嗯嗯，鯛魚燒的\\n創意食譜集……\\n有參考價值喵……', 0);
insert into room_unit_comments values (5105, 102101, 1, 103, 12, 0, 1, '在演出前看到哥哥的臉\\n就覺得非常安心……♪', 0);
insert into room_unit_comments values (5106, 102101, 2, 103, 12, 0, 1, '我想讓哥哥多看看我……\\n所以……\\n我想變得更強……！', 0);
insert into room_unit_comments values (5107, 116901, 1, 1, 1, 0, 1, '真想和大家一起\\n更有效率地工作呢！', 0);
insert into room_unit_comments values (5108, 116901, 1, 2, 1, 0, 1, '得調整其他的\\n打工行程才行……', 0);
insert into room_unit_comments values (5109, 116901, 1, 3, 1, 0, 1, '時薪調漲了！\\n工作能力被認可，\\n讓人更有幹勁了呢。', 0);
insert into room_unit_comments values (5110, 116901, 1, 4, 1, 0, 1, '若能更有效率的行動，\\n就能追加更多業務……\\n我燃起鬥志了！', 0);
insert into room_unit_comments values (5111, 116901, 1, 5, 1, 0, 1, '記帳的訣竅啊……\\n去請教優花梨好了。', 0);
insert into room_unit_comments values (5112, 116901, 1, 101, 4, 0, 1, '我會作為領班，\\n嚴厲地鍛鍊大家！', 0);
insert into room_unit_comments values (5113, 116901, 1, 102, 8, 0, 1, '無論是戰鬥還是打工，\\n都能安心地將\\n背後託付給你呢。', 0);
insert into room_unit_comments values (5114, 116901, 2, 1, 1, 0, 1, '怎麼了？\\n有關工作的事\\n都可以找我談喔！', 0);
insert into room_unit_comments values (5115, 116901, 2, 2, 1, 0, 1, '真不愧是我的徒弟。\\n這下能放心把\\n工作交給你了。', 0);
insert into room_unit_comments values (5116, 116901, 2, 3, 1, 0, 1, '我要做好榜樣，\\n拿出最棒的\\n工作表現才行！', 0);
insert into room_unit_comments values (5117, 116901, 2, 4, 1, 0, 1, '我能……\\n多依賴你一些些嗎……？', 0);
insert into room_unit_comments values (5118, 116901, 2, 101, 4, 0, 1, '呵呵，你也成長為一名\\n能獨當一面的\\n打工戰士了呢！', 0);
insert into room_unit_comments values (5119, 116901, 2, 102, 8, 0, 1, '我試著寫了\\n新玩具的企劃書。\\n能幫我看看嗎？', 0);
insert into room_unit_comments values (5120, 116901, 2, 1, 1, 1, 1, '早晨是最有效率的時段喔！\\n麻利地開始工作吧！', 0);
insert into room_unit_comments values (5121, 116901, 2, 2, 1, 2, 1, '走，去吃午餐吧。\\n今天的菜色是什麼呢……\\n真期待♪', 0);
insert into room_unit_comments values (5122, 116901, 2, 3, 1, 3, 1, '已經這麼晚了……\\n為了明天得好好睡覺呢。', 0);
insert into room_unit_comments values (5124, 116901, 3, 2, 1, 0, 1, '適當的勞動與休息循環\\n能創造出最高效率……\\n呼……', 0);
insert into room_unit_comments values (5125, 116901, 3, 3, 1, 0, 1, '啊～抱起來的手感真棒……\\n是最有效率的療癒小物呢♪', 0);
insert into room_unit_comments values (5126, 116901, 3, 4, 1, 0, 1, '真是令人平靜的香氣……\\n感覺專注力有所提升了。', 0);
insert into room_unit_comments values (5127, 116901, 3, 5, 1, 0, 1, '呼……好療癒……\\n懂得切換「\u200b工作」\\n與「休息」模式很重要喔。', 0);
insert into room_unit_comments values (5129, 116901, 3, 7, 1, 0, 1, '要好好補充能量\\n才能認真工作。', 0);
insert into room_unit_comments values (5130, 116901, 3, 8, 1, 0, 1, '為了提高效率，\\n我也想學學生產管理呢。', 0);
insert into room_unit_comments values (5132, 180301, 1, 1, 1, 0, 1, '在變身的這段期間，\\n來嘗試看看各種事吧。\\n首先是……', 0);
insert into room_unit_comments values (5133, 180301, 1, 2, 1, 0, 1, '能飛還真是方便呢。\\n能在食物冷掉前到家。', 0);
insert into room_unit_comments values (5134, 180301, 1, 3, 1, 0, 1, '公主啊……\\n雖然不是我的風格，\\n但也不討厭。', 0);
insert into room_unit_comments values (5135, 180301, 1, 4, 1, 0, 1, '我該用這份力量\\n做什麼呢……\\n問問劍吧。', 0);
insert into room_unit_comments values (5136, 180301, 1, 5, 1, 0, 1, '即使變身了，我也還是我。\\n就算能暫時變強，\\n本身也不能太弱。', 0);
insert into room_unit_comments values (5137, 180301, 1, 101, 4, 0, 1, '就算沒有變身，\\n只要有他在身邊，\\n還是能湧出力量……', 0);
insert into room_unit_comments values (5138, 180301, 1, 102, 8, 0, 1, '總有一天……\\n我會把心意傳達給你……\\n總有一天一定會！', 0);
insert into room_unit_comments values (5139, 180301, 2, 1, 1, 0, 1, '你要和誰戰鬥嗎？\\n還是要去修行？\\n無論是哪個我都奉陪。', 0);
insert into room_unit_comments values (5140, 180301, 2, 2, 1, 0, 1, '一直維持變身姿態\\n究竟會變得多餓……\\n我可不想嘗試喔？', 0);
insert into room_unit_comments values (5141, 180301, 2, 3, 1, 0, 1, '我雖然不想\\n成為真正的公主，\\n但若有騎士在身邊\\n會很安心吧。', 0);
insert into room_unit_comments values (5142, 180301, 2, 4, 1, 0, 1, '希望有一天，這份力量\\n能用來拯救他人……\\n找到除了戰鬥\\n之外的用途。', 0);
insert into room_unit_comments values (5143, 180301, 2, 101, 4, 0, 1, '真想讓你感受到，\\n這份向我流淌而來的、\\n你給予的力量……\\n那個……還有，我的心意也是……', 0);
insert into room_unit_comments values (5144, 180301, 2, 102, 8, 0, 1, '傳達心意……\\n竟然是一件\\n這麼困難的事……\\n不過，總有一天要傳達出去……！', 0);
insert into room_unit_comments values (5145, 180301, 2, 1, 1, 1, 1, '早安！今天真是\\n適合變身的好日子！\\n呵呵，我開玩笑的。', 0);
insert into room_unit_comments values (5146, 180301, 2, 2, 1, 2, 1, '準備比平時\\n更多的食物吧，不然\\n變身解除後會很痛苦的。', 0);
insert into room_unit_comments values (5147, 180301, 2, 3, 1, 3, 1, '今天辛苦了。\\n我也差不多該把變身解除，\\n好好休息了。', 0);
insert into room_unit_comments values (5149, 180301, 3, 2, 1, 0, 1, '在變身期間\\n就算不活動，\\n是不是也會肚子餓……？', 0);
insert into room_unit_comments values (5150, 180301, 3, 3, 1, 0, 1, '我有沒有變得\\n像你們一樣可愛呢？', 0);
insert into room_unit_comments values (5151, 180301, 3, 4, 1, 0, 1, '對香味的感知力\\n好像也比平時更強了……', 0);
insert into room_unit_comments values (5152, 180301, 3, 5, 1, 0, 1, '幫大忙了，\\n我的腳正好有點冰。', 0);
insert into room_unit_comments values (5154, 180301, 3, 7, 1, 0, 1, '這個是……！\\n不知道能不能\\n改良成攜帶式的？', 0);
insert into room_unit_comments values (5155, 180301, 3, 8, 1, 0, 1, '來讀爺爺給我的技法書，\\n回歸初心吧。', 0);
insert into room_unit_comments values (5157, 117001, 1, 1, 1, 0, 1, '我要製作最強力的媚藥，\\n讓那位大人的心\\n隨我掌控……', 0);
insert into room_unit_comments values (5158, 117001, 1, 2, 1, 0, 1, '說到夏天就是海與西瓜、\\n煙火和泳裝……\\n還有媚藥了……', 0);
insert into room_unit_comments values (5159, 117001, 1, 3, 1, 0, 1, '來準備攻略\\n那位大人的心吧。\\n這個夏天一定要成功……', 0);
insert into room_unit_comments values (5160, 117001, 1, 4, 1, 0, 1, '該怎麼料理接近\\n那位大人的臭蟲呢……\\n真是可恨。', 0);
insert into room_unit_comments values (5161, 117001, 1, 5, 1, 0, 1, '……嘻嘻，調配藥劑的過程\\n真是無比幸福……', 0);
insert into room_unit_comments values (5162, 117001, 1, 101, 4, 0, 1, '希望那位大人的一切\\n都能盡快屬於我……', 0);
insert into room_unit_comments values (5163, 117001, 1, 102, 8, 0, 1, '無論是刨冰、煙火、還是海邊……\\n都要和您一起才有意義……\\n沒錯，您就是我的全部。', 0);
insert into room_unit_comments values (5164, 117001, 2, 1, 1, 0, 1, '來享受只屬於我們\\n兩人的夏天吧……？嘻嘻。', 0);
insert into room_unit_comments values (5165, 117001, 2, 2, 1, 0, 1, '哎呀，大人您打算\\n瞞著我去哪裡呢……？', 0);
insert into room_unit_comments values (5166, 117001, 2, 3, 1, 0, 1, '要不要去海邊呢？\\n那裡是最適合和您\\n一同共度的場所……', 0);
insert into room_unit_comments values (5167, 117001, 2, 4, 1, 0, 1, '您來的正好。\\n請您喝下……我剛做好\\n的藥水……嘻嘻。', 0);
insert into room_unit_comments values (5168, 117001, 2, 101, 4, 0, 1, '若是少了您的存在，\\n波光粼粼的大海\\n也會黯然失色呢……', 0);
insert into room_unit_comments values (5169, 117001, 2, 102, 8, 0, 1, '我和您注定要在一起……\\n不會讓您逃走的……', 0);
insert into room_unit_comments values (5170, 117001, 2, 1, 1, 1, 1, '竟然剛睡醒就馬上來見我，\\n您總算有身為我的伴侶\\n的自覺了……\\n嘻嘻。', 0);
insert into room_unit_comments values (5171, 117001, 2, 2, 1, 2, 1, '您還沒吃午餐嗎？\\n請品嘗我特製的\\n藥草特餐吧……嘻嘻。', 0);
insert into room_unit_comments values (5172, 117001, 2, 3, 1, 3, 1, '送給您能看見\\n我的夢境的藥……\\n大人晚安……嘻嘻。', 0);
insert into room_unit_comments values (5174, 117001, 3, 2, 1, 0, 1, '觸感真好……\\n很適合坐著思考事情。\\n美中不足的是\\n會讓人犯睏……', 0);
insert into room_unit_comments values (5175, 117001, 3, 3, 1, 0, 1, '在這種布偶裡\\n塞入具揮發性的藥物\\n也是個不錯的辦法呢。', 0);
insert into room_unit_comments values (5176, 117001, 3, 4, 1, 0, 1, '這股香氣……\\n感覺能放進正在\\n開發的藥物中……', 0);
insert into room_unit_comments values (5177, 117001, 3, 5, 1, 0, 1, '讓人回想起那位\\n大人的體溫了呢……', 0);
insert into room_unit_comments values (5179, 117001, 3, 7, 1, 0, 1, '真美味……在這麼美味的\\n食物裡摻入藥物\\n應該不會被發現……', 0);
insert into room_unit_comments values (5180, 117001, 3, 8, 1, 0, 1, '這個藥物配方……\\n下次嘗試看看吧。\\n試驗對象當然是\\n那位大人……', 0);
insert into room_unit_comments values (5182, 117001, 3, 10, 1, 0, 1, '要是能兩個人\\n一起坐就好了……', 0);
insert into room_unit_comments values (5183, 117001, 3, 11, 1, 0, 1, '好柔軟……\\n也想要那位大人那份了……', 0);
insert into room_unit_comments values (5184, 101701, 1, 103, 12, 0, 1, '今天想一直和你\\n一起唱歌跳舞呢～♪', 0);
insert into room_unit_comments values (5185, 101701, 2, 103, 12, 0, 1, '啊哈～♪\\n在你身邊好像比在故鄉\\n更令人感到安心～♪', 0);
insert into room_unit_comments values (5186, 117101, 1, 1, 1, 0, 1, '這套泳裝……\\n想給弟弟\\n第一個看♪', 0);
insert into room_unit_comments values (5187, 117101, 1, 2, 1, 0, 1, '偷偷把弟弟運到海邊……\\n給他一個驚喜好了♪', 0);
insert into room_unit_comments values (5188, 117101, 1, 3, 1, 0, 1, '試著綁了和平時\\n不一樣的髮型……\\n不知道弟弟會不會喜歡。', 0);
insert into room_unit_comments values (5189, 117101, 1, 4, 1, 0, 1, '雖然很期待去海邊，\\n但也得小心別曬傷呢。', 0);
insert into room_unit_comments values (5190, 117101, 1, 5, 1, 0, 1, '弟弟，還沒準備好嗎？\\n穿著泳裝的姐姐\\n來接你囉♪', 0);
insert into room_unit_comments values (5191, 117101, 1, 101, 4, 0, 1, '真想和弟弟一起住在\\n能看海的房子裡呢～', 0);
insert into room_unit_comments values (5192, 117101, 1, 102, 8, 0, 1, '弟弟會穿什麼樣\\n的泳裝呢……啊，\\n光是想想就讓人\\n心跳加速呢……嘿嘿。', 0);
insert into room_unit_comments values (5193, 117101, 2, 1, 1, 0, 1, '喂～不可以摸\\n穿泳裝的姐姐唷♪\\n……說笑的啦。', 0);
insert into room_unit_comments values (5194, 117101, 2, 2, 1, 0, 1, '叫我嗎？\\n呵呵，你叫再多次\\n都沒關係唷♪', 0);
insert into room_unit_comments values (5195, 117101, 2, 3, 1, 0, 1, '為了避免在海邊走散，\\n我們手牽著手吧♪', 0);
insert into room_unit_comments values (5196, 117101, 2, 4, 1, 0, 1, '就讓姐姐來教你\\n如何享受大海！', 0);
insert into room_unit_comments values (5197, 117101, 2, 101, 4, 0, 1, '哇，弟弟你在摸哪裡呀？\\n真是的～害我嚇一跳♪', 0);
insert into room_unit_comments values (5198, 117101, 2, 102, 8, 0, 1, '就算是在海邊，\\n姐姐也會一直守著你的。\\n放心吧。', 0);
insert into room_unit_comments values (5199, 117101, 2, 1, 1, 1, 1, '你還睡眼惺忪的呢？\\n快點，大海在等著我們唷。\\n快起床快起床♪', 0);
insert into room_unit_comments values (5200, 117101, 2, 2, 1, 2, 1, '沒事吧？會不會很累？\\n在海邊玩累了\\n要乖乖地休息唷。', 0);
insert into room_unit_comments values (5201, 117101, 2, 3, 1, 3, 1, '好啦，不快點睡\\n明天就不能\\n和姐姐一起玩囉♪', 0);
insert into room_unit_comments values (5203, 117101, 3, 2, 1, 0, 1, '不能讓弟弟看見\\n我這副懶散的模樣呢～', 0);
insert into room_unit_comments values (5204, 117101, 3, 3, 1, 0, 1, '呵呵，真可愛♪\\n如果讓弟弟拿著\\n就更可愛了♪', 0);
insert into room_unit_comments values (5205, 117101, 3, 4, 1, 0, 1, '好香呀……\\n似乎是能讓姐姐\\n別具魅力的香氣……♪', 0);
insert into room_unit_comments values (5206, 117101, 3, 5, 1, 0, 1, '好溫暖～♪\\n弟弟也過來嘛♪', 0);
insert into room_unit_comments values (5208, 117101, 3, 7, 1, 0, 1, '我要好好吃飯，\\n才能隨時回應\\n弟弟的召喚……！', 0);
insert into room_unit_comments values (5209, 117101, 3, 8, 1, 0, 1, '弟弟的泳裝照\\n就藏在這附近好了。', 0);
insert into room_unit_comments values (5211, 117101, 3, 10, 1, 0, 1, '騎上這孩子的背，\\n前往姐姐和弟弟的樂園……\\n開玩笑的啦～♪', 0);
insert into room_unit_comments values (5212, 117101, 3, 11, 1, 0, 1, '這觸感就像大海呢。\\n回想起和弟弟度過的\\n夏日時光了……♪', 0);
insert into room_unit_comments values (5213, 117201, 1, 1, 1, 0, 1, '這個夏天\\n我要比所有人\\n更加閃耀！\\n因為我是偶像呀！', 0);
insert into room_unit_comments values (5214, 117201, 1, 2, 1, 0, 1, '陽傘、臂套、\\n帽子、太陽眼鏡……\\n防紫外線措施完美無缺！', 0);
insert into room_unit_comments values (5215, 117201, 1, 3, 1, 0, 1, '把珍藏的\\n夏日歌曲\\n送給大家！', 0);
insert into room_unit_comments values (5216, 117201, 1, 4, 1, 0, 1, '什麼都不做\\n有點彆扭呢……\\n他怎麼不快點來呢？', 0);
insert into room_unit_comments values (5217, 117201, 1, 5, 1, 0, 1, '彈珠汽水\\n有一種夏天的感覺呢。\\n晚點和他一起喝吧♪', 0);
insert into room_unit_comments values (5218, 117201, 1, 101, 4, 0, 1, '夏天是戀愛的季節！\\n火熱的戀愛……\\n總有一天想試試看呢。', 0);
insert into room_unit_comments values (5219, 117201, 1, 102, 8, 0, 1, '今年夏天的目標是……\\n和他一起\\n留下許多回憶！', 0);
insert into room_unit_comments values (5220, 117201, 2, 1, 1, 0, 1, '你看了我的\\n泳裝寫真集嗎！\\n怎麼樣，可愛嗎？', 0);
insert into room_unit_comments values (5221, 117201, 2, 2, 1, 0, 1, '你想玩打西瓜嗎？\\n不錯耶，好像很好玩！\\n也找大家一起玩吧♪', 0);
insert into room_unit_comments values (5222, 117201, 2, 3, 1, 0, 1, '嘿，你看！\\n我想了一個\\n讓泳裝更吸睛的姿勢！', 0);
insert into room_unit_comments values (5223, 117201, 2, 4, 1, 0, 1, '如果能在演唱會中\\n放煙火的話，\\n一定能帶動氣氛的！', 0);
insert into room_unit_comments values (5224, 117201, 2, 101, 4, 0, 1, '最近有點忙……\\n不過看見你的臉，\\n疲倦就\\n通通消失了！', 0);
insert into room_unit_comments values (5225, 117201, 2, 102, 8, 0, 1, '這個夏天也會\\n一直讓你心跳加速的！\\n做好心理準備吧♪', 0);
insert into room_unit_comments values (5226, 117201, 2, 1, 1, 1, 1, '雖然外面從早上開始就很熱，\\n但我不會屈服的！\\n我要努力工作！', 0);
insert into room_unit_comments values (5227, 117201, 2, 2, 1, 2, 1, '吃個冰淇淋\\n來犒賞自己吧♪\\n你也一起吃嘛！', 0);
insert into room_unit_comments values (5228, 117201, 2, 3, 1, 3, 1, '夏天的夜晚\\n真令人雀躍♪\\n我們去看螢火蟲吧！', 0);
insert into room_unit_comments values (5230, 117201, 3, 2, 1, 0, 1, '呼啊……好睏……\\n不知道是不是因為工作\\n累積了壓力呢……？', 0);
insert into room_unit_comments values (5231, 117201, 3, 3, 1, 0, 1, '如果周邊商品\\n推出了\\n我的造型娃娃……\\n會討他喜歡嗎？', 0);
insert into room_unit_comments values (5232, 117201, 3, 4, 1, 0, 1, '這個香味……\\n是夏季限定的吧？\\n你很有品位呢♪', 0);
insert into room_unit_comments values (5233, 117201, 3, 5, 1, 0, 1, '好熱哦～！\\n不過……\\n出了汗很暢快呢！', 0);
