insert into room_unit_comments values (5687, 119901, 3, 8, 1, 0, 1, '布丁蛋糕…\\n這個的話感覺宮子\\n也會做喲！\\n馬上就來做吧！', 0);
insert into room_unit_comments values (5689, 119901, 3, 14, 1, 0, 1, '嚇了人家一大跳～！\\n竟然能夠嚇到\\n身為幽靈的宮子\\n…你挺厲害的嘛！', 0);
insert into room_unit_comments values (5690, 119901, 3, 15, 1, 0, 1, '這東西的裡面…\\n好暖和喲～♪\\n好適合拿來午睡喲～', 0);
insert into room_unit_comments values (5691, 119201, 1, 1, 1, 0, 1, '聖誕快樂！\\n我會在平安夜\\n帶來歡笑的♪', 0);
insert into room_unit_comments values (5692, 119201, 1, 2, 1, 0, 1, '聖誕節前\\n乳製品賣得很好\\n是很感謝……\\n但真的忙得團團轉～', 0);
insert into room_unit_comments values (5693, 119201, 1, 3, 1, 0, 1, '要先做好\\n要在牧場賣的\\n聖誕花圈才行……', 0);
insert into room_unit_comments values (5694, 119201, 1, 4, 1, 0, 1, '也得為我們家的\\n動物們準備\\n聖誕禮物才行。', 0);
insert into room_unit_comments values (5695, 119201, 1, 5, 1, 0, 1, '要不要試著做\\n聖誕節的\\n限量商品呀？', 0);
insert into room_unit_comments values (5696, 119201, 1, 101, 4, 0, 1, '選要給老哥\\n的禮物……\\n要裝傻搞笑嗎……？\\n還是放棄呢……？', 0);
insert into room_unit_comments values (5697, 119201, 1, 102, 8, 0, 1, '我一直以來真的\\n從老哥這裡得到了\\n很多東西呢……', 0);
