insert into room_unit_comments values (5699, 119201, 2, 2, 1, 0, 1, '試著在晚餐時間\\n做平安夜搞笑表演\\n如何呢？\\n吐槽就交給你囉！', 0);
insert into room_unit_comments values (5700, 119201, 2, 3, 1, 0, 1, '天氣變冷\\n我就會想吃火鍋呢。\\n那老哥呢？', 0);
insert into room_unit_comments values (5701, 119201, 2, 4, 1, 0, 1, '老哥\\n相信有\\n聖誕老人嗎？', 0);
insert into room_unit_comments values (5702, 119201, 2, 101, 4, 0, 1, '做一個大大的\\n聖誕節蛋糕吧！\\n老哥也要\\n來幫忙喔♪', 0);
insert into room_unit_comments values (5703, 119201, 2, 102, 8, 0, 1, '如果老哥\\n是我丈夫的話……\\n呃，老哥！\\n剛剛的你聽到了！？', 0);
insert into room_unit_comments values (5704, 119201, 2, 1, 1, 1, 1, '牧場的一天是很早開始的！\\n那麼，首先\\n先去剷雪吧。', 0);
insert into room_unit_comments values (5705, 119201, 2, 2, 1, 2, 1, '老哥也一起\\n去吃午餐嗎？\\n剛採收的冬季蔬菜\\n很美味喔～♪', 0);
insert into room_unit_comments values (5706, 119201, 2, 3, 1, 3, 1, '好孩子不早點睡的話，\\n聖誕老人是\\n不會來的喔？', 0);
insert into room_unit_comments values (5708, 119201, 3, 2, 1, 0, 1, '這種的感覺\\n鈴鈴會喜歡……\\n先放入禮物\\n候補名單吧！', 0);
insert into room_unit_comments values (5709, 119201, 3, 3, 1, 0, 1, '毛茸茸的\\n超級可愛……\\n啊啊……被治癒了……', 0);
insert into room_unit_comments values (5710, 119201, 3, 4, 1, 0, 1, '這氣味……\\n讓人心情平靜呢。\\n彷彿置身夢境一般。', 0);
