insert into room_unit_comments values (5713, 119201, 3, 7, 1, 0, 1, '這、這個滋味是……！\\n……嗯～\\n也得再多練習\\n誇張的反應呢。', 0);
insert into room_unit_comments values (5714, 119201, 3, 8, 1, 0, 1, '栞栞\\n看了什麼書呢？\\n……滿滿的都是\\n難懂的字啊……', 0);
