insert into room_unit_comments values (5717, 119201, 3, 15, 1, 0, 1, '要穿這個布偶裝\\n來練習模仿\\n莉瑪莉瑪看看嗎……？', 0);
insert into room_unit_comments values (5718, 105001, 1, 103, 12, 0, 1, '我要變得更漂亮，\\n讓那傢伙\\n心服口服', 0);
