insert into room_unit_comments values (5726, 119301, 1, 102, 8, 0, 1, '要送給哥哥\\n作為禮物的編織品，\\n這樣就是第三件了～♪\\n咦，我做太多了？', 0);
insert into room_unit_comments values (5727, 119301, 2, 1, 1, 0, 1, '嘿嘿，你不想\\n再靠近一點看嗎？\\n我這珍藏的\\n聖誕穿搭♪', 0);
insert into room_unit_comments values (5728, 119301, 2, 2, 1, 0, 1, '哥哥若是感冒\\n可就不得了了！\\n用我做的編織品\\n來溫暖你吧♪', 0);
