insert into room_unit_comments values (5730, 119301, 2, 4, 1, 0, 1, '哥哥！\\n嘴巴張開，\\n我來餵你蛋糕！\\n來，啊～♪', 0);
insert into room_unit_comments values (5731, 119301, 2, 101, 4, 0, 1, '聖誕節是要和重要的\\n人一起度過的日子！\\n哥哥……妹妹我\\n這天有空喔！', 0);
insert into room_unit_comments values (5732, 119301, 2, 102, 8, 0, 1, '如果我說\\n聖誕節想\\n跟哥哥一起過……\\n這樣……會太貪心嗎？', 0);
insert into room_unit_comments values (5733, 119301, 2, 1, 1, 1, 1, '早起的鳥兒有蟲吃！\\n……那在聖誕節\\n早起的話，說不定\\n能吃到更多！？', 0);
