insert into room_unit_comments values (5735, 119301, 2, 3, 1, 3, 1, '跟哥哥\\n在聖誕節一起熬夜\\n……嘿嘿，有點\\n讓人心跳加速呢♪', 0);
insert into room_unit_comments values (5737, 119301, 3, 2, 1, 0, 1, '這真是個非常\\n適合編織的舒適空間\\n對吧～♪', 0);
