insert into room_unit_comments values (5742, 119301, 3, 7, 1, 0, 1, '是大餐～♪\\n來，哥哥也\\n趕快吃吧！', 0);
insert into room_unit_comments values (5743, 119301, 3, 8, 1, 0, 1, '聖誕約會的\\n必勝法……這、這\\n不讀不行！', 0);
insert into room_unit_comments values (5745, 119301, 3, 14, 1, 0, 1, '哇，好厲害～！\\n把這個裝飾上去，\\n就變得更有聖誕節的\\n熱鬧氣氛了呢♪', 0);
