insert into room_unit_comments values (5936, 121201, 2, 3, 1, 0, 1, '畢竟還要為你診療，\\n我現在會盡量陪在\\n你身邊。只希望這樣\\n不會造成你的困擾。', 0);
insert into room_unit_comments values (5937, 121201, 2, 4, 1, 0, 1, '少年到處去幫助人時，\\n果然才是最耀眼的。', 0);
insert into room_unit_comments values (5938, 121201, 2, 101, 4, 0, 1, '我是沒做什麼\\n了不起的事情，\\n但你要誇獎一下\\n大家喔。', 0);
insert into room_unit_comments values (5939, 121201, 2, 102, 8, 0, 1, '能讓大家\\n露出笑容的人，\\n果然就是少年啊。', 0);
insert into room_unit_comments values (5940, 121201, 2, 1, 1, 1, 1, '嗨，少年，你早上\\n有沒有睡過頭？那祝你\\n有個愉快的一天。', 0);
insert into room_unit_comments values (5941, 121201, 2, 2, 1, 2, 1, '哎呀，少年！我做了\\n可麗餅來當員工餐，\\n你要吃嗎？', 0);
insert into room_unit_comments values (5942, 121201, 2, 3, 1, 3, 1, '呼啊……已經\\n晚上啦……做起研究後\\n真是時光飛逝啊……\\n晚安，少年。', 0);
insert into room_unit_comments values (5944, 121201, 3, 2, 1, 0, 1, '可能是身體懈怠\\n下來了，上前線後\\n會很累……難不成……\\n是年紀的關係！？', 0);
insert into room_unit_comments values (5945, 121201, 3, 3, 1, 0, 1, '就來調整一下，讓你們\\n也能擁有和現在的我\\n相同的感受吧。', 0);
insert into room_unit_comments values (5946, 121201, 3, 4, 1, 0, 1, '這是誰放的啊？\\n聞起來真香……\\n嗯，我很中意♪', 0);
insert into room_unit_comments values (5947, 121201, 3, 5, 1, 0, 1, '對思考來說，什麼\\n都沒在想的時光\\n其實也很重要啊～', 0);
insert into room_unit_comments values (5949, 121201, 3, 7, 1, 0, 1, '從平常的餐點中，\\n也能感受到\\n用餐的重要性呢♪', 0);
insert into room_unit_comments values (5950, 121201, 3, 8, 1, 0, 1, '嗯……真有意思！\\n下次就把整套\\n借來看吧。', 0);
insert into room_unit_comments values (5952, 116401, 1, 1, 1, 0, 1, '聖學祭啊……我沒什麼\\n美好的回憶啊，\\n好，就忘了吧。', 0);
insert into room_unit_comments values (5953, 116401, 1, 2, 1, 0, 1, '健全的精神會寄宿\\n於健全的肉體……\\n哈，誰管它啊。', 0);
insert into room_unit_comments values (5954, 116401, 1, 3, 1, 0, 1, '反正很閒，來照照\\n鏡子吧……嘻嘻，\\n我是優妮喔♪好，\\n這樣能算兒童價吧。', 0);
insert into room_unit_comments values (5955, 116401, 1, 4, 1, 0, 1, '不知道碧\\n過得好不好……\\n因為她比別人\\n更黏我啊……', 0);
insert into room_unit_comments values (5956, 116401, 1, 5, 1, 0, 1, '靜流差不多要從\\n名為姐姐的幻想中\\n清醒過來了吧……', 0);
insert into room_unit_comments values (5957, 116401, 1, 101, 4, 0, 1, '我明年也會去參加\\n聖學祭……就算\\n畢業了，我和你們的\\n關係也是永恆的。', 0);
insert into room_unit_comments values (5958, 116401, 1, 102, 8, 0, 1, '同志近來的上學頻率\\n有降低的傾向啊……\\n雖然這也是\\n沒辦法的……但是……', 0);
insert into room_unit_comments values (5959, 116401, 2, 1, 1, 0, 1, '喔哇啊啊啊！\\n不要突然跟我說話，\\n學問都要漏出來了。', 0);
insert into room_unit_comments values (5960, 116401, 2, 2, 1, 0, 1, '旁人對這件運動服\\n讚不絕口喔。就因為\\n大家是很棒的人。\\n一臉得意！', 0);
insert into room_unit_comments values (5961, 116401, 2, 3, 1, 0, 1, '不管穿上什麼東西，\\n我就是我，\\n外觀終究只是\\n靈魂的識別符號。', 0);
insert into room_unit_comments values (5962, 116401, 2, 4, 1, 0, 1, '育幼院的綾音最近開始\\n懷疑我自報的\\n年齡了……要不要……\\n殺了那隻熊啊……', 0);
insert into room_unit_comments values (5963, 116401, 2, 101, 4, 0, 1, '嗯？有事嗎？\\n什麼？沒事？\\n沒事卻叫我，難道……\\n這就是朋友！？', 0);
insert into room_unit_comments values (5964, 116401, 2, 102, 8, 0, 1, '這件衣服很適合我？\\n你、你突然\\n講這幹嘛啊……\\n別說了啦，嘻嘻嘻。', 0);
insert into room_unit_comments values (5965, 116401, 2, 1, 1, 1, 1, '呼啊……\\n越來越早了啊……\\n差不多該來睡了……', 0);
insert into room_unit_comments values (5966, 116401, 2, 2, 1, 2, 1, '呼～啊……已經\\n中午了……這不是人類\\n該醒著的時間啊……\\n我得趕快去睡……', 0);
insert into room_unit_comments values (5967, 116401, 2, 3, 1, 3, 1, '嗯～睡得真好！\\n真是個舒適的夜晚啊。\\n今天也要來\\n努力一整天囉。', 0);
insert into room_unit_comments values (5969, 116401, 3, 2, 1, 0, 1, '呼哇……難道這就是\\n都會中謠傳的，能讓人\\n變成廢物的那個……', 0);
insert into room_unit_comments values (5970, 116401, 3, 3, 1, 0, 1, '嗯，真可愛啊。但這\\n太適合我了……如果\\n我拿著，連我都覺得\\n自己看起來就像小孩。', 0);
insert into room_unit_comments values (5971, 116401, 3, 4, 1, 0, 1, '薰香嗎？還真有趣啊。\\n要不要嘗試讓有深月\\n印記的藥水氣化，\\n再從鼻腔吸入呢……', 0);
insert into room_unit_comments values (5972, 116401, 3, 5, 1, 0, 1, '嗯呵，嗯呵呵……\\n火很棒呢……\\n可以一直盯著看……', 0);
insert into room_unit_comments values (5974, 116401, 3, 7, 1, 0, 1, '快、快住手……\\n不要永無止盡地\\n生產固態食物……', 0);
insert into room_unit_comments values (5975, 116401, 3, 8, 1, 0, 1, '看這不可愛的筆法，\\n作者果然是理科的嗎！\\n嗚啊啊，眼睛都要\\n理科化了。', 0);
insert into room_unit_comments values (5977, 121101, 1, 1, 1, 0, 1, '……如果是為了\\n那傢伙，要我再怎麼\\n勉強自己都願意。', 0);
insert into room_unit_comments values (5978, 121101, 1, 2, 1, 0, 1, '這副模樣\\n太難控制魔力了……\\n但因為我很優秀，\\n總是能解決的！', 0);
insert into room_unit_comments values (5979, 121101, 1, 3, 1, 0, 1, '因為我已經下定決心，\\n要接受那傢伙的\\n所有一切！', 0);
insert into room_unit_comments values (5980, 121101, 1, 4, 1, 0, 1, '不管面對誰，我都\\n不覺得現在的自己\\n會輸。不管是龍還是\\n什麼，儘管放馬過來！', 0);
insert into room_unit_comments values (5981, 121101, 1, 5, 1, 0, 1, '就因為得到了\\n不少幫助，這次\\n就由我來幫助那傢伙。', 0);
insert into room_unit_comments values (5982, 121101, 1, 101, 4, 0, 1, '之前和那傢伙\\n一起經歷的冒險，\\n全都是我\\n重要的回憶……', 0);
insert into room_unit_comments values (5983, 121101, 1, 102, 8, 0, 1, '如果那傢伙又\\n一睡不醒，我什麼\\n都願意做。就……\\n就算要親他也……', 0);
insert into room_unit_comments values (5984, 121101, 2, 1, 1, 0, 1, '什麼？你肚子餓了？\\n那就去吃\\n好吃的飯吧♪', 0);
insert into room_unit_comments values (5985, 121101, 2, 2, 1, 0, 1, '看到你這張蠢臉，\\n果然能讓人\\n覺得很放心♪', 0);
insert into room_unit_comments values (5986, 121101, 2, 3, 1, 0, 1, '如果沒有你和\\n貪吃佩可，\\n【美食殿堂】\\n就沒辦法開始啦。', 0);
insert into room_unit_comments values (5987, 121101, 2, 4, 1, 0, 1, '呀啊！？\\n別突然跟我說話啦！？\\n要是魔力爆發，\\n我可不管你！', 0);
insert into room_unit_comments values (5988, 121101, 2, 101, 4, 0, 1, '……像這樣和你一起\\n放空度過一段時光，\\n這個嘛……\\n我是不討厭啦。', 0);
insert into room_unit_comments values (5989, 121101, 2, 102, 8, 0, 1, '只要你能相信\\n並守護著我……\\n我什麼都做得到。', 0);
insert into room_unit_comments values (5990, 121101, 2, 1, 1, 1, 1, '早安，\\n快去洗臉啦，\\n你這隻瞌睡蟲♪', 0);
insert into room_unit_comments values (5991, 121101, 2, 2, 1, 2, 1, '今天要在哪裡吃午餐？\\n可以由你來決定喔。', 0);
insert into room_unit_comments values (5992, 121101, 2, 3, 1, 3, 1, '假如你又被困在夢裡，\\n我會去救你的，\\n所以你就放心地睡吧。', 0);
insert into room_unit_comments values (5994, 121101, 3, 2, 1, 0, 1, '好舒服……♪\\n偶爾也得讓那傢伙\\n像這樣休息才行。', 0);
insert into room_unit_comments values (5995, 121101, 3, 3, 1, 0, 1, '我可能\\n一見鍾情了……！\\n好想把這孩子\\n裝飾在房間裡喔～♪', 0);
insert into room_unit_comments values (5996, 121101, 3, 4, 1, 0, 1, '哦，那傢伙\\n喜歡這種\\n香味嗎……？', 0);
insert into room_unit_comments values (5997, 121101, 3, 5, 1, 0, 1, '暖暖的，真是\\n棒呆了……♪好像要\\n縮成一團了……\\n呼喵……♪', 0);
insert into room_unit_comments values (5999, 121101, 3, 7, 1, 0, 1, '這個可以耶！\\n吃飯果然是件\\n很重要的事情啊～♪', 0);
insert into room_unit_comments values (6000, 121101, 3, 8, 1, 0, 1, '初吻是什麼樣的\\n滋味啊……\\n哦、哦……？\\n原來如此……', 0);
insert into room_unit_comments values (6002, 118101, 1, 1, 1, 0, 1, '我這樣的人……\\n也有辦法……\\n交到朋友嗎……？', 0);
insert into room_unit_comments values (6003, 118101, 1, 2, 1, 0, 1, '我不可以一直\\n東張西望吧……\\n這樣會被別人當成\\n奇怪的女孩……', 0);
insert into room_unit_comments values (6004, 118101, 1, 3, 1, 0, 1, '寬敞的地方\\n讓我靜不下心來……\\n啊嗚……\\n我該待在哪裡……', 0);
insert into room_unit_comments values (6005, 118101, 1, 4, 1, 0, 1, '～♪', 0);
insert into room_unit_comments values (6006, 118101, 1, 5, 1, 0, 1, '【憤怒‧軍團】……\\n總有一天，\\n要再和大家……', 0);
insert into room_unit_comments values (6007, 118101, 1, 101, 4, 0, 1, '……一次就好，我也想\\n被叫媽……啊，\\n那個……\\n……沒、沒什麼。', 0);
insert into room_unit_comments values (6008, 118101, 1, 102, 8, 0, 1, '對現在的我來說，\\n這裡就是能回去的\\n地方…………我可以\\n這麼認為嗎……？', 0);
insert into room_unit_comments values (6009, 118101, 2, 1, 1, 0, 1, '那個……\\n你肚子餓了嗎？\\n是不是想睡了？', 0);
insert into room_unit_comments values (6010, 118101, 2, 2, 1, 0, 1, '我在這裡\\n會妨礙到你嗎……？\\n那我再去\\n更角落的地方……', 0);
insert into room_unit_comments values (6011, 118101, 2, 3, 1, 0, 1, '聊天……？\\n找我聊就好嗎……？\\n我好開心……', 0);
insert into room_unit_comments values (6012, 118101, 2, 4, 1, 0, 1, '跟我說說……\\n你喜歡什麼歌……\\n我會，練習的……', 0);
insert into room_unit_comments values (6013, 118101, 2, 101, 4, 0, 1, '這裡很熱鬧……\\n我卻不覺得吵鬧，\\n反而感到很舒適……', 0);
insert into room_unit_comments values (6014, 118101, 2, 102, 8, 0, 1, '你放心……\\n因為我的歌\\n會保護你……', 0);
insert into room_unit_comments values (6015, 118101, 2, 1, 1, 1, 1, '一下子就好……\\n我不擅長早起……\\n……呼啊……', 0);
insert into room_unit_comments values (6016, 118101, 2, 2, 1, 2, 1, '你吃過，午餐了嗎？\\n如果不介意，\\n就一起吃吧……\\n雖然……有點燒焦……', 0);
insert into room_unit_comments values (6017, 118101, 2, 3, 1, 3, 1, '你睡不著嗎……\\n那、那麼……\\n要不要聽……搖籃曲？', 0);
insert into room_unit_comments values (6019, 118101, 3, 2, 1, 0, 1, '在這麼好的位子……\\n實在不敢當……', 0);
insert into room_unit_comments values (6020, 118101, 3, 3, 1, 0, 1, '就、就趁現在，\\n用這孩子來練習\\n照顧人……', 0);
insert into room_unit_comments values (6021, 118101, 3, 4, 1, 0, 1, '好香的味道……\\n……這也能療癒\\n難過的感覺。', 0);
insert into room_unit_comments values (6022, 118101, 3, 5, 1, 0, 1, '好溫暖……\\n好像可以一直維持\\n這個樣子……', 0);
insert into room_unit_comments values (6024, 118101, 3, 7, 1, 0, 1, '做得好好吃……\\n好厲害……\\n我也得看齊才行……', 0);
insert into room_unit_comments values (6025, 118101, 3, 8, 1, 0, 1, '《小寶寶和媽媽的\\n肢體接觸入門》……\\n……學、\\n學到了好多……', 0);
insert into room_unit_comments values (6027, 121301, 1, 1, 1, 0, 1, '好漂亮的房間……！\\n好像豪華舞台的\\n布景……♪', 0);
insert into room_unit_comments values (6028, 121301, 1, 2, 1, 0, 1, '在演戲方面\\n我是前輩，\\n所以我要好好\\n帶領美咲才行。', 0);
insert into room_unit_comments values (6029, 121301, 1, 3, 1, 0, 1, '～～♪\\n也有很多戲要唱歌，\\n希望自己可以\\n表演得更好……！', 0);
insert into room_unit_comments values (6030, 121301, 1, 4, 1, 0, 1, '也想跟這邊的人\\n變成朋友呢……\\n這樣一來，\\n演繹能力也會變得更廣吧。', 0);
insert into room_unit_comments values (6031, 121301, 1, 5, 1, 0, 1, '有機會的話，\\n我也想跟綾音\\n一起演戲呢♪', 0);
insert into room_unit_comments values (6032, 121301, 1, 101, 4, 0, 1, '為了得到\\n哥哥的稱讚，\\n必須大量進行\\n演戲練習才行呢……♪', 0);
insert into room_unit_comments values (6033, 121301, 1, 102, 8, 0, 1, '啊嗚……\\n太在意哥哥\\n就記不住台詞了啦……！', 0);
insert into room_unit_comments values (6034, 121301, 2, 1, 1, 0, 1, '啊，哥哥……♪\\n你可以幫我看看\\n我的演戲練習嗎……？', 0);
insert into room_unit_comments values (6035, 121301, 2, 2, 1, 0, 1, '剛剛的發聲練習\\n你覺得如何呢？\\n呀……！？\\n不要摸我的頭啦～', 0);
insert into room_unit_comments values (6036, 121301, 2, 3, 1, 0, 1, '美咲的演技\\n也進步了\\n不少喔。\\n我不能輸給她呢♪', 0);
insert into room_unit_comments values (6037, 121301, 2, 4, 1, 0, 1, '你又想要\\n聽我說故事嗎……？\\n你不嫌棄的話\\n我很樂意♪', 0);
insert into room_unit_comments values (6038, 121301, 2, 101, 4, 0, 1, '我有構想了\\n一個新故事。\\n你要聽聽看嗎……？\\n太好了……♪', 0);
insert into room_unit_comments values (6039, 121301, 2, 102, 8, 0, 1, '希望有一天……\\n就算不是在舞台上，\\n我也能毫不扭捏地\\n將心意傳達給哥哥呢……♪', 0);
insert into room_unit_comments values (6040, 121301, 2, 1, 1, 1, 1, '早安♪\\n我從真陽那裡\\n拿到了牛奶。\\n要不要一起喝呢？', 0);
insert into room_unit_comments values (6041, 121301, 2, 2, 1, 2, 1, '牧場的人\\n還給了我\\n橡子麵包～\\n你也吃一個吧♪', 0);
insert into room_unit_comments values (6042, 121301, 2, 3, 1, 3, 1, '睡前要不要\\n再來一杯熱可可，\\n然後聽我說故事呢？\\n嘿嘿，真開心♪', 0);
insert into room_unit_comments values (6044, 121301, 3, 2, 1, 0, 1, '演戲練習結束後，\\n我想在這裡\\n跟哥哥一起\\n好好放鬆♪', 0);
insert into room_unit_comments values (6045, 121301, 3, 3, 1, 0, 1, '你今天也要\\n陪我練習演技喔♪', 0);
insert into room_unit_comments values (6046, 121301, 3, 4, 1, 0, 1, '好棒的香氣……\\n這種感覺\\n是否能活用在\\n戲劇裡呢……？', 0);
